float PI = 3.1415926535;
vec3 MoonDirection = normalize(vec3(-0.5,0.4,-0.3));
vec3 MoonColor = vec3(0.6,0.7,1.2);
float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

mat2 rot(float r){
    return mat2(cos(r),sin(r),-sin(r),cos(r));
}

vec4 dist(vec3 p){
    //p.z *= 0.7;
    p.y -= 0.3;
	float d = 0.009;
    float no = ridgedMF(p.xz+0.3*snoise(p.xz+0.1*iTime));
    vec3 col = vec3(1,1,1)*0.02*exp(-no*3.);
    float thredy = 0.5;
    float thx = p.y-thredy;
    vec3 highems = vec3(1.3,1.0,1.0)*max(thx*8.0*exp(-3.5*vec3(2.,1.2,1.5)*thx),0.);
    col *= highems;
    return vec4(col,d);
}

vec4 ground(vec3 p){
    p.y -= 0.3;
    p.x -= -0.;
    float d = p.y - smoothstep(0.0,1.0,length(p.xz-vec2(-0.4,0.))*1.)*0.23*ridgedMF(vec2(0.9,1.)*(p.xz-vec2(-0.1,0.02*iTime)));
    //d = max(d,-(length(p-vec3(-0.4,0.65,-0.6))-0.8));
    vec3 col = vec3(0);
    return vec4(col,d);
}

vec3 getnormal(vec3 p)
{
	const vec2 e = vec2(0.5773,-0.5773)*0.0001;
	vec3 nor = normalize( e.xyy*ground(p+e.xyy).w +
 		e.yyx*ground(p+e.yyx).w + e.yxy*ground(p+e.yxy).w + e.xxx*ground(p+e.xxx).w);
	nor = normalize(vec3(nor));
	return nor ;
}


vec3 star(vec2 s){
    vec3 c = vec3(snoise(s));
    c = pow(c,vec3(5.));
    c = clamp(7.*clamp(c-0.7,0.0,1.0),0.0,1000.0);

    return c;
}

vec3 background(vec3 rd){
    vec2 rs = vec2(atan(length(rd.xy),rd.z),atan(rd.x,rd.y));
    vec3 moon = 0.5*clamp(MoonColor*0.07/length(MoonDirection-rd),0.0,1.0);
    return moon+star(rs*50.)*vec3(0.5)+vec3(0.7,0.5,0.5)*star(rs*50.+20.)+vec3(0.5,0.5,0.7)*star(rs*50.+70.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv;
    p = 2.0*(p-0.5);
    p.x *= iResolution.x/iResolution.y;

    vec3 offset = vec3(0,-0.4 ,0);
    vec3 ro = vec3(0,0,0)-offset;
    vec3 ta = vec3(0,1.6,-2)-offset;
    vec3 cdir = normalize(ta-ro);
    vec3 side = cross(cdir,vec3(0,1,0));
    vec3 up = cross(side,cdir);
    float fov = 0.4;

    vec3 rd = normalize(side*p.x+up*p.y+cdir*fov);
     //rd.xz *= rot(iTime);
    float d,t=0.;
    float gd = 0.;
    vec3 ac = vec3(0.);
    vec4 disres;
    float kset = 0.3;
    float sen = (1.0+1.5*pow(abs(sin(iTime*kset))*(1.0-fract(iTime*kset/(0.5*PI))),1.));

    for(int i = 0;i<139;i++){
    	disres = 2.0*dist(ro+rd*t)*sen;
        d = disres.w;
        gd = 0.5*ground(ro+rd*t).w;
        d = min(gd,d);
        t += d;
		ac += disres.xyz;
        if((ro+rd*t).z<-1.5)break;
    }

    vec3 col = vec3(0.);

   	col += ac;

    col += background(rd);

    if(gd<0.01){
        vec3 sp = ro+rd*t;
        vec3 normal = getnormal(sp);
        float snk = 1.;
        vec3 cnormal = normal + 0.1*(vec3(random(snk*sp.yz),random(snk*sp.zx),random(snk*sp.xy))-0.5);
        cnormal = normalize(cnormal);
        col = 1.5*vec3(193,157,121)/255.*MoonColor*vec3(max(dot(cnormal,MoonDirection),0.));
        col += MoonColor*0.02;
        ac = vec3(0.0);

        vec3 snormal;
        vec3 rrd;
        for(int i =0; i<8; i++){
            snormal =normal + 1.0*(vec3(random(snk*sp.yz+float(i)*100.),random(snk*sp.zx+float(i)*100.),random(snk*sp.xy+float(i)*100.))-0.5);
            snormal = normalize(snormal);
            t = 0.4;
            ro = sp;
            rrd =snormal;// reflect(rd,snormal);
            for(int i = 0;i<10;i++){
                disres = 6.0*dist(ro+rrd*t)*sen;
                d = disres.w;
                t += d;
                ac += disres.xyz;
            }
        }
        col += 0.1*ac;

    }

    // Output to screen
	col = pow(col,vec3(0.8));
    fragColor = vec4(col,1.0);
}

/*

This is a modified version of https://www.shadertoy.com/view/ldcfDH

Like the original, this tries to approximate blue noise by sampling a hash at 5 points.

Unlike the original, here we sample the points to the left and right, add them together
to get a TPDF, which we then invert back into an RPDF by evaluating the CDF.

Then we do the same for points above and below, then add the horizontal and vertical
RPDF variables to get yet another TPDF that we invert once more.

Finally, we sample the actual point, add with the RPDF computed from the 4 surrounding points
and this time we keep the result as TPDF.

*/
const float levels = 6.; // number of quantisation levels, must be at least 2 (preferably 4)
const float animFPS = 120.; // max framerate for temporal dither, set 0 to disable
const bool separateRGB = false; // use different random values for different channels?
const bool antiCompress = false; // expand near black and white to counter compression

uint hash(uint x)
{
    x ^= x >> 16; x *= 0x7feb352du; 
    x ^= x >> 15; x *= 0x846ca68bu; 
    x ^= x >> 16;
    return x;
}

// fetch random values for RGB channels
vec3 hash2v(uint x, uint y)
{
    // take a hash, split it into components
    uint h = hash(uint(x)+(uint(y)<<16));
    vec3 v;
    v.x = float(h&0xffu); if(separateRGB) h >>= 8;
    v.y = float(h&0xffu); if(separateRGB) h >>= 8;
    v.z = float(h&0xffu);

    return v / float(0xffu);
}

vec3 triCDF(vec3 x)
{
    vec3 y = sqrt(2.)*min(x, 1.-x); y *= y;
    return mix(y, 1.-y, round(x));
}

vec3 dither(vec3 c, uvec2 xy)
{

    // temporal dither
    xy.x += uint(floor(animFPS*iTime)*12345.);

    // compute noise
    vec3 xx = triCDF(.5 * (hash2v(xy.x-1u, xy.y) + hash2v(xy.x+1u, xy.y)));
    vec3 yy = triCDF(.5 * (hash2v(xy.x, xy.y-1u) + hash2v(xy.x, xy.y+1u)));
    vec3 mm = triCDF(.5 * (xx + yy));

    vec3 h = mm - hash2v(xy.x, xy.y);
    float g = levels-1.;

    if(antiCompress)
    {
        // expand near black to counter compression
        vec3 f0 = max(.5-c*g, 0.); c -= .5*f0*f0;
        // expand near white to counter compression
        vec3 f1 = max(.5-(1.-c)*g, 0.); c += .5*f1*f1;    
    }
    return round(c*g + h) / g;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 c = texture(iChannel0,uv.xy).xyz;
    if(fragCoord.y < 0.05*iResolution.y) c = vec3(fragCoord.x / iResolution.x);

    if(abs(iMouse.x - fragCoord.x) < iResolution.x*.25) 
    {
        c = dither(c, uvec2(fragCoord.xy));
    }
	else if(iMouse.y < .5*iResolution.y) c = round(c*(levels-1.)) / (levels-1.);


    // Output to screen
    fragColor = vec4(c, 1.0);
}

// checkEdge() checks whether two point straddle the surface by checking for opposite signs
#define checkEdge(a, b) (a < 0.0) != (b < 0.0)

// vertexInterp() Solves for the intersection point linearly and interpolates the edge vertices (v1 and v2) accordingly
#define vertexInterp(v1, v2, d1, d2) mix(v1, v2, d1 / (d1 - d2))

float sdShape(in vec3 p) {
    p /= 1.5;
    vec3 q = abs(p) - 1.35;
    float cube = max(q.x, max(q.y, q.z));
    float sphere = length(p) - 1.6875;
    float cylinder1 = length(p.yz) - 0.6;
    float cylinder2 = length(p.xz) - 0.6;
    float cylinder3 = length(p.xy) - 0.6;
    return max(max(cube, sphere), -min(cylinder1, min(cylinder2, cylinder3))) * 1.5;
}

vec4 mapScene(in vec3 p) {
    float csz = 0.6 + 0.5 * sin(iTime); // Cell size
    float shape = sdShape(p);

    vec3 c = floor(p / csz) * csz;

    vec3 ldb_p = c;                       // (l)eft,  (d)own, (b)ack  cell corner
    vec3 rdb_p = c + vec3(csz, 0.0, 0.0); // (r)ight, (d)own, (b)ack  cell corner
    vec3 lub_p = c + vec3(0.0, csz, 0.0); // (l)eft,  (u)p,   (b)ack  cell corner
    vec3 rub_p = c + vec3(csz, csz, 0.0); // (r)ight, (u)p,   (b)ack  cell corner
    vec3 ldf_p = c + vec3(0.0, 0.0, csz); // (l)eft,  (d)own, (f)ront cell corner
    vec3 rdf_p = c + vec3(csz, 0.0, csz); // (r)ight, (d)own, (f)ront cell corner
    vec3 luf_p = c + vec3(0.0, csz, csz); // (l)eft,  (u)p,   (f)ront cell corner
    vec3 ruf_p = c + csz;                 // (r)ight, (u)p,   (f)ront cell corner

    float ldb = sdShape(ldb_p); // Distance field sample at cell corner ldb_p
    float rdb = sdShape(rdb_p); // Distance field sample at cell corner rdb_p
    float lub = sdShape(lub_p); // Distance field sample at cell corner lub_p
    float rub = sdShape(rub_p); // Distance field sample at cell corner rub_p
    float ldf = sdShape(ldf_p); // Distance field sample at cell corner ldf_p
    float rdf = sdShape(rdf_p); // Distance field sample at cell corner rdf_p
    float luf = sdShape(luf_p); // Distance field sample at cell corner luf_p
    float ruf = sdShape(ruf_p); // Distance field sample at cell corner ruf_p

    float i = 1000000.0;

    // Checking all the cell edges for intersection and then calculating the intersection point
    if (checkEdge(lub, luf)) i = min(i, length(p - vertexInterp(lub_p, luf_p, lub, luf)) - 0.1);
    if (checkEdge(luf, ruf)) i = min(i, length(p - vertexInterp(luf_p, ruf_p, luf, ruf)) - 0.1);
    if (checkEdge(ruf, rub)) i = min(i, length(p - vertexInterp(ruf_p, rub_p, ruf, rub)) - 0.1);
    if (checkEdge(rub, lub)) i = min(i, length(p - vertexInterp(rub_p, lub_p, rub, lub)) - 0.1);
    if (checkEdge(lub, ldb)) i = min(i, length(p - vertexInterp(lub_p, ldb_p, lub, ldb)) - 0.1);
    if (checkEdge(luf, ldf)) i = min(i, length(p - vertexInterp(luf_p, ldf_p, luf, ldf)) - 0.1);
    if (checkEdge(ruf, rdf)) i = min(i, length(p - vertexInterp(ruf_p, rdf_p, ruf, rdf)) - 0.1);
    if (checkEdge(rub, rdb)) i = min(i, length(p - vertexInterp(rub_p, rdb_p, rub, rdb)) - 0.1);
    if (checkEdge(ldb, ldf)) i = min(i, length(p - vertexInterp(ldb_p, ldf_p, ldb, ldf)) - 0.1);
    if (checkEdge(ldf, rdf)) i = min(i, length(p - vertexInterp(ldf_p, rdf_p, ldf, rdf)) - 0.1);
    if (checkEdge(rdf, rdb)) i = min(i, length(p - vertexInterp(rdf_p, rdb_p, rdf, rdb)) - 0.1);
    if (checkEdge(rdb, ldb)) i = min(i, length(p - vertexInterp(rdb_p, ldb_p, rdb, ldb)) - 0.1);

    return shape < i ? vec4(shape, 1.0, 1.0, 0.0) : vec4(i, 0.5, 0.5, 0.5);
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapScene(p + vec3(0.001, 0.0, 0.0)).x - mapScene(p - vec3(0.001, 0.0, 0.0)).x,
                          mapScene(p + vec3(0.0, 0.001, 0.0)).x - mapScene(p - vec3(0.0, 0.001, 0.0)).x,
                          mapScene(p + vec3(0.0, 0.0, 0.001)).x - mapScene(p - vec3(0.0, 0.0, 0.001)).x));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 10.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float c1 = cos(iTime), s1 = sin(iTime);
    float c2 = c1, s2 = s1;

    float t = 0.0;
    for (float iters=0.0; iters < 150.0; iters++) {
        vec3 p = ro + rd * t;
        p /= 1.25;

        p.xz *= mat2(c1, s1, -s1, c1);
        p.yz *= mat2(c2, s2, -s2, c2);

        vec4 d = mapScene(p);
        if (d.x < 0.001) {
            vec3 n = getNormal(p);
            vec3 l = vec3(-0.58, 0.58, 0.58);

            n.yz *= mat2(c2, -s2, s2, c2);
            n.xz *= mat2(c1, -s1, s1, c1);

            fragColor.rgb += d.yzw;
            fragColor.rgb *= max(0.2, dot(n, l));
            break;
        }

        if (t > 50.0) {
            break;
        }

        t += d.x;
    }
}

