{
	return vec3( glFragCoord.xy-iResolution.xy*.5, iResolution.x*zoom );
}

vec3 Rotate( inout vec3 v, vec2 a )
{
	vec4 cs = vec4( cos(a.x), sin(a.x), cos(a.y), sin(a.y) );

	v.yz = v.yz*cs.x+v.zy*cs.y*vec2(-1,1);
	v.xz = v.xz*cs.z+v.zx*cs.w*vec2(1,-1);

	vec3 p;
	p.xz = vec2( -cs.w, -cs.z )*cs.x;
	p.y = cs.y;

	return p;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized coordinates
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    vec2 uv= fragCoord.xy / iResolution.xy;
    // Aspect ratio
    p.x *= iResolution.x / iResolution.y;

    vec3 ray = Ray(1.8);



	ray = normalize(ray);
    vec3 localRay = ray;

	vec2 mouse = vec2(-.1,iTime*.01);

	mouse = vec2(.5)-iMouse.yx/iResolution.yx;

	float T = iTime*.0;
	vec3 pos = 3.0*Rotate( ray, vec2(.2,1.5-T)+vec2(-1.0,-7.0)*mouse );
	//pos += vec3(0,.3,0) + T*vec3(0,0,-1);
    pos.y += .06-pos.z*.02; // tail is higher
    pos.z += pos.z*.2; // centre on the end of the car we're looking at
    pos.x += .3;//sign(pos.x)*.2*smoothstep(.0,.5,abs(pos.x)); // off-centre framingvec3 ray = Ray(1.8);





    // Camera position and "look at"
    vec3 origin = vec3(0.0, 2.0, 0.0);
    vec3 target = vec3(0.0);

    origin+=pos;

    mat3 toWorld = setCamera(origin, target, 0.0);
    vec3 direction = toWorld * normalize(vec3(p.xy, 2.0));

    // Render scene
    float distance;
    vec3 color = render(origin, direction, distance,getUV(origin + direction*traceRay(origin, direction).x));

    // Tone mapping
    color = Tonemap_ACES(color);

    // Exponential distance fog
    color = mix(color, 0.8 * vec3(0.7, 0.8, 1.0), 1.0 - exp2(-0.011 * distance * distance));

    // Gamma compression
    color = OECF_sRGBFast(color);

    fragColor = vec4(color, 1.0);
}

vec2 rand2D(vec2 n){
    return fract(sin(vec2(dot(n, vec2(12.9898, 4.1414)))));
}

float voronoi2D(vec2 n, float time){
    float dis = 0.9;
    for(float y = 0.0; y <= 1.0; y++){
        for(float x = 0.0; x <= 1.0; x++){
            vec2 p = floor(n) + vec2(x, y);
            float d = length((0.27 * sin(rand2D(p) * 24. + time * 2.0)) + vec2(x, y) - fract(n));
            dis = min(dis, d);
            }
        }
    return dis;
}

vec2 rotateUV(vec2 uv, float rotation, vec2 mid) {
    return vec2(
      cos(rotation) * (uv.x - mid.x) + sin(rotation) * (uv.y - mid.y) + mid.x,
      cos(rotation) * (uv.y - mid.y) - sin(rotation) * (uv.x - mid.x) + mid.y
    );
}

vec2 rotate(vec2 v, float angle) {
    float cosA = cos(angle), sinA = sin(angle);
    return v.x * vec2(cosA, sinA) + v.y * vec2(-sinA, cosA);
}

vec3 shade(vec2 uv, float obj, float size, float offset, float time) {
    float scn = obj;
    vec3  col = vec3(0.,.5,.5);
    float s = 1.;
    vec2 lightPos = vec2(-offset,  offset);
    lightPos = rotate(lightPos, time  + 0.4);

    float dst = length(uv) - obj;
    if(dst < .0001) { s -= 1.; }
    col *= s;
    col += 1.-smoothstep(0.,size,distance(uv,lightPos));

    return col;

}

float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

#define OCTAVES 6
float fbm (in vec2 st) {
    float value = 0.0;
    float amplitude = .5;
    float frequency = 0.;
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * noise(st);
        st *= 2.;
        amplitude *= .5;
    }
    return value;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *=  iResolution.x/iResolution.y;
    uv *=1.7;

    float zooom = (1. + cos(iTime / 3.) / 10.);
    vec2 ruv = uv *  zooom;
    uv.y += sin(iTime / 2.) / 10.;
    uv = rotateUV(ruv, sin(iTime / -4.), vec2(0.));

    float v = smoothstep(0.409, 0.41, length(ruv));
    v = 1. - v;
    float vCenter = smoothstep(0.2, 0.199, length(ruv));
    float vSmallCenter = smoothstep(0.03, 0.031, length(ruv));
    vec3 vCenterC = vCenter * vec3(0.25);
    v -= (1. - v * sin(length(ruv) *800.)) * 0.04 * (1. - vCenter);

    float vN = clamp((pow(voronoi2D(uv * 3., iTime / 4.), 2.)) + 0.25, 0.0, 1.0);
    float fbmNoise = noise(uv * 20. + sin(length(uv) * 20. + iTime));

    vec3 orange = vec3(1., 0.31, 3.);
    vec3 blue = vec3(0.535, 0.38, 0.903);
    vec3 lightBlue = vec3(0.46, 0.71, 0.87);
    vec3 vC = blue;

    vC = mix(vC, lightBlue, smoothstep(0.1, 0.9,  fbmNoise));
    vC = mix(vC, orange,  smoothstep(0.6, 0.9, fbmNoise));
    vC *= max(v, .0);
    vC = (vec3(0., 0.5, 2.0) * vN * (v / 2. + 0.4) * vC + vC);

    vec3 bC = (lightBlue * 2.2) + vec3(0., 0.1, 0.2); 
    bC = mix(bC, blue * 1.6 + lightBlue * 0.5, smoothstep(0.0, 2., (uv.x + 0.5) * length(uv)));
    bC *= vec3(1.2, 1.3, 1.4);

    vec3 sparkles = vec3(smoothstep(0.5, 0.9, length(fract(uv*(12.)+vN+.5) -.5))) * 0.5 * max(mod(iTime / 2. + uv*(2.)+vN, 0.5382).x, 0.3);// * 0.2
    vec2 grid = smoothstep(0., 0.01, mod(uv + vec2(0.003), 0.2));
    float gridC = (min(grid.y, grid.x)) * 0.1;

    vec3 col = mix(vC * v + sparkles * v + gridC * .1 * v, vCenterC,  vCenter);

    vec3 r = col * 1.2 * v
    + ((bC + gridC * 2. + vC * 0.3 ) * ((1. - v) + ((1. - vSmallCenter) * 1.5) * gridC)) * 0.35; 

    r -= shade(ruv, v, .7, 0.3, iTime) * 0.2 * (1. - v);
    r += shade(ruv, v, .3, 0.25, iTime) * 0.25 * v;

    float water = smoothstep(0.4, 0.9, length(uv)) * vN * 0.2;
    r += vec3(water, water, water * 0.8);
    r += vec3(smoothstep(0.55, 0.9, length(fract(uv*(2.)+vN+.5 + sin(iTime / 2.)) -.5))  ) * 0.2  * (1. - v) * vN; 
    r *= (smoothstep(4., 0.5, length(uv)));

    fragColor = vec4(r, 1.0);
}

//https://www.shadertoy.com/view/XlsSRr
//https://www.shadertoy.com/view/tsyfDm
#define LINEWIDTH .03
// Signed Distance to 2D Arc by Adam Simmons
// Based roughly on code by Behdad Esfahbod

// Arc segment with certain depth
struct Arc {
  vec2 p0;
  vec2 p1;

  // Depth of the Arc, valid range of (-1.0, 1.0)
  // with 0.0 = straight line, 1.0 = semi-circle
  //
  float d;
};

vec2 perpendicular (const vec2 v) {
  return vec2 (-v.y, v.x);
}

float tan2atan (float d) {
  return 2. * d / (1. - d * d);
}

vec2 computeArcCenter (const Arc a) {
  return mix (a.p0, a.p1, .5) +
	 perpendicular (a.p1 - a.p0) / (2. * tan2atan (a.d));
}

float sdArcWedge (Arc a, const vec2 p) {
  vec2 c = computeArcCenter (a);
  return -sign (a.d) * (distance (a.p0, c) - distance (p, c));
}

bool isPointInsideArcWedge (const Arc a, const vec2 p) {
  float d2 = tan2atan (a.d);
  return dot (p - a.p0, (a.p1 - a.p0) * mat2(1,  d2, -d2, 1)) >= 0. &&
	 dot (p - a.p1, (a.p1 - a.p0) * mat2(1, -d2,  d2, 1)) <= 0.;
}

float sdArcOutsideWedge (const Arc a, const vec2 p) {
  vec2 m = mix (a.p0, a.p1, .5);
  float d2 = tan2atan (a.d);
  if (dot (p - m, a.p1 - m) < 0.)
    return -dot (p - a.p0, normalize ((a.p1 - a.p0) * mat2(+d2, -1, +1, +d2)));
  else
    return -dot (p - a.p1, normalize ((a.p1 - a.p0) * mat2(-d2, -1, +1, -d2)));
}

float sdArc(Arc arc, vec2 p) {
  // trick to avoid depth values very close to 0.0
//   arc.d = max(abs(arc.d), 0.0001) * (2. * step(0.9, sign(arc.d)) - 1.);

  if (isPointInsideArcWedge(arc, p))
    return sdArcWedge(arc, p);

  return min (distance (p, arc.p0), distance (p, arc.p1)) * sign(sdArcOutsideWedge(arc, p));
}

float DrawSmoothArc(vec2 uv,vec2 a,vec2 b,float d){
    Arc arc;
    arc.p0=a;
    arc.p1=b;
    arc.d=d;
    float dist=abs(sdArc(arc,uv));

    float s=pow(smoothstep(0.,LINEWIDTH,dist),10.);

    return 1.-s;
}

float DistanceToLine(vec2 a, vec2 b, vec2 p)
{
    vec2 pos = p - a;
    vec2 dir = b - a;
    float len = length(dir);
    dir /= len;//
    float f = dot(dir, pos);
    if (f < 0.0)//a
        return distance(a, p);
   	else if (f > len)//
        return distance(b, p);
    else
        return distance(a + dir * f, p);//ap
}
float DrawSmoothLine(vec2 fragCoord, vec2 a, vec2 b, float thickness)
{
    //uv
    float d = DistanceToLine(a, b, fragCoord);
    //,10
    float s = pow(smoothstep(0., thickness, d), 10.);//
    return 1.-s;
}

float Circle(vec2 uv, vec2 centre, float radius)
{
    float d = length(uv - centre);
    float blur=abs(sin(iTime));
    float c = smoothstep(radius, radius - .002, d*blur);
    return c;
}

float line(vec2 uv,vec2 start,vec2 end,float linewidth)
{
    vec2 kv=end-start;
    float k=kv.y==0.?0.:kv.x/kv.y;
    float slit=(uv.x-start.x)*(end.x-uv.x)>0.?1.:0.;
    float line=smoothstep(linewidth, 0.0,abs(uv.y - k*uv.x))*slit;
    return line;
}

vec2 Rotate(vec2 coord, vec2 center,float deg)
{
    vec2 dist=coord-center;

    float angle = radians(deg);
    vec2 rot = dist * mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
    rot+=center;
    return rot;
}
