void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //-1~1
    vec2 uv = (2.0 * fragCoord-iResolution.xy) / iResolution.y;
    vec3 background=vec3(.8,1.,1.);
    vec3 xin=xin(uv);
    vec3 color=mix(background,xin,xin.z);
    vec3 nian=nian(uv);
    vec3 kuai=kuai(uv);
    vec3 le=le(uv);
    color=mix(color,nian,nian.z);
    color=mix(color,kuai,kuai.z);
    color=mix(color,le,le.z);

    for(float i=1.;i<=50.;i++){
        for(float j=1.;j<=20.;j++){
            if(j>20./i) break;
            float size=0.001*i*(1.+rnd(j)/2.);
            float speed=size*.75+rnd(i)/1.5;
            vec2 pos=vec2(0.);
            pos.x=-1.+rnd(j*i)*1.9+.1*cos(iTime+sin(j*i));
            pos.y=fract(sin(j)-speed*iTime)/1.3;
            color+=vec3(1.-i/50.)*starts(uv,pos,size);
        }
    }

    fragColor =vec4(color,1.);
}

const float FOCUS = 4.35;
const float REFRACTION = 0.35;

//Mosaic glass
float mosaicGlass(vec2 p){
    return length(tan(p*sin(iTime*0.3)*FOCUS)*REFRACTION);
}

//Pyramid mosaic
vec3 mosaic(vec3 p,float block){
    return floor(p*block)/block;
}

//Generate a ceiling.
float ceiling(vec3 p){
    return step(-p.y,0.0);
}

vec3 cRepeat(vec3 p){
    return mod(cos(floor(p)),2.0);
}

float sphere(vec3 p){
    return abs(length(p*0.2)-0.1);
}
//////////////////////

//Generate the ground
float ground(vec3 p){
    return step(p.y,0.4);
}

vec3 gRepeat(vec3 p){
    return mod(p,0.9)-0.4;
}

float sdPyramid(vec3 p,float h)
{
  float m2 = h*h+0.25;

  p.xz = abs(p.xz);
  p.xz = (p.z>p.x)?p.zx:p.xz;
  p.xz -= 0.5;

  vec3 q = vec3(p.z,h*p.y-0.5*p.x,h*p.x+0.5*p.y);

  float s = max(-q.x,0.0);
  float t = clamp((q.y-0.5*p.z)/(m2+0.25),0.0,1.0);

  float a = m2*(q.x+s)*(q.x+s) + q.y*q.y;
  float b = m2*(q.x+0.5*t)*(q.x+0.5*t)+(q.y-m2*t)*(q.y-m2*t);

  float d2 = min(q.y,-q.x*m2-q.y*0.5)>0.0?0.0:min(a,b);

  return sqrt((d2+q.z*q.z)/m2)*sign(max(q.z,-p.y));
}
//////////////////////

float mainDist(vec3 p){
    vec3 cP = cRepeat(p);
    float up = ceiling(p)*sphere(cP*0.3);
    vec3 gP = gRepeat(p);
    float under = ground(p)*sdPyramid(mosaic(gP,25.0)*1.02,0.35);
    float d = max(up,under);
    d += up+up+up+up;

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord*2.0-iResolution.xy)/min(iResolution.x,iResolution.y);

    vec3 cd = vec3(0.0,0.0,-1.0);
    vec3 cu = vec3(0.0,1.0,0.0);
    vec3 cs = cross(cd,cu);
    float td = 1.0;
    vec3 ray = normalize(cs * p.x + cu * p.y + cd * td);

    float d,t = 0.0;
    vec3 rp = vec3(sin(iTime*0.5),
        0.4*mosaicGlass(p),
        -iTime*0.8);

    for(int i;i<128;i++){
        d = mainDist(rp + ray * t);
        if(d < 0.001){
            break; 
        }
        t +=d;
    }

    vec3 col1 = step(d,0.0)*vec3(0.3,exp(-0.5*t),exp(-0.1*t));
    vec3 col2 = step(d,0.5)*vec3(exp(-0.2*t),0.2,exp(-0.3*t));

    vec3 col = max(col1,col2);

    fragColor = vec4(col,1.0);
}

#define S(a, b, t) smoothstep(a, b, t)
#define B(a, b, blur, t) S(a-blur, a+blur, t)*S(b+blur, b-blur, t)
#define sat(x) clamp(x, 0., 1.)

float remap01(float a, float b, float t) {
	return sat((t-a)/(b-a));
}

float remap(float a, float b, float c, float d, float t) {
	return sat((t-a)/(b-a)) * (d-c) + c;
}

vec2 within(vec2 uv, vec4 rect) {
	return (uv-rect.xy)/(rect.zw-rect.xy);
}

vec4 Brow(vec2 uv, float smile) {
    float offs = mix(.2, 0., smile);
    uv.y += offs;

    float y = uv.y;
    uv.y += uv.x*mix(.5, .8, smile)-mix(.1, .3, smile);
    uv.x -= mix(.0, .1, smile);
    uv -= .5;

    vec4 col = vec4(0.);

    float blur = .1;

   	float d1 = length(uv);
    float s1 = S(.45, .45-blur, d1);
    float d2 = length(uv-vec2(.1, -.2)*.7);
    float s2 = S(.5, .5-blur, d2);

    float browMask = sat(s1-s2);

    float colMask = remap01(.7, .8, y)*.75;
    colMask *= S(.6, .9, browMask);
    colMask *= smile;
    vec4 browCol = mix(vec4(.4, .2, .2, 1.), vec4(1., .75, .5, 1.), colMask); 

    uv.y += .15-offs*.5;
    blur += mix(.0, .1, smile);
    d1 = length(uv);
    s1 = S(.45, .45-blur, d1);
    d2 = length(uv-vec2(.1, -.2)*.7);
    s2 = S(.5, .5-blur, d2);
    float shadowMask = sat(s1-s2);

    col = mix(col, vec4(0.,0.,0.,1.), S(.0, 1., shadowMask)*.5);

    col = mix(col, browCol, S(.2, .4, browMask));

    return col;
}

vec4 Eye(vec2 uv, float side, vec2 m, float smile) {
    uv -= .5;
    uv.x *= side;

	float d = length(uv);
    vec4 irisCol = vec4(.3, .5, 1., 1.);
    vec4 col = mix(vec4(1.), irisCol, S(.1, .7, d)*.5);		
    col.a = S(.5, .48, d);									

    col.rgb *= 1. - S(.45, .5, d)*.5*sat(-uv.y-uv.x*side); 	

    d = length(uv-m*.4);									
    col.rgb = mix(col.rgb, vec3(0.), S(.3, .28, d)); 		

    irisCol.rgb *= 1. + S(.3, .05, d);						
    float irisMask = S(.28, .25, d);
    col.rgb = mix(col.rgb, irisCol.rgb, irisMask);			

    d = length(uv-m*.45);									

    float pupilSize = mix(.4, .16, smile);
    float pupilMask = S(pupilSize, pupilSize*.85, d);
    pupilMask *= irisMask;
    col.rgb = mix(col.rgb, vec3(0.), pupilMask);		

    float t = iTime*3.;
    vec2 offs = vec2(sin(t+uv.y*25.), sin(t+uv.x*25.));
    offs *= .01*(1.-smile);

    uv += offs;
    float highlight = S(.1, .09, length(uv-vec2(-.15, .15)));
    highlight += S(.07, .05, length(uv+vec2(-.08, .08)));
    col.rgb = mix(col.rgb, vec3(1.), highlight);			

    return col;
}

vec4 Mouth(vec2 uv, float smile) {
    uv -= .5;
	vec4 col = vec4(.5, .18, .05, 1.);

    uv.y *= 1.5;
    uv.y -= uv.x*uv.x*2.*smile;

    uv.x *= mix(2.5, 1., smile);

    float d = length(uv);
    col.a = S(.5, .48, d);

    vec2 tUv = uv;
    tUv.y += (abs(uv.x)*.5+.1)*(1.-smile);
    float td = length(tUv-vec2(0., .6));

    vec3 toothCol = vec3(1.)*S(.6, .35, d);
    col.rgb = mix(col.rgb, toothCol, S(.4, .37, td));

    td = length(uv+vec2(0., .5));
    col.rgb = mix(col.rgb, vec3(1., .5, .5), S(.5, .2, td));
    return col;
}

vec4 Head(vec2 uv) {
	vec4 col = vec4(.9, .65, .1, 1.);

    float d = length(uv);

    col.a = S(.5, .49, d);

    float edgeShade = remap01(.35, .5, d);
    edgeShade *= edgeShade;
    col.rgb *= 1.-edgeShade*.5;

    col.rgb = mix(col.rgb, vec3(.6, .3, .1), S(.47, .48, d));

    float highlight = S(.41, .405, d);
    highlight *= remap(.41, -.1, .75, 0., uv.y);
    highlight *= S(.18, .19, length(uv-vec2(.21, .08)));
    col.rgb = mix(col.rgb, vec3(1.), highlight);

    d = length(uv-vec2(.25, -.2));
    float cheek = S(.2,.01, d)*.4;
    cheek *= S(.17, .16, d);
    col.rgb = mix(col.rgb, vec3(1., .1, .1), cheek);

    return col;
}

vec4 Smiley(vec2 uv, vec2 m, float smile) {
	vec4 col = vec4(0.);

    if(length(uv)<.5) {					
        float side = sign(uv.x);
        uv.x = abs(uv.x);
        vec4 head = Head(uv);
        col = mix(col, head, head.a);

        if(length(uv-vec2(.2, .075))<.175) {
            vec4 eye = Eye(within(uv, vec4(.03, -.1, .37, .25)), side, m, smile);
            col = mix(col, eye, eye.a);
        }

        if(length(uv-vec2(.0, -.15))<.3) {
            vec4 mouth = Mouth(within(uv, vec4(-.3, -.43, .3, -.13)), smile);
            col = mix(col, mouth, mouth.a);
        }

        if(length(uv-vec2(.185, .325))<.18) {
            vec4 brow = Brow(within(uv, vec4(.03, .2, .4, .45)), smile);
            col = mix(col, brow, brow.a);
        }
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime;

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 m = iMouse.xy / iResolution.xy;
    m -= .5;

    if(m.x<-.49 && m.y<-.49) {			
    	float s = sin(t*.5);
        float c = cos(t*.38);

        m = vec2(s, c)*.4;
    }

    if(length(m) > .707) m *= 0.;		

    float d = dot(uv, uv);
    uv -= m*sat(.23-d);

    float smile = sin(t*.5)*.5+.5;
	fragColor = Smiley(uv, m, smile);
}

//Borrowed rainbow function from u/demofox's Rainbow Shader
vec3 rainbow(float level)
{
	/*
		Target colors
		=============

		L  x   color
		0  0.0 vec4(1.0, 0.0, 0.0, 1.0);
		1  0.2 vec4(1.0, 0.5, 0.0, 1.0);
		2  0.4 vec4(1.0, 1.0, 0.0, 1.0);
		3  0.6 vec4(0.0, 0.5, 0.0, 1.0);
		4  0.8 vec4(0.0, 0.0, 1.0, 1.0);
		5  1.0 vec4(0.5, 0.0, 0.5, 1.0);
	*/

	float r = float(level <= 2.0) + float(level > 4.0) * 0.5;
	float g = max(1.0 - abs(level - 2.0) * 0.5, 0.0);
	float b = (1.0 - (level - 4.0) * 0.5) * float(level >= 4.0);
	return vec3(r, g, b);
}

