#define xp(f,a) (pow(6., a* f-a))
#define downthresh 1.04
#define upthresh 1.
#define B 1.5

float drop(vec2 uv, float x, float a, float time){
//    return length(uv.xy-vec2(x, clamp(3.3*a*fract(time), -.5, 1.5)));
//    return length(uv.xy-vec2(x, clamp(3.3*a*fract(time), -1., 5.)-.5));

    return 4.*clamp(length(uv.xy-vec2(x, (6.*fract(time)-.8)*.3*a)),0.,.3);
    //return 4.*clamp(distance(uv.xy, vec2(x, 4.*fract(time)-2.5)),0.,.2);
}

