    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //vec3 col = vec3(1.);

//    float b = drop(uv, 0.6, 1., iTime*0.25);
//         b *= drop(uv, 0.3, 1., .7+iTime*0.25);

   float b = drop(uv, 0.3, 2., iTime*0.25);
         b *= drop(uv, 0.7, 1.4, .4+iTime*0.27);
         b *= drop(uv, 0.1, 1.5, .6+iTime*0.25);
         b *= drop(uv, 0.45, 1.9, .2+iTime*0.26);

    float fb = upthresh-xp(uv.y, 4.);
    fb *= downthresh-xp(1.-uv.y*B, 4.);

    col *= 1.-(b*8.*fb);

    fragColor = vec4(col,1.0);
}

