struct Line
{
    vec2 a;
    vec2 b;
    float t;
};
float dstToLine(vec2 P, Line l)
{
    vec2 g = l.b - l.a;
    vec2 h = P - l.a;
    float d = length(h - g * clamp(dot(g, h) / dot(g,g), 0.0, 1.0));
	return smoothstep(l.t, 0.9*l.t, d);
}
void DrawLine(inout vec3 col, vec2 uv, Line l)
{
    float intensity = dstToLine(uv, l);
    col = mix(col, vec3(1), intensity);
}

