struct Square
{
    vec2 pos;
    float size;
    vec2 maxR;
    vec2 minR;
    vec3 colour;
};
Square newSquare(vec2 p, float s, vec3 c)
{
    Square sq;
    sq.pos = p;
    sq.size = s;
    sq.maxR = vec2(p.x+s, p.y+s);
    sq.minR = vec2(p.x-s, p.y-s);
    sq.colour = c;
    return sq;
}
bool InSquare(vec2 p, Square s)
{
    return (p.x > s.minR.x && p.y > s.minR.y && p.x < s.maxR.x && p.y < s.maxR.y);
}
void DrawSquare(inout vec3 col, vec2 p, Square s)
{
    if (InSquare(p, s))
    {
        col = s.colour;
    }
}
void DrawHollowSquare(inout vec3 col, vec2 p, Square inner, float t)
{
    vec3 prevCol = col;
    DrawSquare(col, p,newSquare(inner.pos, inner.size, inner.colour));
    DrawSquare(col, p,newSquare(inner.pos, inner.size-t, prevCol));
}
void DrawBoxArray(inout vec3 col, vec2 p, Square s, bool top)
{
    float sideLength = s.size/ 2.5f;
    float size = s.size/5.0f;
    vec2 bottomleft = s.minR+vec2(size,size);

    for (int x = 0; x < 5; x++)
    {
        for (int y = 0; y < 5; y++)
        {
            float thisX = bottomleft.x+(float(x)*sideLength);
            float thisY = bottomleft.y+(float(y)*sideLength);

            vec2 thisP = vec2(thisX, thisY);
            vec3 co = vec3(float(x)/5.0f, float(y)/5.0f, 0.0f);
            if (top&&col == co)
            {
                DrawSquare(col, p, newSquare(thisP, size, vec3(1)));
            }
            else
            {
                DrawSquare(col, p, newSquare(thisP, size, co));
            }
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0);
    vec2 middle = iResolution.xy/2.0f;

    //VARIABLES
    vec2 innerBoxPos = middle+vec2(sin(iTime)*50.0f, cos(iTime)*50.0f);
    //END

    Square b = newSquare(middle, 100.0f, vec3(1,1,1));
    DrawBoxArray(col, fragCoord, b, false);
    DrawHollowSquare(col, fragCoord,b, 2.0f);

    Square f = newSquare(innerBoxPos, 50.0f, vec3(0));
    DrawBoxArray(col, fragCoord, f, true);
    DrawHollowSquare(col, fragCoord, f, 2.0f);

    // Output to screen
    fragColor = vec4(col,1.0);
}

bool is_above_line(vec2 r, vec2 q, vec2 p) {
    return (q.y - p.y)*(r.x - q.x)-(q.x - p.x) * (r.y - q.y) > 0.;
}

vec2 reflect_line(vec2 a, vec2 b, vec2 p) {
    vec2 x = 0.5 * (a + b);
    vec2 n = normalize(x);
    return x + reflect(p - x, n);
}
vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}
bool is_heart(vec2 p, float size) {
    p /= size;
    p.x = abs(p.x);
    return 1. - p.y*p.y - 0.5*max(p.y/p.x, 0.) > 2.*p.x;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/ iResolution.y;
    uv *= 10.;

    float brightness = 1.0;
    vec2 center = vec2(0, 0);
    vec2 right = vec2(sqrt(3.)/2., 0.5);
    vec2 left = vec2(-sqrt(3.)/2., 0.5);
    vec2 bottom = vec2(0., -1.);
    vec2 p = uv;

    for (int i = 0; i < 10; i++) {
        bool lr = is_above_line(left, right, p);
        bool rb = is_above_line(right, bottom, p);
        bool bl = is_above_line(bottom, left, p);
        bool cr = is_above_line(center, right, p);
        bool cb = is_above_line(center, left, p);
        bool cl = is_above_line(center, bottom, p);
        if (lr || rb || bl) {
            if (lr && (cr && !cb)) {
                p = reflect_line(left, right, p);
            } else if (rb && (cl)) {
                p = reflect_line(right, bottom, p);
            } else if (bl && (cb)) {
                p = reflect_line(bottom, left, p);
            } else {
                p = vec2(0.);
            }
            brightness *= 0.85;
        }
    }

    // Time varying pixel color
    p = rotate(p + .7*cos(iTime), iTime);
    float size = 2. + max(0., sin(24.*mod(0.25*iTime, .1)));
    if (is_heart((mod(3.*p, 2.0) + vec2(-1.0)), size*0.25)) {
        p += vec2(3.0);
        p *= 0.25;
    }
    vec3 col = 0.5*cos(iTime + p.xyx+vec3(0,2,4)) + 0.1*sin(p.xyx*10.);
    col.g = 0.;

    if (distance(vec2(0.), p) < 0.01) {
        col = vec3(0.);
    }

    // Output to screen
    fragColor = vec4(brightness*col, 1.0);
}

// variant of "field art" by FabriceNeyret2. https://shadertoy.com/view/wtVyRW

#define hash2(p) fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)

// --- flownoise from https://www.shadertoy.com/view/wdKXzd
// gradient noise derived from https://www.shadertoy.com/view/XdXGW8

float _z = 1.;
vec2 hash( vec2 p ) 
{
 // float s = 0.;                      // standard Perlin noise
    float s = mod(p.x+p.y,2.)*2.-1.;   // flow noise checkered rotation direction
 // float s = mod(floor(p.x)+floor(p.y),2.)*2.-1.;   // should be this but x is integer
 // float s = 1.;                      // flow noise universal rotation direction
 // s *= iTime;                        // same rotation speed at all scales
    s *= iTime/ _z;                    // rotation speed increase with small scale    

    return ( -1. + 2.*hash2(p) ) 
        *  mat2(cos( s + vec4(0,33,11,0))); // rotating gradients. rot: https://www.shadertoy.com/view/XlsyWX
}

float noise( vec2 p )
{
    vec2 i = floor( p ),
         f = fract( p ),
	  // u = f*f*(3.-2.*f);
         u = f*f*f* ( 10. + f * ( -15. + 6.* f ) ); // better for derivative. from http://staffwww.itn.liu.se/~stegu/TNM022-2005/perlinnoiselinks/paper445.pdf

#define P(x,y) dot( hash( i + vec2(x,y) ), f - vec2(x,y) )
    return mix( mix( P(0,0), P(1,0), u.x),
                mix( P(0,1), P(1,1), u.x), u.y);
}

#define noise01(p) 1.5 * ( .5+.5* noise(p)-.18 )    // well fitted to [0,1] without saturation

float perlin( vec2 p )                              // fractal noise
{	
    mat2 m = mat2(2.); // mat2( 1.6,  1.2, -1.2,  1.6 );
    float v  = 0.,s = 1.;
    for( int i=0; i < 5; i++, s /= 2. ) { _z = s;   // for flownoise
        v += s*noise01( p ); p *= m;                // variant using positive noise
    }
    return v;
}

// --- sampling inspired from https://www.shadertoy.com/view/lljczz

#define srnd(p,s) ( 2.* fract( 4e4* sin( 1e3* mat2(1,7.1,-5.1,2.3) * (p) + 1e3*s )) -1. )

// #define f(U)   noise01(U)
   #define f(U)   noise01(ofs(U))
// #define f(U) ( noise01(ofs(U)) + .5*noise01(2.*ofs(U)) ) / 1.2
// #define f(U)   perlin(U) / 1.5     
// #define f(U)   perlin(ofs(U)) / 1.5
#define ofs(U) ( (U) *vec2(1,2) + vec2(.3*iTime,0) )

void mainImage( out vec4 O,  vec2 u )
{
    vec2 R = iResolution.xy, eps = vec2(1./R.y,0),
         U = (2.*u-R)/R.y, p, P, X,V, x;
    O-=O;       

    float v, n =   11., // R.y/32., // 11.                // number of cells (optim)
          l = .08,                                        // stokes length
       // d = 90.*pow((1.-U.y)/2.,1.7)*R.y/360.;          // strokes density
          d = 90.*pow(max(f(U)-.25 -.0*U.y,0.),1.7)*R.y/360.;// strokes density
    int N = 3; // int(3.*R.y/360.);                       // Neighborhood size = NxN . goes with l

    p = floor(U*n+.5) / n;                                // cell center (NB: we should deffered /n )

    for( int i=0; i<N*N; i++)                             // Worley optim. allows overflow to neighbor cell
      for( float j=0.; j<d; j++) {                        // density loop
        P = p + vec2( i%N -N/2, i/N -N/2 ) / n;           // cell coordinate in neighborhood
        P +=     .5* srnd( round(P*n) , j) / n;           // jittering (round: for precision issues)
        X = U-P,                                          // local coordinate
        V; // =  f(.5*U);    // f(.5*P)                   // local field.
        v = f(U); // v = V.x;
        V = vec2(  dFdx(v), dFdy(v) ) * R.y;              // divergence-free  ( since we trace ortho(V) )  
     // V = vec2( -dFdy(v), dFdx(v) ) * R.y;              // divergence-free  ( if we were tracing V )  
     // V = vec2( f(P)-f(P-eps), f(P)-f(P-eps.yx) );      // tangents rather than curves

        v = dot ( X , V );                                // distance to segment
     // v /= fwidth(v);
        v /= length(V) * 2.5/R.y;
        O += (1.-O)*                                      // blend ( comment out for add )
             .5* max( 0., 1. - 1.3*abs(v) )               // draw segment of dir V and length .04
               * clamp( ( l - length(X) ) *R.y ,0.,1.);
}

 // O -=  max(0., length(U)-.8 ) *R.y;                    // white out of circle
    O = sqrt(1.-O);                                       // reverse + to sRGB
}

// Licence CC0: Simple plane marcher
//  I had fun messing around with "plane marching"
//  While not right to produce photo-realistic shaders you 
//  create some pretty interesting effects with them
//  Thought others might appreciate a basic "plane marcher"
//  to play around with

// -----------------------------------------------------------------------------
// COMMON
// -----------------------------------------------------------------------------

#define PI              3.141592654
#define TAU             (2.0*PI)
#define TIME            iTime
#define RESOLUTION      iResolution
#define ROT(a)          mat2(cos(a), sin(a), -sin(a), cos(a))

const vec3 std_gamma        = vec3(2.2, 2.2, 2.2);

float hash(float co) {
  return fract(sin(co*12.9898) * 13758.5453);
}

vec4 alphaBlend(vec4 back, vec4 front) {
  float w = front.w + back.w*(1.0-front.w);
  vec3 xyz = (front.xyz*front.w + back.xyz*back.w*(1.0-front.w))/w;
  return w > 0.0 ? vec4(xyz, w) : vec4(0.0);
}

vec3 alphaBlend(vec3 back, vec4 front) {
  return mix(back, front.xyz, front.w);
}

float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

vec2 mod2_1(inout vec2 p) {
  vec2 c = floor(p + 0.5);
  p = fract(p + 0.5) - 0.5;
  return c;
}

float hex(vec2 p, float r) {
  const vec3 k = vec3(-sqrt(3.0)/2.0,1.0/2.0,sqrt(3.0)/3.0);
  p = p.yx;
  p = abs(p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

// -----------------------------------------------------------------------------
// PATH
// -----------------------------------------------------------------------------

// The path function
vec3 offset(float z) {
  float a = z;
  vec2 p = -0.075*(vec2(cos(a), sin(a*sqrt(2.0))) + vec2(cos(a*sqrt(0.75)), sin(a*sqrt(0.5))));
  return vec3(p, z);
}

// The derivate of the path function
//  Used to generate where we are looking
vec3 doffset(float z) {
  float eps = 0.1;
  return 0.5*(offset(z + eps) - offset(z - eps))/eps;
}

// The second derivate of the path function
//  Used to generate tilt
vec3 ddoffset(float z) {
  float eps = 0.1;
  return 0.125*(doffset(z + eps) - doffset(z - eps))/eps;
}

// -----------------------------------------------------------------------------
// PLANE MARCHER
// -----------------------------------------------------------------------------

// Plane generating function returns rgba
//  pp is point on plane
//  off is path at plane z
//  aa is estimated pixel size at the plane
//  n is plane number
vec4 plane(vec3 ro, vec3 rd, vec3 pp, vec3 off, float aa, float n) {
  float h = hash(n);
  float s = mix(0.05, 0.25, h);

  vec3 hn;
  vec2 p = (pp-off*vec3(1.0, 1.0, 0.0)).xy;
  p *= ROT(TAU*h);
  const float lw = 0.005;
  float d = hex(p/s, 0.5)*s;   
  float dd = abs(d) - lw;

  float a  = smoothstep(-aa, aa, -dd);
  float ha = smoothstep(-aa, aa, d+lw);

  vec4 col = vec4(mix(vec3(1.0-tanh_approx(15.0*length(p))), vec3(0.0), a), ha);

  return col;
}

vec3 skyColor(vec3 ro, vec3 rd) {
  float ld = max(dot(rd, vec3(0.0, 0.0, 1.0)), 0.0);
  return vec3(tanh_approx(3.0*pow(ld, 100.0)));
}

vec3 color(vec3 ww, vec3 uu, vec3 vv, vec3 ro, vec2 p) {
  float lp = length(p);
  vec2 np = p + 1.0/RESOLUTION.xy;
  float rdd = (2.0+0.5*tanh_approx(lp));  // Playing around with rdd can give interesting distortions
  vec3 rd = normalize(p.x*uu + p.y*vv + rdd*ww);
  vec3 nrd = normalize(np.x*uu + np.y*vv + rdd*ww);

  const float planeDist = 1.0-0.75;
  const int furthest = 11;
  const int fadeFrom = max(furthest-6, 0);
  const float fadeDist = planeDist*float(furthest - fadeFrom);
  float nz = floor(ro.z / planeDist);

  vec3 skyCol = skyColor(ro, rd);


  vec4 acol = vec4(0.0);
  const float cutOff = 0.95;
  bool cutOut = false;

  // Steps from nearest to furthest plane and accumulates the color 
  for (int i = 1; i <= furthest; ++i) {
    float pz = planeDist*nz + planeDist*float(i);

    float pd = (pz - ro.z)/rd.z;

    if (pd > 0.0 && acol.w < cutOff) {
      vec3 pp = ro + rd*pd;
      vec3 npp = ro + nrd*pd;

      float aa = 3.0*length(pp - npp);

      vec3 off = offset(pp.z);

      vec4 pcol = plane(ro, rd, pp, off, aa, nz+float(i));

      float nz = pp.z-ro.z;
      float fadeIn = exp(-2.5*max((nz - planeDist*float(fadeFrom))/fadeDist, 0.0));
      float fadeOut = smoothstep(0.0, planeDist*0.1, nz);
      pcol.xyz = mix(skyCol, pcol.xyz, (fadeIn));
      pcol.w *= fadeOut;
      pcol = clamp(pcol, 0.0, 1.0);

      acol = alphaBlend(pcol, acol);
    } else {
      cutOut = true;
      break;
    }

  }

  vec3 col = alphaBlend(skyCol, acol);
// To debug cutouts due to transparency  
//  col += cutOut ? vec3(1.0, -1.0, 0.0) : vec3(0.0);
  return col;
}

// Classic post processing
vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, 1.0/std_gamma);
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

vec3 effect(vec2 p, vec2 q) {
  float tm  = TIME*0.4;
  vec3 ro   = offset(tm);
  vec3 dro  = doffset(tm);
  vec3 ddro = ddoffset(tm);

  vec3 ww = normalize(dro);
  vec3 uu = normalize(cross(normalize(vec3(0.0,1.0,0.0)+ddro), ww));
  vec3 vv = normalize(cross(ww, uu));

  vec3 col = color(ww, uu, vv, ro, p);
  col = postProcess(col, q);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = effect(p, q);

  fragColor = vec4(col, 1.0);
}

vec2 fragCoord_;
uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
const uint max32 = 0xffffffffu;
vec2[8] gtable2 = vec2[](   //0.92387953 = cos(pi/8), 0.38268343 = cos(pi/8)
    vec2(0.92387953, 0.38268343),
    vec2(0.38268343, 0.92387953),
    vec2(-0.92387953, 0.38268343),
    vec2(-0.38268343, 0.92387953),
    vec2(0.92387953, -0.38268343),
    vec2(0.38268343, -0.92387953),
    vec2(-0.92387953, -0.38268343),
    vec2(-0.38268343, -0.92387953)
);
uint hash11u(uint n) {
    n ^= (n << 24);
    n ^= (n >> 1);
    n ^= (n << 1);
    return n * k.x;
}
vec2 hash22(vec2 p) {
        uint n = hash11u(hash11u(uint(p.x)) + uint(p.y));
        n = n >> 29;
        n = n % 8u;
        return gtable2[n];
}
float pnoise21(vec2 p) {
    vec2 f = fract(p);
    vec2 g00 = hash22(p);
    vec2 g01 = hash22(p + vec2(0.0, 1.0));
    vec2 g10 = hash22(p + vec2(1.0, 0.0));
    vec2 g11 = hash22(p + vec2(1.0, 1.0));
    float v00 = dot(g00, f);
    float v01 = dot(g01, f - vec2(0.0, 1.0));
    float v10 = dot(g10, f - vec2(1.0, 0.0));
    float v11 = dot(g11, f - vec2(1.0, 1.0));
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f); 
    float v = mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y);
    return v;
}
float hash21(vec2 p) {
    p = mod(p, 1000.0);
    uint n = hash11u(uint(p.x)) + uint(p.y);
    return float(hash11u(n)) / float(max32);
}
float vnoise21(vec2 p) {
    vec2 i = floor(p);
    float v00 = hash21(i);
    float v01 = hash21(i + vec2(0.0, 1.0));
    float v10 = hash21(i + vec2(1.0, 0.0));
    float v11 = hash21(i + vec2(1.0, 1.0));
    vec2 f = fract(p);
    f = f * f * (3.0 -2.0 * f); 
    return mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y) - 0.5;
}

float fbm21(vec2 p, float G){
    float val = 0.0;
    float amp =1.0;
    float freq = 1.0;
    for (int i = 0; i < 4; i++){
        val += amp * vnoise21(freq * p);
        amp *= G;
        freq *= 2.01;
    }
    return val;
}
float warp21(vec2 p, float G){
    float val = 0.0;
    for (int i = 0; i < 4; i++){
        float t = G * val + iTime * 0.1;
        vec2 dir = vec2(sin(t), cos(t));
        if (fragCoord_.x < 0.5 * iResolution.x){
            val = fbm21(p + dir, 0.5);
        } else{
            val = pnoise21(p + dir);
        }
    }
    return val;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    fragCoord_ = fragCoord;
    vec2 pos = fragCoord.xy/min(iResolution.x, iResolution.y);
    pos *= 10.;
    pos += 0.5 * iTime;
    float v = 0.5 * warp21(pos, 3.0) + 0.5;  //down
    fragColor = vec4(vec3(v), 1.0);
}

