#define PI 3.14159265358979323846

float rand(vec2 coord){
    coord = mod(coord,10000.);
    return fract(sin(dot(coord, vec2(12.9898,78.233)))*43758.5453);
}

float random (in float x) {
    return fract(sin(x)*1e4);
}


vec2 rand2(vec2 coord){
    coord = mod(coord,10000.);
    return fract(sin( vec2( dot(coord,vec2(127.1,311.7)), dot(coord,vec2(269.5,183.3)) ) ) * 43758.5453);
}

float circle (in vec2 _st, in float _radius, in float _size){
    vec2 dist = _st-vec2(0.5);
    return smoothstep(_radius-_radius*_size,_radius+_radius*_size,dot(dist,dist)*4.0) ;       
}

float value_noise(vec2 coord){
    vec2 i = floor(coord);
    vec2 f = fract(coord);

    float tl = rand(i);
    float tr = rand(i + vec2(1.,0.));
    float bl = rand(i + vec2(0.,1.));
    float br = rand(i + vec2(1.,1.));

    //linear distance will cause sharp transition 
    //use smoothstep or cubic function

    vec2 cubic = f*f*(3.-2.*f);

    float topmix = mix(tl,tr,cubic.x);
    float botmix = mix(bl,br,cubic.x);
    float wholemix = mix(topmix,botmix,cubic.y);

    return wholemix;

}

float perlin_noise(vec2 coord){
    vec2 i = floor(coord);
    vec2 f = fract(coord);

    float tl = rand(i) * 6.283;
    float tr = rand(i + vec2(1.,0.))* 6.283;
    float bl = rand(i + vec2(0.,1.))* 6.283;
    float br = rand(i + vec2(1.,1.))* 6.283;

    vec2 tlvec = vec2(-sin(tl),cos(tl));
    vec2 trvec = vec2(-sin(tr),cos(tr));
    vec2 blvec = vec2(-sin(bl),cos(bl));
    vec2 brvec = vec2(-sin(br),cos(br));

    float tldot = dot (tlvec,f);
    float trdot = dot(trvec,f - vec2(1.,0.));
    float bldot = dot(blvec, f- vec2(0.,1.));
    float brdot = dot(brvec, f - vec2(1.,1.));

    // or delete the +0.5 after the wholemix - this shows different patterns 
    //tldot = abs(tldot);
    ////trdot = abs(trdot);
    //bldot = abs(bldot);
    //brdot = abs(brdot);

    vec2 cubic = f*f*(3.-2.*f);

    float topmix = mix(tldot,trdot,cubic.x);
    float botmix = mix(bldot,brdot,cubic.x);
    float wholemix = mix(topmix,botmix,cubic.y);//maintain the brightness 

    return wholemix+0.5;

}

float cellular_noise(vec2 coord){
   vec2 i = floor(coord);
    vec2 f = fract(coord);

    float min_dist=9999.;
   for(float x = -1.0; x <= 1.0; x++) {
		for(float y = -1.0; y <= 1.0; y++) {

        vec2 node = rand2(i+vec2(x,y))+vec2(x,y);
        float dist = sqrt((f-node).x*(f-node).x+(f-node).y*(f-node).y);
        min_dist = min(min_dist,dist);
       }
    }
    return min_dist;

}

// can use it to combine with other noise functions 
float fbm(vec2 coord, int OCTAVES){
    //for loop size 
    //int OCTAVES =4;

    float normalize_factor =0.;
    float value =0.;
    float scale = 0.5; 

    for (int i =0; i< OCTAVES; i++){
    value += cellular_noise(coord)*scale;
    normalize_factor +=scale; 
    coord *=2.;
    scale *= 0.5;
    }
    return value/normalize_factor; 


}

