#define sabs(x)sqrt((x)*(x)+.005)
#define sabs2(x)sqrt((x)*(x)+1e-4)
#define smax(a,b) (a+b+sabs2(a-b))*.5

void sfold90(inout vec2 p)
{
    p=(p.x+p.y+vec2(1,-1)*sabs(p.x-p.y))*.5;
}

float map(vec3 p){
	p.xy *= rot(iTime*0.3);
    p.yz *= rot(iTime*0.4);
    vec3 q=p;
    p=abs(p)-4.;
    sfold90(p.xy);
    sfold90(p.yz);
    sfold90(p.zx);

	float s=2.5;
	p=sabs(p);
	vec3  p0 = p*1.5;
	for (float i=0.; i<4.; i++){
    	p=1.-sabs2(sabs2(p-2.)-1.); 
    	float g=-5.5*clamp(.7*smax(1.6/dot(p,p),.7),.0,5.5);
    	p*=g;
    	p+=p0+normalize(vec3(1,5,12))*(5.-.8*i);
        s*=g;
	}
	s=sabs(s);
	float a=25.;
	p-=clamp(p,-a,a);

	q=abs(q)-vec3(3.7);
    sfold90(q.xy);
    sfold90(q.yz);
    sfold90(q.zx);
  	return smax(max(abs(q.y),abs(q.z))-1.3,length(p)/s-.00);
}

vec3 calcNormal(vec3 pos){
  vec2 e = vec2(1,-1) * 0.005;
  return normalize(
    e.xyy*map(pos+e.xyy)+e.yyx*map(pos+e.yyx)+ 
    e.yxy*map(pos+e.yxy)+e.xxx*map(pos+e.xxx)
  );
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<80;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<0.001) return t;
        if (t>=far) return far;
    }
    return far;
}

float calcShadow( vec3 light, vec3 ld, float len ) {
	float depth = march( light, ld, 0.0, len );	
	return step( len - depth, 0.01 );
}

