}

float map(float value, float min1, float max1, float min2, float max2) {
  if(value > max1){
      value = max1;
  }
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}


float speedControl(float t, float min1,float max1, vec2 uv){
    t = t/7200.;
    t = fract(.65 * t);
    if(t >= max1)
