    uv += vec2(-1. * _pos.x, _pos.y);
    float r = box(uv,_size,.001);
    return r;
}

float RectGenerator(vec2 _st, float ran,float num, float time){
    float r = 0.;
    for(float i = 0.;i < num; i++){
        float x = random(vec2(i * .1 + .5));
        float x2 =  random(vec2(i * .4 + .5));
        r += Rect(_st,vec2(x * move(i + ran,time),1./num),vec2(x2 + sin(time *(x2 + .5) *ran),(1./num)* i));//move(i + ran,iTime)
    }
