#define BIG_NUMBER 1000000.0
#define FOCAL_LENGTH 0.25

vec3 Hash13(in float x) {
   vec3 p = fract(x * vec3(0.1031, 0.103, 0.0973));
   p += dot(p, p.yzx + 33.33);
   return fract((p.xxy + p.yzz) * p.zyx); 
}

float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

float fakeSphere(in vec2 p, in vec3 o, in float r) {
    float persp = FOCAL_LENGTH / o.z;
    o.xy *= persp;
    r *= persp;

    p -= o.xy;
    float d = length(p);
    if (d < r) {
        return -cos(asin(d / r)) * r + o.z;
    }

    return BIG_NUMBER;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    float depth = BIG_NUMBER;
    for (float seed=318.547; seed < 338.547; seed++) {
        vec3 o1 = Hash13(seed + floor(iTime)) * vec3(2.0, 2.0, 1.0) - vec3(1.0, 1.0, 0.0);
        vec3 o2 = Hash13(seed + ceil(iTime)) * vec3(2.0, 2.0, 1.0) - vec3(1.0, 1.0, 0.0);
        vec3 o = mix(o1, o2, fract(iTime));
        float r = 0.1 + 0.1 * Hash11(seed);
        depth = min(depth, fakeSphere(uv, o, r));
    }

    fragColor += 0.75 - depth;
}

float Circle(vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float Smiley(vec2 uv, vec2 p, float size)  {
    uv -= p; // translates coord system
    uv *= 1.0/size; // scales coord system

    float mask = Circle(uv, vec2(0.0, 0.0), 0.4, 0.002);

    mask -= Circle(uv, vec2(-0.2, 0.1), 0.15, 0.002);
    mask -= Circle(uv, vec2(0.2, 0.1), 0.15, 0.002);

    mask += Circle(uv, vec2(-0.25, 0.07), 0.07, 0.002);
    mask += Circle(uv, vec2(0.15, 0.07), 0.07, 0.002);

    float mouth = Circle(uv, vec2(0.0, -0.1), 0.2, 0.002);
    mouth -= Circle(uv, vec2(0.0, -0.05), 0.2, 0.002);

    mask -= clamp(mouth, 0.0, 1.0);

    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float move = sin(iTime);
    move *= 0.05;
    vec3 col = vec3(0.0);

    float mask = Smiley(uv, vec2(move, 0), 1.0);
    mask += Smiley(uv, vec2(-0.7, move + 0.3), 0.3);
    mask += Smiley(uv, vec2(-0.7, move - 0.3), 0.3);
    mask += Smiley(uv, vec2(0.7, move + 0.3), 0.3);
    mask += Smiley(uv, vec2(0.7, move - 0.3), 0.3);
    col = vec3(0.9 ,0.8 ,0.0) * mask;


    fragColor = vec4(col, 1.0);
}

