#define FROM_SCREEN(uv) ((2.*uv-iResolution.xy)/iResolution.y)

////////////////////////////////////////////////////////////////
// scene controls

// alternative distance metrics

#define MANHATTAN_
#define DOTTY_

// toggle grayscale single layer of voronoi
#define BASIC_

// in case the image gets too dark
#define GAMMA 2.

// alternative color scheme( may need to bump up GAMMA)
#define GLASSY_

// for some reason... mix isn't working for me...
#define MIX(x,y,a)   (1.-a) * x + y * a
#define MIX_(x,y,a)  mix(x,y,a)
#define USE_MIX_INSTEAD_OF_IFS___NOT

////////////////////////////////////////////////////////////////
// functions 

vec2 hash22( in vec2 uv ) {
    vec3 q = fract( uv.xyx * vec3( 19.191, 53.733, 73.761 ) );
    q += dot( q, q + vec3( 41.557, 23.929, 37.983 ) );
    return fract( vec2( q.x * q.y, q.y * q.z ) );
}

vec4 makeVoronoiPoint( in vec2 id ) {
    vec2 n = hash22( id ) ;
    vec2 point = sin( n * iTime ) * .5 + .5;
    return vec4( point.xy, n.xy );
}

vec3 calculateVoronoiDistance( in vec2 st, in vec2 id, in vec2 neighbor ) {
    vec4 voronoiPoint = makeVoronoiPoint( id + neighbor );
    st -= voronoiPoint.xy + neighbor;

#ifdef MANHATTAN
    float d = abs( st.x ) + abs( st.y );
#else
#ifdef DOTTY
    st = abs( st );
    float d = dot( st, st ) / ( st.x + st.y );
#else
    float d = length( st );
    d = dot( st, st ) ;/// (abs(st.x) + abs(st.y));
#endif
#endif

    return vec3( d, voronoiPoint.xy );
    return vec3( d, id );
}

vec3 voronoiToColor( in vec2 v ) {
    vec3 a = mix( vec3(1.,.0,.0), vec3(.0,.0,1.), sin( v.x ) );
    vec3 b = mix( vec3(.0,1.,.0), vec3(.5,.0,.5), sin( v.y ) );
    return mix( a, b, cos( v.x * v.y ) );
}

vec3 calculateVoronoiPoint( in vec2 st, in vec2 id ) {
    vec3 voronoi_n1_n1 = calculateVoronoiDistance( st, id, vec2( -1., -1. ) );
    vec3 voronoi_n1_n0 = calculateVoronoiDistance( st, id, vec2( -1., -0. ) );
    vec3 voronoi_n1_p1 = calculateVoronoiDistance( st, id, vec2( -1., +1. ) );
    vec3 voronoi_n0_n1 = calculateVoronoiDistance( st, id, vec2( -0., -1. ) );
    vec3 voronoi_n0_n0 = calculateVoronoiDistance( st, id, vec2( -0., -0. ) );
    vec3 voronoi_n0_p1 = calculateVoronoiDistance( st, id, vec2( -0., +1. ) );
    vec3 voronoi_p1_n1 = calculateVoronoiDistance( st, id, vec2( +1., -1. ) );
    vec3 voronoi_p1_n0 = calculateVoronoiDistance( st, id, vec2( +1., -0. ) );
    vec3 voronoi_p1_p1 = calculateVoronoiDistance( st, id, vec2( +1., +1. ) );

    vec3 closest = vec3( 1e33 );
#ifdef USE_MIX_INSTEAD_OF_IFS
    closest = MIX( closest, voronoi_n1_n1, step( voronoi_n1_n1.x, closest.x ) );
    closest = MIX( closest, voronoi_n1_n0, step( voronoi_n1_n0.x, closest.x ) );
    closest = MIX( closest, voronoi_n1_p1, step( voronoi_n1_p1.x, closest.x ) );
    closest = MIX( closest, voronoi_n0_n1, step( voronoi_n0_n1.x, closest.x ) );
    closest = MIX( closest, voronoi_n0_n0, step( voronoi_n0_n0.x, closest.x ) );
    closest = MIX( closest, voronoi_n0_p1, step( voronoi_n0_p1.x, closest.x ) );
    closest = MIX( closest, voronoi_p1_n1, step( voronoi_p1_n1.x, closest.x ) );
    closest = MIX( closest, voronoi_p1_n0, step( voronoi_p1_n0.x, closest.x ) );
    closest = MIX( closest, voronoi_p1_p1, step( voronoi_p1_p1.x, closest.x ) );
#else
    if ( voronoi_n1_n1.x < closest.x ) closest = voronoi_n1_n1;
    if ( voronoi_n1_n0.x < closest.x ) closest = voronoi_n1_n0;
    if ( voronoi_n1_p1.x < closest.x ) closest = voronoi_n1_p1;
    if ( voronoi_n0_n1.x < closest.x ) closest = voronoi_n0_n1;
    if ( voronoi_n0_n0.x < closest.x ) closest = voronoi_n0_n0;
    if ( voronoi_n0_p1.x < closest.x ) closest = voronoi_n0_p1;
    if ( voronoi_p1_n1.x < closest.x ) closest = voronoi_p1_n1;
    if ( voronoi_p1_n0.x < closest.x ) closest = voronoi_p1_n0;
    if ( voronoi_p1_p1.x < closest.x ) closest = voronoi_p1_p1;
#endif
    return closest;
}

vec3 vornoing( in vec2 uv, float scale ) {
    uv *= scale;

    vec2 st = fract( uv );
    vec2 id = uv - st;

    vec3 closest = calculateVoronoiPoint( st, id );
#ifdef GLASSY
    return voronoiToColor( closest.yz ) * closest.x;
#else
    return vec3( closest.x );
#endif
}

////////////////////////////////////////////////////////////////
// main

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = FROM_SCREEN( fragCoord );

#ifdef BASIC
    fragColor = vec4( vornoing( uv, 4. ), 1. );
    return;
#endif

    float t = iTime * .01;
    float s = sin( t );

    vec3 color = vec3( .0 );
    color += .4 * vornoing( uv + 33. * s, 1. );
    color += .3 * vornoing( uv + 17. * s, 3. );
    color += .2 * vornoing( uv +  9. * s, 7. );
    color += .1 * vornoing( uv +  5. * s, 13. );

    color *= .5 + .5 * vec3( cos( t ), sin( t ), cos( t * 2. ) - sin( t * 2. + .77 ) );

    fragColor = vec4( color * GAMMA, 1. );
}

