 float PerlinNoise(float x) {
                float aL = rand(vec2(floor(x), 0.1));
                float aR = rand(vec2(floor(x + 1.0), 0.1));
                float wL = aL * fract(x);
                float wR = aR * (fract(x) - 1.0);
                float f = fract(x);
                float u = pow(f, 2.0) * (3.0 - 2.0 * f);
                float n = mix(wL, wR, u);

                return n;
            }

float Box(vec3 p,vec3 size)
{
