}

float map(vec3 p,inout vec3 col,float freqs[4])
{
    p.z-=iTime;

    vec3 pos=p;
    float modNum=0.75;
    //get axis random id
    float xid=floor(pos.x/modNum);
    float yid=floor(pos.y/modNum);
    float zid=floor(pos.z/modNum);

    pos.xz=mod(pos.xz,modNum)-0.5*modNum;

    //get constant id
    int id=(int(floor(rand(vec2(xid,zid))*3.0)))%3;
    float aud=0.0;
    if(id==0)
    {
        col=vec3(1.0,0.0,0.0);
        aud=freqs[0];
    }else if(id==1)
    {
        col=vec3(0.0,1.0,0.0);
        aud=freqs[1];
    }else if(id==2)
    {
        col=vec3(0.0,0.0,1.0);
        aud=freqs[2];
    }

    return Box(pos,vec3(0.2,
    aud*2.0
   // +PerlinNoise(float(xid))*10.0+PerlinNoise(float(zid))*10.0
    ,0.2))-0.15;

}

float freqs[4];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    freqs[0] = texture( iChannel0, vec2( 0.01, 0.25 ) ).x;
	freqs[1] = texture( iChannel0, vec2( 0.07, 0.25 ) ).x;
	freqs[2] = texture( iChannel0, vec2( 0.15, 0.25 ) ).x;
	freqs[3] = texture( iChannel0, vec2( 0.30, 0.25 ) ).x;
    //-----------

    vec2 uv = (fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 col = vec3(0,0,0);

    vec3 ro=vec3(0.0,-8.0,6.0);
    vec3 target=vec3(0,0,0);
    vec3 cDir=normalize(target-ro);
    vec3 cSide=cross(cDir,vec3(0.0,1.0,0.0));
    vec3 cUp=cross(cDir,cSide);
    float depth=1.0;
    vec3 rd=vec3(uv.x*cSide+uv.y*cUp+depth*cDir);

       vec3 mapCol = vec3(0);
    float d, t = 0.;
    for(int i=0;i<128;i++)
    {
        d = map(ro + rd*t, mapCol,freqs);
        if(d<0.001||t>100.0)break;
        t+=d*.35;
    }


    vec3 p = ro + rd*t;
    // Directional derivative lighting.   
    float dif = max((map(p - .01, mapCol,freqs) - map(p, mapCol,freqs))/.01, 0.);
    col = vec3(exp(-0.05*t))*mapCol*(dif + .2)*2.;

    fragColor = vec4(sqrt(max(col, 0.)), 1); // Gamma correction.
}

mat2 Rot(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

mat3 RotX(float a){
    float s = sin(a);
    float c = cos(a);

    return mat3(1., .0, .0, .0, c, -s, .0, s, c);

}

mat3 RotY(float a){
    float s = sin(a);
    float c = cos(a);

    return mat3(c, .0, s, .0, 1., .0, -s, .0, c);

}

mat3 RotZ(float a){
    float s = sin(a);
    float c = cos(a);

    return mat3(c, -s, .0, s, c, .0, .0, .0, 1.);

}

float Box(vec3 p, vec3 sc, float r){
    return length(max(abs(p)-sc, 0.))-r;
}

float N21(vec2 p){
    p = fract(p*vec2(233.34, 851.73));
    p += dot(p, p+23.45);
    return fract(p.x*p.y);

}

vec2 map(vec3 p){

    vec2 d = vec2(0.0);



    vec3 c = vec3(5.0, 4.0, 5.0);
    vec3 q = mod(p+0.5*c,c)-0.5*c;
    q = abs(q);

    float sp;
    float time = mod(iTime, 10.0);
    if(time < 5.0)
        sp= length(q - vec3(0.0, 2.0, 0.0)) - 1.2;
    else
        sp = Box(q - vec3(0.0, 2.0, 0.0), vec3(1.0), 0.1);


    d.x = sp;
    d.y = 1.0;




    return d;

}

vec3 RM(vec3 ro, vec3 rd, float _d){
    vec3 d = vec3(_d, 0.0, 0.0);
    for(int i = 0;i < 100;i++){
        d.yz = map(ro + d.x * rd).xy;

        if(abs(d.y) <(0.001))
            break;

        d.x += d.y;

        if(d.x > 30.0)break;

    }
    if(d.x > 30.0)d.x = -1.0;

    return d;

}

vec3 calcNormal( in vec3 pos, in float t, in float px )
{
    vec4 tmp;
    vec2 e = vec2(1.0,-1.0)*0.5773*0.25*px;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}




vec3 getLight(vec3 p, vec3 ro, vec3 rd, vec3 lightpos, out vec3 n, vec2 uv){
    vec2 e = vec2(0.01, 0.0);
    vec2 nd = map(p);

    n = nd.x - vec3(map(p - e.xyy).x ,
                        map(p- e.yxy).x ,
                        map(p- e.yyx).x );

    n = normalize(n);



    vec3 l = normalize(lightpos-p);

    float occ = calcAO(p, n); 
    float dif = clamp(dot(n, l), 0.0, 1.0);
    dif += occ;



    vec3 op = p;
    rd = reflect(rd, n);
    vec3 sh = RM(ro+n*0.003, rd, 0.0);
    p = ro + rd * sh.x;

    ro += rd*sh.x;
    nd = map(ro);

    n = nd.x - vec3(map(ro - e.xyy).x ,
                        map(ro- e.yxy).x ,
                        map(ro- e.yyx).x );

    n = normalize(n);
    float occ2 = calcAO(p, n); 
    float dif2 = clamp(dot(n, l), 0.0, 1.0);
    dif2 += occ2;

    float spec = pow(max( dot( reflect(-l, n), -rd ), 0.), 8.);

    vec3 col = vec3(0.0);



    if(sh.z == 1.0){
         vec3 c = vec3(5.0,4.0,5.0);
        //vec2 id = floor((op.xz+0.5*c.xz)/c.xz-0.5*c.xz);
        vec2 id = floor(mod(p.xz+0.5*c.xz,c.xz)-0.5*c.xz);
        float m = 0.0;


        for(int y = -1;y <=1;y++){
            for(int x =-1;x<=1;x++){

                m += N21(id-vec2(x, y));
            }

        }
        m = m/9.0;

        float cn = m;
        vec3 cl;
        if(cn <0.25)cl = vec3(0.50, 0.3, 0.2);
        else if(cn <0.5)cl = vec3(0.3, 0.5, 0.2);
        else if(cn <0.75)cl = vec3(0.3, 0.2, 0.5);
        else if(cn <=1.0)cl = vec3(1.0, 0.5, 0.0);
        //col = mix(col ,  vec3(dif2) * cl, 0.7);
        col = vec3(dif2) * cl*2.0 ;
        col += spec*100.;
        return col;

    }

    else
        return vec3(dif);


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv /= vec2(iResolution.y/iResolution.x, 1.0);

    // Time varying pixel color
    vec3 col = vec3(0.0);


    vec3 p;
    vec3 ro = vec3(-2.0, -0.5, -7.0); 
    vec3 lookat = vec3((iMouse.x/iResolution.x)*10.0, (iMouse.y/iResolution.y)*30.0, 30.0);
    //vec3 rd = normalize(vec3(0.0, 1.0, 0.0));
    vec3 lightpos = vec3(0.0, 5.0, -1.0);

    vec3 dirc = vec3(0.0, 2.0, 0.0);

    float an = (iMouse.x/iResolution.x)*10.0;
    float anx = (iMouse.y/iResolution.y)*10.0;
    ro -= dirc;
    lookat -= dirc;
    ro *= RotZ(anx);
    lookat *= RotZ(anx);
    ro *= RotX(anx);
    lookat *= RotX(anx);
    ro *= RotY(an);
    lookat *= RotY(an);
    ro += dirc;
    lookat += dirc;

    lightpos -= dirc;
    lightpos *= RotZ(anx);
    lightpos *= RotX(anx);
    lightpos *= RotY(an);
    lightpos += dirc;

    lightpos.z += iTime*5.0;
    dirc.z += iTime*5.0;
    ro.z += iTime*5.0;
    lookat.z += iTime*5.0;
    lookat -= dirc;
    lookat *= RotY(iTime/5.0);
    lookat += dirc;


    float zoom = 1.0;    
    vec3 f = normalize(lookat-ro),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f, r),
        c = ro + f * zoom,
        i = c + uv.x * r + uv.y * u,
        rd = normalize(i-ro);

    float dan = 0.0;
    vec3 d = RM(ro, rd, dan);

    vec4 rc;
    if(d.x > 0.0){
        p = ro + d.x * rd;

        vec3 n;
        vec3 dif = getLight(p, ro, rd, lightpos, n, uv);

        if(d.z == 1.0){
            vec3 c = vec3(5.0,4.0,5.0);
            //vec2 id = floor((op.xz+0.5*c.xz)/c.xz-0.5*c.xz);
            vec2 id = floor(mod(p.xz+0.5*c.xz,c.xz)-0.5*c.xz);
            float m = 0.0;


            for(int y = -1;y <=1;y++){
                for(int x =-1;x<=1;x++){

                    m += N21(id-vec2(x, y));
                }

            }
            m = m/9.0;

            float cn = m;
            vec3 cl;
            if(cn <0.25)cl = vec3(0.0, 1.0, 0.0);
            else if(cn <0.5)cl = vec3(1.0, 0.5, 0.0);
            else if(cn <0.75)cl = vec3(1.0, 0.5, 1.0);
            else if(cn <=1.0)cl = vec3(0.0, 0.5, 1.0);
            col = dif * cl;



        }
        else if(d.z == 2.0){
            col = dif * vec3(1.0, 1.0, 0.1);


        }

    }                 




    // Output to screen
    fragColor = vec4(col,1.0);
}

float hash(float n){
    return fract(sin(n)*43758.5453);
}
float noise(in vec3 pos)
{
    vec3 p = floor(pos);
    vec3 k = fract(pos);
    k = k*k*(3.0f-2.0f*k);
    float n = p.x + p.y * 57.0 + p.z * 113.0; //seed

    float a = hash(n);
    float b = hash(n+1.0);
    float c = hash(n+57.0);
    float d = hash(n+58.0);

    float e = hash(n+113.0);
    float f = hash(n+114.0);
    float g = hash(n+170.0);
    float h = hash(n+171.0);

    float res = mix(mix(mix(a,b,k.x),mix(c,d,k.x),k.y),
                    mix(mix(e,f,k.x),mix(g,h,k.x),k.y),
                    k.z);
    return res;
}
float fbm(in vec3 pos)
{
    vec3 p = pos;
    float f = 0.5000*noise(p); p*=2.02;
    f += 0.2500*noise(p); p*=2.03;
    f += 0.1250*noise(p); p*=2.01;
    f += 0.0625*noise(p);
    return f/0.9375;
}
vec3 tableMaterial(in vec3 pos, in vec3 norm)
{
    vec3 col = vec3(0.8,0.7,0.2);
    float f = fbm(pos*vec3(6.0,0.0,0.5));
    col = mix(col,vec3(0.3,0.2,0.1),f);
    f = smoothstep( 0.6, 1.0, fbm(pos*48.0));
    col = mix( col, vec3(0.2,0.2,0.15), f);

    float ao = 0.2 + 0.8*smoothstep( 0.4,2.0, length( pos.xz));
    col *= ao;
    return col;
}
vec3 fruitMaterial(in vec3 pos, in vec3 norm)
{

    float a = atan( pos.x, pos.y);
    float r = length(pos.xz);
    vec3 col = vec3(1.0,0.2,0.02);

    //col += vec3(0.0,0.50,0.0);//*fbm(pos);

    //Make it green
    float f = smoothstep(0.2,1.0f,fbm((pos)));
    col = mix(col, vec3(0.8,1.0,0.2),f);

    //Make it dirty
    f = smoothstep(0.0,1.0,fbm(pos*4.0));
    col *= 0.7 + 0.3*f;

    //Freckles
    f = smoothstep(0.7,0.9,fbm(pos*48.0));
    col = mix(col,vec3(0.9,1.0,0.6),f);

    // stripes
    f = fbm( vec3(a*7.0, pos.y, pos.z));
    f *= smoothstep( 0.4, 1.2, pos.y + 0.5 * ( noise(pos.yxz)-0.5));
    col = mix(col, vec3(0.4,0.2,0.0),f);

    // top cap
    f = smoothstep( 0.1, 0.2, r);
    col = mix( col, vec3(0.6,0.6,0.5),1.-f);
    //Fake AO
    float ao = 0.55 + 0.45*norm.y;
    col *= ao;
    return col;
}
vec2 fruitShape(in vec3 pos)
{
    float f = pow(dot(pos.xz,pos.xz),0.2);
    pos.y -= 0.5*f;
    vec2 d = vec2(length(pos-vec3(0.0,0.0,0.0))-1.0 ,1.0);
    return d;
}
vec2 tableShape(in vec3 pos)
{
    vec2 d = vec2(length(pos.y + 0.5),2.0);
    return d;
}
vec2 map(in vec3 pos)
{
    vec2 d1 = fruitShape(pos);
    vec2 d2 = tableShape(pos);
    if(d2.x < d1.x)
    {
        d1 = d2;
    }
    return d1;
}
float softShadows(in vec3 ro, in vec3 rd)
{
  float res = 1.0;
  for(float t = 0.1; t < 8.0f;)
  {
      float h = map( ro + rd*t).x;
      if(h < 0.01) return 0.0f;
      res = min(res,6.0f*h/t);
      t += h;
  }
  res = smoothstep(0.0,1.0,res);
  //res = sqrt(res);
  return res;
}
vec2 intersect(in vec3 ro, in vec3 rd)
{
    for( float t = 0.0; t < 8.0;){
        vec2 h = map( ro + rd*t);
        if(h.x < 0.0001) return vec2(t,h.y);
        t += h.x;
    }
    return vec2(0.);
}
vec3 calcNorm(in vec3 pos)
{
    vec3 e = vec3(0.0001,0.0,0.0);
    vec3 n;
    n.x = map(pos+e.xyy).x - map(pos - e.xyy).x;
    n.y = map(pos+e.yxy).x - map(pos - e.yxy).x;
    n.z = map(pos+e.yyx).x - map(pos - e.yyx).x;

    return normalize(n);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = vec2(-1.0+2.0*uv)*vec2(iResolution.x/iResolution.y,1.0);
 //   vec3 ro = vec3(0.0,0.00,2.40);
   vec3 ro = 2.0*vec3(cos(iTime), 0.0, sin(iTime))+vec3(0,2.,0.0);
   //vec3 rd = vec3( vec2(-1.0 +2.0*uv),-1.0);
   //vec3(0.0,0.00,-3.50));
    vec3 ww = normalize(-ro);
    vec3 uu = normalize(cross(vec3(0.,1.,0.),ww));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( uu* q.x + vv * q.y + 1.5* ww);
    vec2 t = intersect(ro, rd);
    vec3 col = vec3(0.80);
    if(t.y > 0.5)
    {
        vec3 pos = ro + t.x*rd;
        vec3 norm = calcNorm(pos);
        vec3 lig = normalize(vec3(1.0,0.80,0.60));
        vec3 blig = vec3( -lig.x, lig.y, -lig.z);
        float diff = max(dot(lig,norm),0.0);
        float bac = max( 0.0, 0.2+ 0.8*dot(norm,blig));
        vec3 ref = normalize(reflect(rd,norm));
        float spe = pow(clamp(dot(ref,lig),0.0,1.0),32.0);
        float rim = pow(1.0 + dot(norm,rd),2.5);
        float con = 1.0;
        float amb = 0.5+0.5*norm.y;
        float sha = softShadows(pos,lig);

        //col = vec3(1.0,1.0,1.0);
        col = con*vec3(0.1,0.15,0.2);
        col += amb*vec3(0.1,0.15,0.2);
        col += diff*vec3(1.0,0.97,0.85)*sha;
        col += bac*vec3(1.0,0.97,0.85);
        col = col*0.3 + 0.7*sqrt(col);
        col *= 0.5;
        if(t.y > 1.5)
        {
            col *= tableMaterial(pos,norm);
        }
        else
        {  
            col *= fruitMaterial(pos,norm);
        }
        col += 0.3*rim*amb;
        col += 0.7*spe*sha*amb;
    }
    col *= 0.2 + 0.8*pow(uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y),0.10);
    // Output to screen
    fragColor = vec4(col,1.0);
}

const float PI = 3.14159265;
const float EPS = 0.001;
const float INF = 5.;

const vec3 top_color = vec3(.2, .6, 1.);
const vec3 horizon_color = vec3(.9);

const vec3 water_bottom_color = vec3(0., .2, .5);
const vec3 water_horizon_color = vec3(0., 0., 1.);

vec3 sky_color(vec3 rd) {
  float t = 0.5 * (rd.y + 1.0);
  return t * top_color + (1.0 - t) * horizon_color;
}

vec3 water_color(vec3 rd) {
  float t = 0.5 * (-rd.y + 1.0);
  return t * water_bottom_color + (1.0 - t) * water_horizon_color;
}

vec4 plane = vec4(0.0, 1.0, 0.0, 2.0);

float distPlane(vec3 p, vec4 n) {
  return dot(p, n.xyz) * n.w;
}

float distSphere(vec3 p, vec3 c, float r) {
  return length(p - c) - r;
}

float distFunc(vec3 p) {
  float r = .2;
  float theta = iTime * PI * 6.0 / 3.;
  vec3 c = vec3(0., r + (cos(theta + 0.1) - 0.5) * .01, 0.);

  float d1 = distSphere(p, c, r);
  float d2 = distPlane(p, plane);
  float dist = length(p.xz);
  d2 += sin(dist * PI * 10.0 - theta) * 0.003 / max(dist * dist * 3., 1.);// * max(1. / dist, 1.);
  return min(d1, d2);
  return d2;
}

const vec3 dx = vec3(EPS, 0, 0);
const vec3 dy = vec3(0, EPS, 0);
const vec3 dz = vec3(0, 0, EPS);

vec3 normal(in vec3 p) {
  return normalize(vec3(
  	distFunc(p + dx) - distFunc(p - dx),
  	distFunc(p + dy) - distFunc(p - dy),
  	distFunc(p + dz) - distFunc(p - dz)
  ));
}

float schlick(const float cosine, const float ref_idx) {
    float r0 = (1. - ref_idx) / (1. + ref_idx);
    r0 = r0 * r0;
    return r0 + (1. - r0) * pow((1. - cosine), 5.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 r = iResolution.xy;
  vec2 p = (fragCoord.xy * 2. - r) / min(r.x,r.y);
  vec3 ta = vec3(p, 0.);
  vec3 ro0 = vec3(0., 0.1, -1.);
  vec3 rd = normalize(ta - ro0);

  float t = 0.;
  vec3 albedo = vec3(1.);
  vec3 sphere_color = vec3(0.99, 0.99, 0.);
  float ray_rate = 1.;
  vec3 col = vec3(0.);
  for (int ns = 0; ns < 96; ++ns) {
    vec3 ro = ro0 + rd * t;
    float d = distFunc(ro);
    if (length(ro) > INF) break;
    if (d < EPS) {
      vec3 nv = normal(ro);

      vec3 reflected = reflect(rd, nv);
      if (ro.y > 0.01) {
        if (sphere_color.x > 1.0 || sphere_color.y > 1. || sphere_color.z > 1.0) {
          col += ray_rate * albedo * sphere_color;
          fragColor = vec4(sqrt(col), 1.);
          return;
        }
        t = max(d, EPS * 1.5);
        rd = reflected;
        ro0 = ro;
        albedo *= sphere_color;
        continue;
      }

      float ref_idx = 1.3330; // water
      float etai_over_etat = 1.0 / ref_idx; // front face
      float cos_theta = min(dot(-rd, nv), 1.0);
      float sin_theta = sqrt(1.0 - cos_theta*cos_theta);

      if (etai_over_etat * sin_theta > 1.0) {
        rd = reflected;
      } else {
        float rate = schlick(cos_theta, etai_over_etat);
        col += ray_rate * (1. - rate) * water_color(refract(rd, nv, etai_over_etat));
        ray_rate *= rate;
        rd = reflected;
      }
      t = max(d, EPS * 1.5);
      ro0 = ro;
    }
    t += d * .5;
  }

  col += ray_rate * albedo * sky_color(rd);

  fragColor = vec4(sqrt(col), 1.);
}

/*
 * Similar to my first two shaders of a similar name, but this one
 * effectively ray marches the perimeter of the voxel using circles
 * to check if the sdf lies inside the voxel to a given degree of accuracy (the size of a pixel).
 *
 * It also does the checks in a slightly larger area than the voxel to catch edge cases where an sdf resolves to
 * less than minstep size between two voxels.
 */

#define gridSize1 5.0
#define gridSize2 10.0
#define gridSize3 20.0
#define gridSize4 40.0

float cornerRadiusMultiplier2d = 3.-2./sqrt(2.)-sqrt(2.); // 0.17157287525
float cornerRadiusMultiplier3d = 3./16.; // 0.1875

float sdBox( vec2 p, in vec2 bounds )
{
    vec2 d = abs(p)-bounds;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdCircle(vec2 p, float radius) {
    return length(p) - radius;
}

float sdArc( in vec2 p, in vec2 sca, in vec2 scb, in float ra, float rb )
{
    p *= mat2(sca.x,sca.y,-sca.y,sca.x);
    p.x = abs(p.x);
    float k = (scb.y*p.x>scb.x*p.y) ? dot(p.xy,scb) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

vec2 pixelSize() {
    return 2.0 * ((vec2(iResolution.x, iResolution.y) - (0.5*iResolution.xy))/iResolution.y) / iResolution.xy;
}

float sdf(vec2 uv) {

    vec2 pixelSize = pixelSize();

    float screenEdgeMargin = 0.5;

    float radius1 = 0.25 * (1.0 + sin(iTime * 0.6));
    vec2 position1 = vec2(sin(iTime * 0.2) * (iResolution.x / iResolution.y) * (1. - screenEdgeMargin), sin(iTime * 0.10) * (1. - screenEdgeMargin));
    float circle1 = sdCircle(uv - position1, radius1);   

    // 1/10th the size of a pixel circles
    float radius3 = 0.1 * min(pixelSize.x, pixelSize.y); 
    vec2 position3 = vec2(sin(iTime * 0.6) * (iResolution.x / iResolution.y) * (1. - screenEdgeMargin), sin(iTime * 0.6) * (1. - screenEdgeMargin));
    float circle3 = sdCircle(uv - position3, radius3);

    // animation
    float ta = 3.14*(0.5+0.5*cos(iTime*0.52+2.0));
    float tb = 3.14*(0.5+0.5*cos(iTime*0.31+2.0));
    float rb = 0.15*(0.5+0.5*cos(iTime*0.41+3.0));
    float size = 1.;

    // distance
    // vec2 arcPosition1 = vec2(sin(iTime * 0.25) * (iResolution.x / iResolution.y) * (1. - screenEdgeMargin), sin(iTime * 0.25) * (1. - screenEdgeMargin));
    vec2 arcPosition1 = vec2(0.,0.);
    // vec2 arcPosition1 = vec2(0.542,0.543);
    float arc1 = sdArc(uv - arcPosition1,vec2(sin(ta),cos(ta)),vec2(sin(tb),cos(tb)), size, rb);

    return min(min(circle1, circle3), arc1);
}

bool insideBoundingSquare(vec2 position, vec2 bound) {

    vec2 pixelSize = pixelSize();

    // We'll check just outside of the square to catch cases where the sdf resolves in sub-pixel level between
    // two squares. This means we potentially treat a square as inside/touching when it isn't but better than
    // artefacts due to this scenario.
    vec2 adjustedBound = bound + pixelSize;

    // If all points inside the grid's outer circle are negative, then we're definitely inside.
    float midpointD = sdf(position);
    if (midpointD <= -length(adjustedBound)) {
        return true;
    } else if (abs(midpointD) < min(adjustedBound.x, adjustedBound.y)) {
        // There is an sdf edge inside the inner circle, so must not be completely inside.
        return false;
    }

    // For each corner,
    for (float x = 0.; x < 2.; x++) {
        for (float y = 0.; y < 2.; y++) {
            vec2 cornerDirection = normalize(vec2(-1.0 + 2. * x, -1.0 + 2.0 * y)); // -1.0, -1.0 to 1.0, 1.0
            float r = adjustedBound.x; // Set radius of to radius of inner circle of square.
            float distFromMidpoint = r; // Total distance from midpoint to corner circle midpoint.

            // Keep shrinking the corner circle until we reach pixel size (or return a value inside).
            while (r >= min(pixelSize.x, pixelSize.y)) {

                // Calculate size of inscribed circle in corner.
                r = cornerRadiusMultiplier2d * r;
                distFromMidpoint += r; // Move to midpoint of new circle
                vec2 cornerCircleMidpoint = position + cornerDirection * distFromMidpoint;
                distFromMidpoint += r; // Move to edge of circle for next circle

                // We march in a clock-wise direction around the perimeter.
                float posOrNeg = 1. - x*2.; // 1 or -1
                float applyX = abs(x-y); // 1 or 0
                float applyY = abs(applyX - 1.); // 1 or 0
                vec2 edgeDirection = vec2(applyX * posOrNeg, applyY * posOrNeg);

                // March along the edge.
                for (float i = r; i < adjustedBound.x - r; i = i + r) {
                    vec2 midpoint = cornerCircleMidpoint + i * edgeDirection;
                    if (sdf(midpoint) > r) {
                        return false;
                    }
                }
            }
        }
    }


    return true;
}

bool touchingBoundingSquare(vec2 position, vec2 bound) {

    vec2 pixelSize = pixelSize();

    // We'll check just outside of the square to catch cases where the sdf resolves in sub-pixel level between
    // two squares. This means we potentially treat a square as inside/touching when it isn't but better than
    // artefacts due to this scenario.
    vec2 adjustedBound = bound + pixelSize;

    // If all points inside the grid's outer circle are outside,
    // then we're definitely outside and not touching.
    float midpointD = sdf(position);
    if (midpointD > length(adjustedBound)) {
        // Doesn't touch outer circle, so must be completely outside sdf volume.
        return false;
    } else if (midpointD < min(adjustedBound.x, adjustedBound.y)) {
        // Touches inner circle so must be inside square.
        return true;
    }

    // For each corner,
    for (float x = 0.; x < 2.; x++) {
        for (float y = 0.; y < 2.; y++) {
            vec2 cornerDirection = normalize(vec2(-1.0 + 2. * x, -1.0 + 2.0 * y)); // -1.0, -1.0 to 1.0, 1.0
            float r = adjustedBound.x; // Set radius of to radius of inner circle of square.
            float distFromMidpoint = r; // Total distance from midpoint to corner circle midpoint.

            // Keep going until we reach pixel size or exit condition.
            while (r >= min(pixelSize.x, pixelSize.y)) {

                // Calculate size of inscribed circle in corner.
                r = cornerRadiusMultiplier2d * r;
                distFromMidpoint += r; // Move to midpoint of new circle
                vec2 cornerCircleMidpoint = position + cornerDirection * distFromMidpoint;
                distFromMidpoint += r; // Move to edge of circle for next circle
                float posOrNeg = 1. - x*2.; // 1 or -1
                float applyX = abs(x-y); // 1 or 0
                float applyY = abs(applyX - 1.); // 1 or 0
                vec2 edgeDirection = vec2(applyX * posOrNeg, applyY * posOrNeg);
                // March along the edge.
                for (float i = r; i < adjustedBound.x - r; i = i + r) {
                    vec2 midpoint = cornerCircleMidpoint + i * edgeDirection;
                    if (sdf(midpoint) <= r) {
                        return true;
                    }
                }
            }
        }
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelSize = pixelSize();

    // Normalized pixel coordinates (from y = -1 to 1 with 0,0 in the center of the screen)
    vec2 uv = 2.0 * (fragCoord - (0.5*iResolution.xy))/iResolution.y;

    vec2 minScreenExtent = 2.0 * (vec2(0,0) - (0.5*iResolution.xy))/iResolution.y;
    vec2 maxScreenExtent = 2.0 * (iResolution.xy - (0.5*iResolution.xy))/iResolution.y;

    // Calculate SDF
    float d = sdf(uv);

    float gridSize;
    if (uv.x > minScreenExtent.x + 3. * (maxScreenExtent.x - minScreenExtent.x) / 4.) {
        gridSize = gridSize4;
    } else if (uv.x > minScreenExtent.x + 2. * (maxScreenExtent.x - minScreenExtent.x) / 4.) {
        gridSize = gridSize3;
    } else if (uv.x > minScreenExtent.x + 1. * (maxScreenExtent.x - minScreenExtent.x) / 4.) {
        gridSize = gridSize2;
    } else {
        gridSize = gridSize1;
    }

    // Bounding Box
    vec2 voxelMidpoint = vec2((floor(uv.x * gridSize) / gridSize) + (0.5 / gridSize), (floor(uv.y * gridSize) / gridSize) + (0.5 / gridSize));
    vec2 voxelSize = vec2(0.5 / gridSize, 0.5 / gridSize);
    vec2 minExtent = voxelMidpoint - voxelSize;
    vec2 maxExtent = voxelMidpoint + voxelSize;
    float box1 = sdBox(voxelMidpoint - uv, voxelSize);

    // Work out if the SDF is inside the box or not.
    bool touching = touchingBoundingSquare(voxelMidpoint, voxelSize);
    bool inside = false;
    if (touching) {
        inside = insideBoundingSquare(voxelMidpoint, voxelSize);
    }

    bool outside = !touching;

    vec3 statusColor = vec3(1.0, 1.0, 0.0);
    if (inside) {
        statusColor = vec3(0.0, 1.0, 0.0);
    } else if (outside) {
        statusColor = vec3(1.0, 0.0, 0.0);
    }

    // Set base colour depending on inside or outside bounding box.
    vec3 baseColor = vec3(0.1,0.4,0.7);

    // Taken from iq to visualize sdf distances (https://www.shadertoy.com/view/4lcBWn)    
    vec3 col = vec3(1.0) - sign(d)*baseColor;
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.008,abs(d)));

    // Add the grid
    col = mix( col, statusColor, 1.0 - smoothstep(0.0,0.008,abs(box1)) );

    // Output to screen
    fragColor = vec4(col,1.0);

}

// Credits to iquilezles fir SDF functions, https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
// Creduts to Marius Horga for his Raymarching write-up, http://metalkit.org/2016/12/30/raymarching-in-metal.html

struct Ray {
    vec3 origin;
    vec3 direction;
};

struct BoundingBox {
    vec3 center;
    vec3 bounds;
    float edge;
};

float distToBoundingBox(Ray ray, BoundingBox bb) {
    vec3 p = abs(ray.origin - bb.center) - bb.bounds;
    vec3 q = abs(p + bb.edge) - bb.edge;
    return min(min(
             length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
             length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
             length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0)
    );
}

float distToBoundingBoxScene(Ray r) {
    BoundingBox bb = BoundingBox(vec3(1.0), vec3(1.0), 0.05);
    Ray repeatRay = r;
    repeatRay.origin = mod(r.origin, 2.0);
    return distToBoundingBox(repeatRay, bb);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 camPos = vec3(1000.0 + (sin(iTime) / 2.0) + 1.0, 1000.0 + (cos(iTime) / 2.0) + 1.0, iTime);
    Ray ray = Ray(camPos, normalize(vec3(uv, 1.0)));
    vec3 col = vec3(0.0);
    for (float i=0.0; i<100.0; i++) {
        float dist = distToBoundingBoxScene(ray);
        if (dist < 0.01) {
              col = vec3(1.0);
              break;
        }
        ray.origin += ray.direction * dist;
    }
    vec3 posRelativeToCamera = ray.origin - camPos;
    fragColor = vec4(1.0) - vec4(col * abs(posRelativeToCamera / 5.0), 0.0);
}

#define PI 3.14159265359
#define PHI 1.618033988749895

// HG_SDF
void pR(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}
float vmax(vec3 v) {
	return max(max(v.x, v.y), v.z);
}
float fBox(vec3 p, vec3 b) {
	vec3 d = abs(p) - b;
	return length(max(d, vec3(0))) + vmax(min(d, vec3(0)));
}

// Rotate on axis, blackle
vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax,p)*ax, p, cos(ro))+sin(ro)*cross(ax,p);
}

// Sign but returns -1 instead of 0
vec3 boolSign(vec3 v) {
    return max(vec3(0), sign(v)) * 2. - 1.;
}

// Closest icosahedron vertex
vec3 icosahedronVertex(vec3 p) {
    vec3 ap, v, v2, v3;
    ap = abs(p);
    v = vec3(PHI, 1, 0);
    v2 = v.yzx;
    v3 = v2.yzx;
    if (distance(ap, v2) < distance(ap, v)) v = v2;
    if (distance(ap, v3) < distance(ap, v)) v = v3;
    return normalize(v) * boolSign(p);
}

// Closest dodecahedron vertex
vec3 dodecahedronVertex(vec3 p) {
    vec3 ap, v, v2, v3, v4;
    ap = abs(p);
    v = vec3(PHI);
	v2 = vec3(0, 1, PHI + 1.);
	v3 = v2.yzx;
    v4 = v3.yzx;
    if (distance(ap, v2) < distance(ap, v)) v = v2;
    if (distance(ap, v3) < distance(ap, v)) v = v3;
    if (distance(ap, v4) < distance(ap, v)) v = v4;
    return normalize(v) * boolSign(p);
}

// Second closest dodecahedron vertex
vec3 secondDodecahedronVertex(vec3 p, vec3 iv, vec3 dv) {
    // Find which side of the icosahedron vertex -> dodecahedron vertex line we're on
    float side = sign(dot(p, cross(iv, dv)));
    // Rotate dodecahedron vertex around the dodecahedron face
    return erot(dv, iv, PI * 2. / 5. * side);
}

// The model to explode
float object(vec3 p) {
    pR(p.xz, 1.2);
    pR(p.xy, .3);
    float d = fBox(p, vec3(.25 - .02)) - .02;
    d = max(d, -d - .05);
    return d;
}

// https://www.iquilezles.org/www/articles/functions/functions.htm
float expImpulse( float x, float k )
{
    float h = k*x;
    return h*exp(1.0-h);
}

float map2(vec3 p) {

    // Three closest vertices of a spherical pentakis dodecahedron
    // or, three closest faces of a buckyball
    vec3 a = icosahedronVertex(p);
    vec3 b = dodecahedronVertex(p);
    vec3 c = secondDodecahedronVertex(p, a, b);

    float d = 1e12;

    vec3 pp = p;

    // Render the nearest three fragments to get
    // a clean distance estimation

    for (int i = 0; i < 3; i++) {

        // Offset space
        float t = mod((iTime - dot(a.xy, vec2(1,-1)) / 6.) / 3., 1.);
        float t2 = min(t * 1.85, 1.);
        float explode = 1. - pow(1. - t2, 10.); // expand
        explode *= 1. - pow(t2, 5.); // contract
        t2 = max(t - .53, 0.) * 1.2;
        float wobble = sin(expImpulse(t2, 20.) * 2.2 + pow(3. * t2, 1.5) * 2. * PI * 2. - PI) * smoothstep(.4, .0, t2) * .15;
        float anim = wobble + explode;
        p -= a * anim / 2.8;

        // Build boundary edge of face
        float edgeA = dot(p, normalize(b - a));
        float edgeB = dot(p, normalize(c - a));
        float edge = max(edgeA, edgeB);

        // Intersect with object
        d = min(d, max(object(p), edge));

        // Reset space for next iteration
        p = pp;

        // Cycle faces for next iteration
        vec3 aa = a;
        a = b;
        b = c;
        c = aa;
    }

    return d;
}


float map(vec3 p) {
    if (iMouse.x > 0. && iMouse.y > 0.) {
        pR(p.yz, (.5 - iMouse.y / iResolution.y) * PI / 2.);
        pR(p.xz, (.5 - iMouse.x / iResolution.x) * PI * 2.);
    }
    return map2(p);
}

// compile speed optim from IQ https://www.shadertoy.com/view/Xds3zN
vec3 calcNormal(vec3 pos){
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.0005*e);
    }
    return normalize(n);
}

// https://www.shadertoy.com/view/Xds3zN
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor );
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2. * fragCoord.xy) / iResolution.y;

    vec3 camPos = vec3(0,0,3.2);
    vec3 rayDirection = normalize(vec3(p,-4));

    vec3 rayPosition = camPos;
    float rayLength = 0.;
    float dist = 0.;
    bool bg = false;
    vec3 bgcol = vec3(.007);
    vec3 col = bgcol;

    for (int i = 0; i < 150; i++) {
        rayLength += dist;
        rayPosition = camPos + rayDirection * rayLength;
        dist = map(rayPosition);

        if (abs(dist) < .001) {
        	break;
        }

        if (rayLength > 5.) {
            bg = true;
            break;
        }
    }

    if ( ! bg) {
        vec3 albedo = vec3(1);
        vec3 n = calcNormal(rayPosition);
        vec3 lp = vec3(-.5,.5,.5);
        float l = max(dot(lp, n), 0.);
        vec3 ld = normalize(lp - rayPosition);
        l += .02;
        l += pow(max(0., 1. + dot(n, rayDirection)), 3.) * .05;
        float ao = calcAO(rayPosition, n);
        col = albedo * l * mix(1., ao, .8);
        col = mix(col, bgcol, 1.0 - exp2(-0.2 * pow(rayLength - 1., 3.)));
    }

    col = pow(col, vec3(1./2.2));

    fragColor = vec4(col,1);
}

mat2 rotate(float r) {
    float c = cos(r);
    float s = sin(r);
    return mat2(c, -s, s, c);
}
float circle (vec2 uv, float r, float blur){
    float d = length(uv);
    return smoothstep(r,r-blur,d);
}
float DrawSquare (vec2 uv, float width, float height, float posX, float posY, float blur){
    float w = length((uv.x + posX));
    float h = length(uv.y + posY);
    float col = smoothstep(width,width-blur, w);
    col *= smoothstep(height,height-blur, h);
    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x *= iResolution.x/iResolution.y;

    //ray origin
    vec3 ro = vec3(0.0, 0.0, -3.0);

    //ray direction
	vec3 rd = vec3(uv.x,uv.y, 0)-ro;

    // Time varying pixel color
    float width = 0.3;
    float height = 0.3;
    float blur = 0.01;
    float posX = 0.;
    float posY = -0.;

    float head = DrawSquare(uv, width, height, posX, posY, blur);
    head += DrawSquare(uv, width * 0.5, height * 0.15, posX, posY + 0.335, blur);
    head += DrawSquare(uv, width * 2., height, posX, posY + 0.67, blur);
    vec3 img = vec3(.8) * head;
    //outer eyes
    float Reye = DrawSquare(uv, width * 0.2, height * (sin(iTime)*0.05 +0.15), posX + 0.15, posY-0.1, blur);

    img -= vec3(1.,1.,1.) * Reye;

    float Leye = DrawSquare(uv, width * 0.2, height * 0.2, posX - 0.15, posY-0.1, blur);

    img -= vec3(1.,1.,1.) * Leye;

    //inner eyes
    float RIeye = DrawSquare(uv, width * 0.1, height * 0.1, posX + 0.15, posY-0.1, blur);

    img += vec3(smoothstep(1.,0.9,sin(iTime)),0.,0.) * RIeye;

    float LIeye = DrawSquare(uv, width * 0.1, height * 0.1, posX - 0.15, posY-0.1, blur);

    img += vec3(1.,smoothstep(smoothstep(1.,0.9,cos(iTime*5.)),0.9,sin(iTime*5.)),smoothstep(1.,0.9,sin(iTime*5.))) * LIeye;

    //mouth
    float mouth = DrawSquare(uv, width * 0.5, height * 0.2, posX, posY+0.15, blur);
    img -= vec3(1.,1.,1.) * mouth;

    //mouth inner
    float mouthI = DrawSquare(uv, width * 0.48, height * (sin(iTime*3.)*0.04+0.14), posX, posY+0.15, blur);
    img += vec3(0.,1.,1.) * mouthI;

    //mouth slats
    float mouthS = DrawSquare(uv, width * 0.05, height * 0.18, posX, posY+0.15, blur);
    mouthS += DrawSquare(uv, width * 0.05, height * 0.18, posX-.05, posY+0.15, blur);
    mouthS += DrawSquare(uv, width * 0.05, height * 0.18, posX-.1, posY+0.15, blur);
    mouthS += DrawSquare(uv, width * 0.05, height * 0.18, posX+.05, posY+0.15, blur);
    mouthS += DrawSquare(uv, width * 0.05, height * 0.18, posX+.1, posY+0.15, blur);
    img -= vec3(0.,1.,1.) * mouthS;

    //nose
    vec2 rotUV = uv * rotate(iTime);
    float nose = circle (rotUV, 0.03, blur);
    nose += DrawSquare(rotUV, width * 0.03, height * 0.09, posX, posY, blur);
    nose += DrawSquare(rotUV, width * 0.09, height * 0.03, posX, posY, blur);
    img -= vec3(.5,.5,.5) * nose;

    //screws
    float screws = circle (uv - vec2(0.25), 0.03, blur);
    screws += DrawSquare(uv, width * 0.03, height * 0.09, posX - 0.25, posY - 0.25, blur);
    screws += DrawSquare(uv, width * 0.09, height * 0.03, posX - 0.25, posY - 0.25, blur);

    screws += circle (uv - vec2(-0.25), 0.03, blur);
    screws += DrawSquare(uv, width * 0.03, height * 0.09, posX + 0.25, posY + 0.25, blur);
    screws += DrawSquare(uv, width * 0.09, height * 0.03, posX + 0.25, posY + 0.25, blur);

    screws += circle (uv - vec2(-0.25, 0.25), 0.03, blur);
    screws += DrawSquare(uv, width * 0.03, height * 0.09, posX + 0.25, posY - 0.25, blur);
    screws += DrawSquare(uv, width * 0.09, height * 0.03, posX + 0.25, posY - 0.25, blur);

    screws += circle (uv - vec2(0.25, -0.25), 0.03, blur);
    screws += DrawSquare(uv, width * 0.03, height * 0.09, posX - 0.25, posY + 0.25, blur);
    screws += DrawSquare(uv, width * 0.09, height * 0.03, posX - 0.25, posY + 0.25, blur);
    img -= vec3(.5,.5,.5) * screws;
    // Output to screen
    fragColor = vec4(img,1.0);
}

#define spheres_num 3
struct Camera {
    vec3 Observer;
    vec3 View;
    vec3 Up;
    vec3 Horiz;
    float screenHeight;
    float screenWidth;
    float screen_observer_distance;
};
vec3 EPSILON = vec3(1e-9);
struct Ray {
    vec3 Source;
    vec3 Direction;
};
struct Sphere {
    vec3 Center;
    float radius;
    vec3 Color;
    bool doesReflect;
};
struct IntersectionInfo {
    Sphere sphere;
    vec3 IntersectionPoint;
    bool doesIntersectASphere;
    float tVal;
    Ray incidenceRay;
};
Camera createCamera(in vec3 Observer, in vec3 LookAt, in float aperture) {
    Camera camera;
    camera.Observer = Observer;
    camera.View = normalize(LookAt - Observer);
    vec3 kAxis = vec3(0.0,0.0,1.0);
    camera.Horiz = normalize(cross(kAxis,camera.View));

    //no need to normalize up since view and horiz are already normalized;
    camera.Up = cross(camera.View, camera.Horiz);
    camera.screenHeight = iResolution.y;
    camera.screenWidth = iResolution.x;
    camera.screen_observer_distance = (camera.screenHeight / 2.0) / tan((aperture * 3.1415 / 180.0) / 2.0);
    return camera;
}
Ray launchRay (in Camera camera, in vec2 pixCoord) {
    float adjustedX = pixCoord.x - camera.screenWidth / 2.0;
    float adjustedY = pixCoord.y - camera.screenHeight / 2.0;
    return Ray(camera.Observer, camera.screen_observer_distance * camera.View 
    + adjustedX * camera.Horiz 
    + adjustedY * camera.Up);
}

/*
   If bool = true , the intersection will be at : M = RayOrigin + atT * Dir
*/
bool doesIntersectSphere(in Ray ray, in Sphere sphere, out float atT) {
    vec3 SC = ray.Source - sphere.Center;
    float a = dot(ray.Direction, ray.Direction);// R.dir;
    float b = 2.0 * dot(ray.Direction, SC);
    float c = dot(SC, SC) - sphere.radius*sphere.radius;
    float delta = b*b - 4.0*a*c;
    if(delta < 0.0) {
    return false;
    };
    atT = (-b-sqrt(delta)) / (2.0*a);
    return true;
}

IntersectionInfo getIntersectionInfo(in Ray ray, in Sphere[spheres_num] spheres) {
    IntersectionInfo info;
    float t = 1e25;
    info.doesIntersectASphere = false;
    info.incidenceRay = ray;
    for(int i=0; i < spheres_num; i++) {
        Sphere sphere = spheres[i];
        float potentialT = t;
        if(doesIntersectSphere(ray, sphere, potentialT)) {
            info.doesIntersectASphere = true;
            if(potentialT < t) {
                t = potentialT;
                info.sphere = sphere;
                info.IntersectionPoint = ray.Source + potentialT * ray.Direction;
                info.tVal = potentialT;
            }
        }

    }

    return info;
}

float getLightingRatio(in Sphere sphere,in vec3 IntersectionPoint, in vec3 Sun) {
    vec3 NormaleVec = IntersectionPoint - sphere.Center;
    vec3 IncidenceVec = Sun - IntersectionPoint;
    float incidence = max(dot(NormaleVec,IncidenceVec)/(length(NormaleVec)*length(IncidenceVec)), 0.0);
    return incidence;
}


bool applyShadow(Ray shadowRay, Sphere[spheres_num] spheres) {
   bool shadow = false;
   for(int i = 0 ; i < spheres_num;i++) {
       float atT;
       shadow = shadow || (doesIntersectSphere(shadowRay, spheres[i], atT) && atT > 1e-6 && atT < 1.0);
       if(shadow) break;
   }
   return shadow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragColor = vec4(0.5, 0.5, 1.0, 1.0);
    Camera cam =createCamera(vec3(3.0, 3.0, 1.5),
    vec3(0.5, 0.5, 0.5),
    50.0);
    Sphere Sun= Sphere(vec3(0.0,2.5,0.0),0.01,vec3(1.0,1.0,1.0), false);
    Sphere sphereA = Sphere(vec3(0.0, 0.0, 0.0), 0.5, vec3(1.0,0.0,0.0), false);
    float rotation = iTime;
    Sphere sphereB = Sphere(0.9*vec3(cos(rotation), sin(rotation), 0),0.1,vec3(0.0,0.0,1.0), false);
    Sphere sphereC = Sphere(vec3(0.0,0.0,1.2), 0.3, vec3(0.0,1.0,0.0), true);
    Sphere[spheres_num] spheres = Sphere[spheres_num](sphereB, sphereA, sphereC);

    Ray ray = launchRay(cam, fragCoord);
    float t;
    /*if(doesIntersectSphere(ray,S, t)) {
        vec3 IntersectionPoint = ray.Source + t* ray.Direction;
        float incidence = getLightingRatio(S,IntersectionPoint, Sun);
        fragColor = incidence*vec4(1.0);
    };*/
    IntersectionInfo info = getIntersectionInfo(ray, spheres);
    if(info.doesIntersectASphere) {
        vec3 IntersectionPoint = info.IntersectionPoint;
        // Defining a shadow detector ray
        Ray shadowRay;
        shadowRay.Source = IntersectionPoint;
        shadowRay.Direction =  Sun.Center - shadowRay.Source;
        IntersectionInfo shadowInfo = getIntersectionInfo(shadowRay, spheres);
        //SHADOW section
        bool shadow = applyShadow(shadowRay, spheres);
        if (!shadow) {
            // Lighting section
            if(!info.sphere.doesReflect) {
                float incidence = getLightingRatio(info.sphere, IntersectionPoint, Sun.Center);
                fragColor = vec4(incidence * info.sphere.Color,1.0);

            } else {
                Ray incidenceRay = info.incidenceRay;
                vec3 Center = info.sphere.Center;
                vec3 NormaleVec = (IntersectionPoint - Center);
                NormaleVec = normalize(NormaleVec);
                Ray reflectedRay = Ray(info.IntersectionPoint, - (incidenceRay.Direction - (2.0 * dot(incidenceRay.Direction,NormaleVec)*NormaleVec)));
                IntersectionInfo reflectionInfo = getIntersectionInfo(reflectedRay, spheres);
                if(reflectionInfo.doesIntersectASphere) {
                        float incidence = getLightingRatio(reflectionInfo.sphere, reflectionInfo.IntersectionPoint, Sun.Center);
                        fragColor = vec4(incidence* reflectionInfo.sphere.Color,1.0);



                }


            }

            // End of Lighting

        } else {

            fragColor = vec4(0.01*info.sphere.Color, 1.0);
        }

    } else {
        fragColor = vec4(0.5, 0.5, 1.0, 1.0);
    }



}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

 mat3 rotationMatrix(vec3 axis, float angle)
{
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}

float mixV(float a, float b, float d) {
	return (d * a)+(1.0 - d)*b;
}

float map(vec3 p)
{
   float displacement = .5 * sin(iTime + 5.*p.x)*cos(iTime * 1.1 + 5.*p.y)*sin(iTime * 1.2 + 5.*p.z);    
   return displacement + sdBox(p, vec3(5.0, 5.0, 5.0));
}

