    vec3 col = 0.2+0.5*cos(iTime+vec3(2,0,4));

    // Output to screen
    fragColor = 0.7*vec4(col,1.0);

   const float FARAWAY=1e30;
   float t = FARAWAY;

   vec3 P;  // Point courant
   vec3 N;  // Normale
   Material material; // Couleur

   for(int i=0; i<scene.length(); ++i) {
       float cur_t;
       if(
          intersect_sphere(R, scene[i].sphere, cur_t)
          && cur_t < t
       ) {
           t = cur_t;
           P = R.Origin + t*R.Dir;
           N = normalize(P - scene[i].sphere.Center);
           material = scene[i].material;
       }
   }

   if(t != FARAWAY) {
      fragColor = lighting(P,N,material);
   }

}

