const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;

// -------- basic tool function -------- //
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }

// ---- Rotation ---- //
mat3 rot3_X(float theta) {
    return mat3(1, 0,          0,
                0, cos(theta), -sin(theta),
                0, sin(theta), cos(theta));
}

mat3 rot3_Y(float theta) {
    return mat3(cos(theta), 0,  sin(theta),
                0,     1,     0,
                -sin(theta), 0 , cos(theta));
}

mat3 rot3_Z(float theta) {
    return mat3(-sin(theta), cos(theta), 0,
                cos(theta),  sin(theta), 0,
                0,           0,          1);
}

// -------- basic object SDF function -------- //
float sphereSDF(vec3 samplePoint) {
    return length(samplePoint) - 1.0;
}
float sdCappedCylinder( vec3 p, float h, float r )
{
    vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sceneSDF(vec3 samplePoint) {
    samplePoint.y -= 1.0;
    float sphereDist1 = sphereSDF(samplePoint / 1.2) * 1.2;
    vec3 morph = vec3(1.0, 1.25, 1.0);
    float sphereDist2 = sphereSDF(samplePoint / 1.6  * morph + vec3(0.0, 1.4, 0.0)) * 1.2;
    float cover = sdCappedCylinder(samplePoint - vec3(0.0, 1.4, 0.0), 0.22, 0.5);

    float result = 0.0;
    result = opSmoothUnion(sphereDist1, sphereDist2, 0.3);
    result = opSmoothUnion(result, cover, 0.5);

    return result;
}

// -------- rayMarching -------- //
float rayMarching(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
            return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

vec3 rayDirection(float fov, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fov) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

// -------- shading function -------- //

struct LightProperty {
    float k_a;
    float k_d;
    float k_s;
    float shiness;

    vec4 ambient;
    vec4 diffuse;
    vec4 specular;

    vec3 lightPos;
    float lightIntensity;
};
// ------- Phong -------- //
vec4 PhongContribForLight(LightProperty light, vec3 p, vec3 eye) {
    vec4 color = vec4(0.0);
    vec3 N = estimateNormal(p);
    vec3 L = normalize(light.lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = max(dot(L, N), 0.0);
    float dotRN = max(dot(R, N), 0.0);
    // -------- Diffuse -------- //
    color += light.k_d * dotLN * light.diffuse;
    // -------- Specular -------- //
    color += light.k_s * pow((dotRN), light.shiness) * light.specular;
    color *= light.lightIntensity;

    return color;
}
vec4 PhongIllumination(LightProperty light, vec3 p, vec3 eye) {
    vec4 color = vec4(1.0);
    // Ambient
    color = light.ambient * light.k_a;

    // -------- Light1 -------- //
    light.lightPos = vec3(4.0 * sin(iTime),2.0,4.0 * cos(iTime));
    light.lightIntensity = 0.8;
    color += PhongContribForLight(light, p, eye);

    // -------- Light2 -------- //
    light.lightPos = vec3(2.0 * sin(0.37 * iTime),2.0 * cos(0.37 * iTime), 2.0);
    light.lightIntensity = 0.4;
    color += PhongContribForLight(light, p, eye);

    return color;
}
// ------- Blinn_Phong -------- //
vec4 blinnPhongContribForLight(LightProperty light, vec3 p, vec3 eye) {
    vec4 color = vec4(0.0);
    vec3 N = estimateNormal(p);
    vec3 L = normalize(light.lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 H = normalize(L + V);

    float dotLN = max(dot(L, N), 0.0);
    float dotNH = max(dot(N, H), 0.0);
    // -------- Diffuse -------- //
    color += light.k_d * dotLN * light.diffuse;
    // -------- Specular -------- //
    color += light.k_s * pow((dotNH), light.shiness) * light.specular;
    color *= light.lightIntensity;

    return color;
}
vec4 blinnPhongIllumination(LightProperty light, vec3 p, vec3 eye) {
    vec4 color = vec4(1.0);
    // Ambient
    color = light.ambient * light.k_a;

    // -------- Light1 -------- //
    light.lightPos = vec3(4.0 * sin(iTime),2.0,4.0 * cos(iTime));
    light.lightIntensity = 0.8;
    color += blinnPhongContribForLight(light, p, eye);

    // -------- Light2 -------- //
    light.lightPos = vec3(2.0 * sin(0.37 * iTime),2.0 * cos(0.37 * iTime), 2.0);
    light.lightIntensity = 0.4;
    color += blinnPhongContribForLight(light, p, eye);

    return color;
}
// -------- camera view transform -------- //
mat4 viewMatrix_MY(vec3 eye, vec3 center, vec3 up) {
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //  
    vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 10.0, 20.0);
    mat4 viewToWorld;
    viewToWorld = viewMatrix_MY(eye, vec3(0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;
    float dist = rayMarching(eye, worldDir, MIN_DIST, MAX_DIST);

    // 
    vec3 p = eye + dist * worldDir;

    LightProperty light;
    light.ambient = vec4(0.1, 0.1, 0.1, 1.0);
    light.diffuse = vec4(0.7, 0.2, 0.2, 1.0);
    light.specular = vec4(1.0, 1.0, 1.0, 1.0);
    light.shiness = 20.0;
    light.k_a = 1.0;
    light.k_d = 1.0;
    light.k_s = 1.0;


    vec2 pic1 = gl_FragCoord.xy / iResolution.xy;
    vec4 color =  blinnPhongIllumination(light, p, eye) * step(pic1.x, 0.5);
    color += PhongIllumination(light, p, eye) * step(0.5, pic1.x);

    fragColor = color;
}

