vec2 hash2(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}
const float PHI = 1.61803398875;
const float TAU = 6.28318530718;

mat2 rot(float theta)
{
    float x = cos(theta);
    float y = sin(theta);
    return mat2(x,-y,y,x);
}

float gradient( vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash2( i + vec2(0,0) ), f - vec2(0,0) ), 
                     dot( hash2( i + vec2(1,0) ), f - vec2(1,0) ), u.x),
                mix( dot( hash2( i + vec2(0,1) ), f - vec2(0,1) ), 
                     dot( hash2( i + vec2(1,1) ), f - vec2(1,1) ), u.x), u.y);
}
float sdPie( vec2 p, vec2 c, float r ) // SDF by iq
{
    p.x = abs(p.x);
    float l = length(p) - r;
	float m = length(p - c*clamp(dot(p,c),0.0,r) );
    return max(l,m*sign(c.y*p.x-c.x*p.y));
}

vec4 Lilypads(vec2 uv)
{    
    const float inner = 15.; // Hide lilypads that come before $inner
    const float outer = 80.; // ... or come after $outer

    const float shift = 1./20.; // magnitude of random shifting about
    const float r = .15; // lilypad radius
    const float density = 8.;
    const float slice = .48*TAU;
    const vec2 c = vec2(sin(slice),cos(slice));
    const float border = 0.02;
    float aa = 3./iResolution.x;

    float d = 1.;

    vec3 col = vec3(1);

    vec3 col1 = vec3(.4,.8,.6);
    vec3 col2 = vec3(.4,.8,.6)-.1;

    for(float i = inner; i<outer; i++){
        float theta = TAU*fract(i*PHI);
        float rho = pow(i,PHI-1.1);
        vec2 p = vec2(cos(theta),sin(theta))*rho/density;
        if(length(p-uv)>r+shift) continue;

        vec2 q = p+gradient(p+iTime)*shift/2.-uv;
        q *= rot(gradient(p+iTime/100.)*10.*TAU);

        d = sdPie(q,c,r-1.2*border)-1.2*border;
        if(d<aa) {
            vec3 fill = mix(col2,col1,smoothstep(aa,-aa,d+border));
            col = mix(col,fill,smoothstep(aa,-aa,d));
            if(d<-aa) break;
        }
    }    

    return vec4(col,1);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.x;

    vec3 col = vec3(1);

    vec4 lilypads = Lilypads(uv);
    col = mix(vec3(1),lilypads.rgb,lilypads.a);

    fragColor = vec4(col,1.0);
}

float circ(vec2 uv, vec2 pos, float r, float blur){

    vec2 off = pos*r;
    off.x += sin(iTime*r*7.)*.02;
    off.y += cos(iTime*r*4.)*.04;

    float d = length(uv+off);//+sin(iTime*5.)*.01; // distance from center

    float coli = d;

    return smoothstep(r,r-blur,d);



}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5; //center it
    uv.x *= iResolution.x/iResolution.y;

    float t = .1;//basecolor

   int i=0;
   while(i<10){

        t += circ( uv,
        vec2(sin(float(i)*1.2),cos(float(i)*1.2)),
        float(i)*.04,
        float(i)*.02
        );

        i++;
    }


    vec3 col = vec3(t*.4)+.4*cos(iTime+uv.xyx+vec3(5,3,4))*t;   // Time varying pixel color

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define MAXITER 100
#define MAXVAL 5.
#define PI 3.14159265359

vec3 getColorf(float iter,vec2 z){

    vec3 color;
    color.r=float(mod(iter,10.))/9.;
    color.b=float(mod(iter/10.,10.))/9.;
    color.g=.5+.5*sin(iter/10.-log2(1.+log2(1.+dot(z,z))));
    return color;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    vec2 uv = fragCoord.xy/iResolution.xy;
    uv-=.5;
//   float zoom=.004+iMouse.x/iResolution.x;
  float zoom=.75+.4*(cos(PI+.07*iTime));

    uv*=2.*pow(zoom,8.);
    vec3 color;
    vec2 c=uv+vec2(-.74515,.1858);
   // vec2 c=uv+vec2(-.68435,.322500);
    vec2 z=vec2(0.);
    float iter=0.;
    float warp=2.;//-(iMouse.y/iResolution.y);
    for(int i=0;i<MAXITER;++i){
        z=vec2(z.x*z.x-z.y*z.y,warp*z.x*z.y)+c;
        if (length(z)>MAXVAL) break;
        iter++;
    }
    color=getColorf(iter,z);    
    fragColor = vec4(color,1.0);
}

