float random3 (in vec3 _st) {
    return fract(sin(dot(_st,
                         vec3(12.9898,78.233,123.24647)))*
      43758.5453123);
}

float noise3 (in vec3 _st) {
    vec3 i = floor(_st);
    vec3 fr = fract(_st);

    // Four corners in 2D of a tile
    float a = random3(i);
    float b = random3(i + vec3(1.0, 0.0, 0.0));
    float c = random3(i + vec3(0.0, 1.0, 0.0));
    float d = random3(i + vec3(1.0, 1.0, 0.0));

    float e = random3(i + vec3(0.0, 0.0, 1.0));
    float f = random3(i + vec3(1.0, 0.0, 1.0));
    float g = random3(i + vec3(0.0, 1.0, 1.0));
    float h = random3(i + vec3(1.0, 1.0, 1.0));

    vec3 u = fr * fr * (3.0 - 2.0 * fr);

    float bf = mix(a,b,u.x);
    float bb = mix(c,d,u.x);

    float bot = mix(bf,bb,u.y);

    float tf = mix(e,f,u.x);
    float tb = mix(g,h,u.x);

    float top = mix(tf,tb,u.y); 

    return mix(bot,top,u.z);
}

#define NUM_OCTAVES 4

float fbm3 ( in vec3 _st) {
    float v = 0.0;
    float a = 0.5;
    vec3 shift = vec3(100.0);
    float offset = 0.;
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        //v += a * sin((_st.y + _st.x+ _st.y)*10.)*1.5;
        offset += a*2.5/1.5;
        v += a * noise3(_st)*2.5;
        _st = _st * 2. + shift;
        a *= 0.5;
    }
    return v/offset;
}

float voronoi(vec2 uv){
    uv*=3.;
    float minDist = 100.;
    float t = iTime;

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);
    for(float y=-1.;y<=1.;y++){
        for(float x = -1.;x<=1.;x++){
            vec2 offs = vec2(x,y);
            vec2 n = N22(id + offs);
            vec2 p = offs + sin(n*t)*.5;

            float d = length(gv-p);

            if(d<minDist){
                minDist = d;
            }

        }
    }

    return minDist;

}

float smoosh(float a){
    float b = atanh((a-0.5)*2.)/10.+0.5;
    return b;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 R = iResolution.xy;
    vec2 uv = (2.*fragCoord-R.xy)/R.y;
    uv = (2.*fragCoord-R.xy)/512.;

    float m = 0.;
    float t =iTime;

    float minDist = 100.01;
    vec3 oCol = vec3(1.);
    if(false){
    for(float i = 0.;i<50.;i++){
        vec2 n = N22(vec2(i+1.));
        vec2 n2 = N22(vec2(i*40.));
        vec2 p = sin(n*t);
        p.x *= R.x/R.y;

        float d = length(uv-p);
        m += smoothstep(.02,0.01,d);

        if(d<minDist){
            minDist = d;
            oCol = vec3(n.x,n2.x,n2.y);
        }

    }
    }else{
        uv*=1.6;

        vec2 gv = fract(uv)-.5;
        vec2 id = floor(uv);
        for(float y=-1.;y<=1.;y++){
            for(float x = -1.;x<=1.;x++){
                vec2 offs = vec2(x,y);
                vec2 n = N22(id + offs);
                vec2 n2 = N22((id + offs)*40.);
                vec2 p = offs + sin(n*t+n2)*.5;

                float d = length(gv-p) + voronoi(vec2(uv.xy*1.+n.xy))*2.;

                if(d<minDist){
                    minDist = d;
                    oCol = vec3(n.x,n2.x,n2.y)*0.8+0.2; //for some reason sometimes n and n2 are all 0 making oCol 0...;
                }

            }
        }
    }

    //float val = minDist;
    float val = aafract(minDist*4.);
    float v2 = floor(minDist*4.)/4.;

    vec3 hslCol = hsl2rgb(vec3(abs(sin(v2*PI+iTime/5.))/3.+iTime/10.,0.32,0.5));

    vec3 col = vec3(val);
    //col *= oCol;
    //col *= vec3(0.5,0.8,0.7)*v2; //could feed v2 into a hsl function lots of fun posibilities
    col *= hslCol;

    fragColor = vec4(col,1.0);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

 mat3 rotationMatrix(vec3 axis, float angle)
{
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}

float mixV(float a, float b, float d) {
	return (d * a)+(1.0 - d)*b;
}

float map(vec3 p)
{
   float displacement = .55 * sin(iTime + 5.*p.x)*cos(iTime * 1.1 + 5.*p.y)*sin(iTime * 1.2 + 5.*p.z);    
   return displacement + sdTorus(p, vec2(7.0, 3.0));
}

void getCamPos(inout vec3 ro, inout vec3 rd)
{
    ro.z = -16.0;
}

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 _p = (iResolution.xy - 2.0 * fragCoord.xy) / iResolution.y;
    vec3 ray = normalize(vec3(_p, 1.0));
    vec3 cam = vec3(0.0, 0.0, 0.0);
    bool hit = false;
    getCamPos(cam, ray);  

    mat3 rm = rotationMatrix(vec3(0.66, 1., 0.9), iTime);


    float depth = 0.0, d = 0.0, iter = 0.0;
    vec3 p;

    for( int i = 0; i < 30; i ++)
    {
    	p = depth * ray + cam;
        d = map(rm * p);

        if (d < 0.0001) {
			hit = true;
            break;
        }

		depth += d * 0.75;
		iter++;

    }

    vec3 col = vec3(1.0 - iter / 31.0);
    /*
    const vec3 a = vec3(0.5, 0.5, 0.5);
    const vec3 b = vec3(0.5, 0.5, 0.5);
    const vec3 c = vec3(2., 1., 0.);
