    vec3 sq = sqrt(col);

    vec3 gg = palette(sq.x * sq.y * sq.z, a, b, c, da);

    fragColor = vec4(gg, 0.0);


}

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float lpNorm(vec3 p, float n)
{
	p = pow(abs(p),vec3(n));
	return pow(p.x+p.y+p.z,1./n);
}

float map(vec3 p){
    p.xy*=rot(iTime*.3);
    p.yz*=rot(iTime*.3);
    float scale=4.5;
    float mr2=.5;
    float off=.5;
    float s=1.;
    vec3 p0 = p;
    for (int i=0; i<16; i++) {
        if(i%3==0)p=p.yzx;
        if(i%2==1)p=p.yxz;
        p -= clamp(p,-1.,1.)*2.;
        float r2=pow(lpNorm(p.xyz,5.),2.);
        float g=clamp(mr2*max(1./r2,1.),0.,1.);
        p=p*scale*g+p0*off;
        s=s*scale*g+off;
    }
    return length(p)/s-.01;
}

vec3 calcNormal(vec3 pos)
{
  vec2 e=vec2(1,-1)*.005;
  return normalize(
    e.xyy*map(pos+e.xyy)+e.yyx*map(pos+e.yyx)+ 
    e.yxy*map(pos+e.yxy)+e.xxx*map(pos+e.xxx)
  );
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<80;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

float calcShadow(vec3 light, vec3 ld, float len)
{
	float depth=march(light,ld,0.,len);	
	return step(len-depth,.01 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0,8.);
    vec3 rd = normalize(vec3(uv,-2.));
    vec3 col = vec3(0);
	const float maxd = 40.;
    float t = march(ro,rd,0.,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=vec3(.3,.3,.6)+cos(p*.17)*.5+.5;
        vec3 n=calcNormal(p);      
		vec3 lightPos=vec3(20);
    	vec3 li=lightPos-p;
		float len=length(li);
		li/=len;
		float dif=clamp(dot(n, li),.5,1.);
        float sha=calcShadow(lightPos,-li,len);
        col*=max(sha*dif,.4);
        float rimd=pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn=rimd+2.2*(1.-rimd);
    	col*=frn*.9;
        col*=max(.5+.5*n.y,0.);
        col*=exp2(-2.*pow(max(0.,1.-map(p+n*.3)/.3),2.));
        col+=vec3(.5,.9,.9)*pow(clamp(dot(reflect(rd,n),li),0.,1.),8.);
        col=mix(vec3(0),col,exp(-t*t*.003));
    }
    fragColor.xyz=col;
}

#define MAX_STEPS 25
#define MAX_DIST 25.
#define SURF_DIST .008

float g_time;

float sdSphere( vec3 p, float s ) { return length(p)-s; }

float sdRoundedCylinder( vec3 p, float ra, float rb, float h ) {
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
} 

float sdBox( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb) {
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float smax( float a, float b, float k ) {
    k *= 1.4;
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*h/(6.0*k*k);
}

float smin( float a, float b, float k ) {
    float h = max(k-abs(a-b),0.0);
    return min(a, b) - h*h*0.25/k;
}

vec2 rot(vec2 p, in float an ) {
    float cc = cos(an);
    float ss = sin(an);
    return mat2(cc,-ss,ss,cc)*p;
}

float displacement(vec3 p) { return sin(60.*p.x)*sin(60.0*p.y)*sin(60.*p.z); }

float sdCone( in vec3 p, in vec2 c ) {
  vec2 q = vec2( length(p.xz), p.y );

  vec2 a = q - c*clamp( (q.x*c.x+q.y*c.y)/dot(c,c), 0.0, 1.0 );
  vec2 b = q - c*vec2( clamp( q.x/c.x, 0.0, 1.0 ), 1.0 );

  float s = -sign( c.y );
  vec2 d = min( vec2( dot( a, a ), s*(q.x*c.y-q.y*c.x) ),
			    vec2( dot( b, b ), s*(q.y-c.y)  ));
  return -sqrt(d.x)*sign(d.y);
}


vec2 getDist(vec3 p) {

    float matId;
    p.y -= 0.2;
    vec3 orig =  vec3(0, 1, 6.4);

    //HEAD
	vec3 headPos = p-orig;
    vec3 direction =  normalize(headPos);
    headPos.x -= (p.y - 1.1) * 0.35 * direction.x;
    headPos.x += pow(abs(sin((p.y + 2.) * 8.)) * 0.1, 1.2 + p.y) * normalize(headPos).x;  

    float head = length(headPos) -1. + p.y * 0.5;

    //HAIR
    vec3 hairPos = p-orig;
    float hair = sdRoundedCylinder(hairPos, 0.24, 0.15, 0.27);
    hair += displacement(hairPos) * .005;

    if (hair < head) {
       head = smin(hair, head, 0.01);
        matId = 0.;
    } else {
        matId = 2.;
    }

    //EYES
    vec3 eyePos = headPos + vec3(0.05 * direction.x, 0.25, .5);
    vec3 eyeOff = vec3(0.19, 0., 0.);
    eyePos.x = abs(eyePos.x);

    float eye = length(eyePos -eyeOff) -0.12;
    if (-eye > head) { head += eye; }
    head = min(eye + 0.01, head);    

    vec3 eyeBrowPos = vec3( rot( vec2(eyePos.x * 1.2 - eyeOff.x * 1.3 , eyePos.y -0.17) , 1.5),  eyePos.z+0.1);
    eyeBrowPos.x -= (1. - pow(abs(sin(g_time)), 0.4)) * 0.03;
    eyeBrowPos.x += sin(eyeBrowPos.y * 20. - 1.) * .012;
    float eyeBrow = sdRoundedCylinder(eyeBrowPos, 0.005, 0.04, 0.11);

    if (eyeBrow < head) {
        head = smin(eyeBrow, head, 0.1);
        matId = 0.;
    }

    //GLASSES
    eyePos.z += 0.2;
    eyeOff.x += 0.02;
    float glass = sdBox(eyePos-eyeOff, vec3(0.15, 0.07 , 0.02));
    eyeOff.x += 0.24;
    eyeOff.z += 0.2;
    float glassb = sdRoundedCylinder(vec3(rot((eyePos-eyeOff).xz, -.4), eyePos.y), 0.005, 0.02, 0.2);    

    eyePos.x *= 2.;
    eyePos.y *= 1.8;
    eyePos.z *= 0.9;
    eyePos.z -= 0.2;
    float glassC = sdCappedTorus( vec3(eyePos.x, rot((eyePos+eyeOff).yz, -1.5)), vec2(0.2, 0.), 0.15, 0.02);

    if (glassC < head) {
        head = min(glassC, head);
        matId = 6.;
    }
    if (glassb < head) {
        head = min(glassb, head);
        matId = 6.;
    }

    if (glass < head) {
        head = min(glass, head);
        matId = 1.;
    }

    //NOSE
    vec3 nosePos = p-orig;
    nosePos.z += 0.7;
    nosePos.y += 0.15;
    nosePos.yz = rot(nosePos.yz,-0.3);
    float h = smoothstep(0.,0.3,-nosePos.y );
    float nose = 1.;
    nosePos.x = sqrt(nosePos.x*nosePos.x + 0.001);
    nose = smin( nose, sdSphere(nosePos-vec3(0.04, -0.3, 0.037),0.03 ), 0.1);
    nose = smin( nose, sdSphere(nosePos*vec3(1.05,0.95,1.)-vec3(.0, -0.155, 0.05),0.02), 0.19);
    head = smin(head,nose,0.05-0.04*h);
    if (nose - 0.001 < head) {
        matId = 2.;
    }

    //EARS
    vec3 earsPos = p-orig;
    earsPos.x = abs(earsPos.x);
    earsPos.y += 0.35;
    earsPos.y *= 0.7;
    earsPos.z += 0.1;
    earsPos.x -= 0.41;
    float ear = sdSphere(earsPos,0.08); 
    if (ear < head) {
        head = smin(head,ear,0.02);
        matId = 2.;
    }

    //MOUTH
    float mT = 0.88 - pow(abs(g_time) / 1.2, 1.8);
    vec3 mPos = p - vec3(orig.x + 0.7 * direction.x * mT, orig.y-.6, orig.z -0.55);
    mPos.y -= pow(abs(sin(abs(p.x))), 1.9 + mT * 0.3);
   	float mouth = length(mPos) - 0.05;
    mPos.x = orig.x + 0.69 * direction.x  * (1.55 - mT);
    mPos.z *= 1.8;
    mPos.z -= 0.064;
    mPos.y -= 0.015;
    mPos.y *= 0.95;
    float outMouth = length(mPos) - 0.05;
    if (-mouth - 0.008 > head ) {
        matId = 3.;
        head = smin(-mouth, head, 0.2);
    }

    head = smin(head, outMouth, 0.03);  
    outMouth = length(vec3(mPos.x, mPos.y - 0.01 ,  mPos.z))  - 0.05;
    if (outMouth - 0.022 < head){ matId = 4.; }

    //DOLLAR
    vec3 dollaInitialPos = headPos + vec3(1.66, -0.52, 1.6) - vec3(.5*sin(g_time),0., 1.*cos(g_time));
    vec3 dollaPos = dollaInitialPos;
    dollaPos.x *= 0.85 - p.y * 0.01;
    dollaPos.xy *= 2.5;
    dollaPos.y *= 1.1;
    dollaPos.z += sin(iTime + dollaPos.y * 5.) * 0.05;
    dollaPos.xy = rot( dollaPos.xy, 0.9);
    dollaPos.x = dollaPos.y > 0. ? -dollaPos.x + 0.36 : dollaPos.x;
    float dollaA = sdCappedTorus(dollaPos, vec2(sin(iTime-.3), cos(iTime-.3)), 0.22, 0.025);

    dollaPos.xy = dollaInitialPos.xy - vec2(0.02, 0.06);
    dollaPos.xy *= 2.;
    dollaPos.xy = rot( dollaPos.xy, -.2);
    dollaPos.x -= 0.05;
    dollaPos.x = abs(dollaPos.x);
    dollaPos.x -= 0.05;
    float dollaB = sdRoundedCylinder(dollaPos, 0.01, 0.02, 0.49 * abs(cos(iTime / 2. - 1.) ));    


   if (dollaA < head) {
       head = min(dollaA, head);
       matId = 5.;
    } 
    if (dollaB < head) {
        head = smin(dollaB, head, 0.075);
        matId = 5.;
    }

    return vec2(head, matId);
}


vec2 rayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float matId = -1.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        vec2 res = getDist(p);
        float dS = res.x;
        dO += dS;
        matId = res.y;

        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return vec2(dO, matId);
}

vec3 getNormal(vec3 p) {
	float d = getDist(p).x;
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        getDist(p-e.xyy).x,
        getDist(p-e.yxy).x,
        getDist(p-e.yyx).x);

    return normalize(n);
}

float light(vec3 p, vec3 n) {
    vec3 lightPos = vec3(0.5, 1.5, 4.);
    vec3 l = normalize(lightPos-p);
    float dif = clamp(dot(n, l), 0., 1.);     
    return dif*1.5;
}

float slight(vec3 p, vec3 ro, vec3 n) {
    vec3 lightPos = vec3(g_time * 4., 2., 4.);
    vec3 l = normalize(lightPos-p);

    float lDist = max(length(l), 0.001);
    float atten = 1./(1.0 + lDist*lDist*0.5);	
    vec3 reflection = reflect(-l, n);
    float spec = pow(max( dot(reflection, -normalize(ro)), 0.), 9.); 

    return spec*3. *atten;;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mo = (iMouse.xy-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);

    float iTime = iTime * 4.;
    vec2 m = vec2(0.5);
    g_time = sin(-0.25 + 0.31416*iTime) - 6.2831*(m.x-0.5);

	vec3 ro = vec3(.5*sin(g_time),1., 1.*cos(g_time));
    vec3 rd = normalize(vec3(uv.x, uv.y , 2.));

    vec2 d = rayMarch(ro, rd);
    vec3 p = ro + rd * d.x;
    vec3 n = getNormal(p);
    float dif = light(p, n) / 2.7;
    float spec = slight(p, ro, n);
    vec3 bc = vec3(0.85, 0.72, 0.56);

    col = dif * vec3(0.85, 0.35, 0.2);

    if (d.y == 0.) { // HAIR
          col = dif * vec3(0.12, 0.10, 0.101); 
    } else  if (d.y == 1.) { // GLASSES
          col = min((spec+spec/1.5+dif) * vec3(0.84, 0.2, 0.15), 1.); 
    } else  if (d.y == 2.) { // SKIN
        col = dif * vec3(0.85, 0.35, 0.2);
    } else if (d.y == 3.) { // MOUTH IN
          col = dif * vec3(1., 0.5, 0.4) * 0.9;
    } else if (d.y == 4.) { // MOUTH
              col = dif * vec3(0.9, 0.3, 0.2) / 1.3;
    } else if (d.y == 5.) { // DOLLA
              col = max(pow(spec+dif+dif, 1.2), 0.3) * vec3(0.1, 0.8, 0.4) / 2.;
    } else if (d.y == 6.) { // GLASSES BASE
              col = (spec+spec/2.+dif) * vec3(0.9, 0.7, 0.15);
    } else if (d.y == 7.) { // PARTS
            col =  vec3(0.32, 0.22, 0.12) * 0.6;// - dif/2.*bc;
    }

    col = pow(col, vec3(.6));
    col +=  0.1 * vec3(.25, 0.36, 0.7);
    col *= 1.5;

    fragColor = vec4( max(vec3( col), 0.12),1.0);
}

