//distance functions: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm

float sdCircle(in vec2 pos,in float size)
{
    return length(pos) - size;
}

float sdLine(in vec2 pos,in vec2 a,in vec2 b)
{
    vec2 pa = pos - a;
    vec2 ba = b - a;
    float h = clamp(dot(pa,ba) / dot(ba,ba), 0.0, 1.0);
    return length(pa - ba * h);
}

float sdPolygon(in vec2 pos,in vec2[5] v)
{
    const int num = v.length();
    float d = dot(pos - v[0],pos - v[0]);
    float s = 1.0;
    for(int i = 0,j = num-1;i < num;j = i,i ++)
    {
        vec2 e = v[j] - v[i];
        vec2 w = pos - v[i];
        vec2 b = w - e * clamp(dot(w,e) / dot(e,e),0.0,1.0);
        d = min(d,dot(b,b));
        bvec3 cond = bvec3(pos.y >= v[i].y,pos.y < v[j].y,e.x * w.y > e.y * w.x);
        if( all(cond) || all(not(cond)) ) s=-s;  
    }
    return s * sqrt(d);
}

float pShapeAdd(in float a,in float b)
{
    return min(a,b);
}

float pShapeSubtract(in float a,in float b)
{
    return max(a,-b);
}

float pShapeOverlap(in float a,in float b)
{
    return max(a,b); 
}

float pShapeAddSmooth(in float a,in float b,in float k) 
{
    float h = clamp(0.5 + 0.5 * (b - a) / k,0.0,1.0);
    return mix(b,a,h) - k * h * (1.0 - h); 
}

/*float pShapeSubtractSmooth(in float a,in float b,in float k) 
{
    float h = clamp(0.5 - 0.5 * (b + a) / k,0.0,1.0);
    return mix(b,-a,h) + k * h * (1.0 - h); 
}*/

/*float pShapeOverlapSmooth(in float a,in float b,in float k) 
{
    float h = clamp(0.5 - 0.5 * (b - a) / k,0.0,1.0);
    return mix(b,a,h) + k * h * (1.0 - h);
}*/

float pSmooth(in float val,in float factor)
{
    return smoothstep(factor,-(3.0 / iResolution.y),val);
}

vec3 cSky(in vec2 pos)
{
    //vec3(mix(vec3(0.8,0.8,2.0),vec3(0.7,0.8,1.0),pos.y - 0.5))
    return vec3(mix(vec3(0.8,0.8 + (pos.y + pos.x) / 10.0,2.0),vec3(0.7,0.8,1.0),pos.y - 0.5));
}

vec3 cDebug(in vec2 pos)
{
    return vec3(0.8,0.5,0.6);
}

vec3 cWater(in vec2 pos,in float height,in float move)
{
    vec3 color = vec3(0.1,0.5,1.0);
    color.r += height + sin(move) * abs(pos.x);
    color.g += height + (move * 1.2);
    color.b += (pos.y / 4.0);
    return color;
}

vec3 cIce(in vec2 pos)
{
   vec3 color;
   float move = abs(sin(((-pos.x + (iTime / 6.0)) / 50.0) * 400.0) / 20.0);
   move += (sin(abs(pos.x) + iTime * 10.0) / 100.0);
   move = pos.y + move + 0.4 + 0.2;
   color = cWater(pos,0.6,move);
   color.r /= abs(move - 0.16);
   color.g /= abs(move + 0.1);
   color.b /= pos.y * move;
   return color * (color * 0.4);
}

vec3 cBear(in vec2 pos)
{
    pos.y += 0.2;
    vec3 color = vec3(2.0 - (1.0 - (pos.x - 0.5) - (pos.y / 1.2)),1.0 - (0.1 - pos.y),0.9);
    return color;
}

vec3 cRing(in vec2 pos)
{
    vec3 color = vec3(4.0,0.4,1.5);
    float a = atan(pos.x,pos.y);
    a = sin((a + 0.2) * 4.0);
    a = pSmooth(a,0.02);
    color.r += pos.y * 0.4;
    color.g += a - (pos.y * 2.0);
    color.b *= a * a;
    return color * (color * 0.4);
}

vec4 sWater(in vec2 pos,in float height,in float factor)
{
   float move = abs(sin(((-pos.x + (iTime / 6.0)) / 50.0) * 400.0) / 20.0);
   move += (sin(abs(pos.x) + iTime * 8.0) / 100.0);
   move = pos.y + move + height + 0.1;
   float sd = pSmooth(move,factor);
   return vec4(cWater(pos,height,move),sd);
   return vec4(0.0);
}

float sIcePoly(in vec2 pos)
{
   float sd;
   pos.y += abs(sin(iTime) / 30.0);
   vec2 v1 = vec2(-0.4,-0.1);
   vec2 v2 = vec2(0.0,-0.4);
   vec2 v3 = vec2(0.4,-0.1);
   vec2 v4 = vec2(0.2,0.1);
   vec2 v5 = vec2(-0.01,0.1);
   vec2[] v = vec2[](v1,v2,v3,v4,v5);  
   sd = sdPolygon(pos,v);
   v1 -= vec2(0.2,0.0);
   v2 -= vec2(0.2,0.0);
   v3 -= vec2(0.2,0.0);
   v4 -= vec2(0.2,0.0);
   v5 -= vec2(0.2,-0.2);
   v = vec2[](v1,v2,v3,v4,v5); 
   sd = pShapeAdd(sd,sdPolygon(pos,v));
   v1 -= vec2(-0.4,-0.2);
   v2 -= vec2(-0.6,-0.1);
   v3 -= vec2(-0.6,-0.1);
   v4 -= vec2(-0.4,-0.1);
   v5 -= vec2(-0.4,0.1);
   v = vec2[](v1,v2,v3,v4,v5);
   sd = pShapeAdd(sd,sdPolygon(pos,v));
   return sd;
}

vec4 sIce(in vec2 pos,in float factor)
{
   float sd = sIcePoly(pos);
   sd = pSmooth(sd,factor);
   return vec4(cIce(pos),sd);     
}

vec4 sIceWater(in vec2 pos,in float factor)
{
    float sd = sIcePoly(pos);
    float move = abs(sin(((-pos.x + (iTime / 6.0)) / 50.0) * 400.0) / 20.0);
    move += (sin(abs(pos.x) + iTime * 4.0) / 100.0);
    move = pos.y + move + 0.1; 
    sd = pShapeOverlap(sd,move);
    sd = pSmooth(sd,factor);
    return vec4(cWater(pos,0.3,0.004),sd);
}

vec4 sBear(in vec2 pos,in float factor)
{
    pos.y += abs(sin(iTime) / 30.0);
    float m = (abs(sin(iTime)) / 100.0);
    float dm;
    float sd = sdCircle(pos,0.1 - m / 4.0);
    float dd = sdCircle(pos - vec2(0.1,0.08 - m),0.04);
    sd = pShapeAddSmooth(sd,dd,0.1);
    dd = sdCircle(pos - vec2(0.1,-0.08),0.03);
    sd = pShapeAddSmooth(sd,dd,0.05);
    dd = sdCircle(pos - vec2(0.12,-0.09),0.026);
    sd = pShapeAdd(sd,dd);
    dd = sdCircle(pos - vec2(0.15,0.01 - m / 4.0),0.02);
    sd = pShapeAddSmooth(sd,dd,0.05);
    dd = sdCircle(pos - vec2(0.17,0.0),0.02);
    sd = pShapeAdd(sd,dd);
    dd = sdCircle(pos - vec2(0.08,0.1),0.05);
    sd = pShapeAdd(sd,dd);
    dd = sdCircle(pos - vec2(0.10,0.1),0.02);
    sd = pShapeAddSmooth(sd,dd,0.1);
    dd = sdCircle(pos - vec2(0.12,0.11),0.06);
    sd = pShapeAdd(sd,dd);
    dd = sdCircle(pos - vec2(0.12,0.17),0.02);
    sd = pShapeAdd(sd,dd);
    dd = sdCircle(pos - vec2(0.18,0.13),0.02);
    sd = pShapeAdd(sd,dd);
    sd = pSmooth(sd,factor);
    return vec4(cBear(pos - vec2(0.0,-0.25)),sd);
}

vec4 sBear2(in vec2 pos,in float factor)
{
    pos.y += abs(sin(iTime) / 30.0);
    float m = (abs(sin(iTime + 20.0)) / 60.0);
    float dm;
    float sd = sdCircle(pos - vec2(0.0,0.01),0.11 - m / 2.0);
    float dd = sdCircle(pos - vec2(-0.12,0.06),0.035);
    sd = pShapeAddSmooth(sd,dd,0.1);
    dd = sdCircle(pos - vec2(0.07,-0.01),0.03);
    sd = pShapeAddSmooth(sd,dd,0.1);
    dd = sdCircle(pos - vec2(-0.1,0.06),0.07);
    sd = pShapeAdd(sd,dd);
    dd = sdCircle(pos - vec2(-0.1,0.13),0.02);
    sd = pShapeAdd(sd,dd);
    dd = sdCircle(pos - vec2(-0.166,0.07),0.02);
    sd = pShapeAdd(sd,dd);
    sd = pSmooth(sd,factor);
    return vec4(cBear(pos - vec2(0.0,-0.2)),sd);
}

vec4 sBearTail(in vec2 pos,in float factor)
{
    pos.y += abs(sin(iTime) / 30.0);
    float sd = sdCircle(pos - vec2(-0.08,-0.05),0.02);
    sd = pSmooth(sd,factor);
    return vec4(cBear(pos - vec2(0.0,-0.20)),sd);
}

vec4 sBearTail2(in vec2 pos,in float factor)
{
    pos.y += abs(sin(iTime) / 30.0);
    float sd = sdCircle(pos - vec2(-0.08,-0.05),0.02);
    sd = pSmooth(sd,factor);
    return vec4(cBear(pos - vec2(-0.08,-0.30)),sd);
}

vec4 sRing(in vec2 pos,in float factor)
{
    pos.y += abs(sin(iTime) / 30.0);
    pos -= vec2(0.1,-0.23);
    float sd = sdCircle(pos,0.1);
    float dd = sdCircle(pos,0.05);
    sd = pShapeSubtract(sd,dd);
    sd = pSmooth(sd,factor);
    return vec4(cRing(pos),sd);  
}

vec4 sVignette(in vec2 pos,in float factor)
{
    float sd = sdLine(pos,vec2(-2.0,0.0),vec2(2.0,0.0));
    sd = pSmooth(sd,factor);
    return vec4(vec3(0.1,0.1,0.3),sd);
}

vec3 pDraw(in vec3 color,in vec4 obj)
{
    return mix(color,obj.rgb,obj.a);
}

vec3 pDrawEx(in vec3 color,in vec4 obj,in float factor)
{
    return mix(color,obj.rgb * factor,obj.a);
}

vec2 pScreen(in vec2 pix,in vec3 res)
{
    return vec2((2.0 * pix - res.xy) / res.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = pScreen(fragCoord,iResolution);
    vec3 color = cSky(uv);
    vec4 water = sWater(uv - vec2(-0.7,0.0),0.3,0.004); 
