    color = pDraw(color,beartail2);
    vec4 vignette = sVignette(uv - vec2(0.0,-1.0),0.5);
    color = pDraw(color,vignette);
    fragColor = vec4(color,1.0);
}

#define CONTOUR_SPACING 1.0
#define CONTOUR_THICKNESS 2.0
#define DELTA 0.001

// Hardware derivatives don't seem to produce good contours on my computer
//#define USE_HARDWARE_DERIVATIVES

// Hue to RGB function from Fabrice's shadertoyunofficial blog:
#define hue2rgb(hue) 0.6 + 0.6 * cos(6.3 * hue + vec3(0.0, 23.0, 21.0))

float f(in vec2 p) {
    p *= 3.0;
    vec3 p3 = vec3(p, iTime);
    return dot(sin(p3), cos(p3.zxy));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = CONTOUR_THICKNESS / iResolution.y * 4.0;

    float hSpacing = 0.5 * CONTOUR_SPACING;
    float fRes = f(uv);

    #ifdef USE_HARDWARE_DERIVATIVES
    float grad = length(fwidth(fRes)) * iResolution.y / 5.0;

    #else
    float grad = length(vec2(f(uv + vec2(DELTA, 0.0)) - f(uv - vec2(DELTA, 0.0)),
                             f(uv + vec2(0.0, DELTA)) - f(uv - vec2(0.0, DELTA)))) / (2.0 * DELTA);
    #endif

    float contour = abs(mod(fRes + hSpacing, CONTOUR_SPACING) - hSpacing) / grad;

    fragColor = vec4(hue2rgb(fRes * 0.5) - smoothstep(unit, 0.0, contour), 1.0);
}

#define ITER 10
#define MAX_STEPS 300
#define MAX_DIST 100.
#define SURF_DIST 0.001
#define SPEED 100.

float dist(vec2 p, vec2 t){
    float dx = p.x-t.x;
    float dy = p.y-t.y;
    return sqrt(dx*dx+dy*dy);
}

// 3d mandelbulb
float mandel3(in vec3 v, out vec3 map){
    vec3 r = v;
    vec3 rNext = r;
    float m = dot(v, v);
    vec4 trap = vec4(abs(r), m);

    int i = 0;
    float d = 1.;

    float n =  3.;

    for(i=0; i<ITER; i++){
        float cr = length(r);
        float nr = pow(cr, n);
        //*abs(sin(iTime/5.) )
        float accos = acos(fract(r.z/cr+iTime/10.));
        float actan = atan(r.y/r.x);

        d = pow(cr, n-1.) *n * d + 1.0;

        rNext.x = nr*sin(accos*n)*cos(n*actan);
        rNext.y = nr*sin(accos*n)*sin(n*actan);
        rNext.z = nr*cos(n*accos);
        r = r - rNext;

        trap = min(trap, vec4(abs(r), m));
        map = vec3(trap.yzw);

        m = dot(r,r);

        if(m > 4.){
            break;
        }
    }
    //return pow(length(r), 2.)+sqrt(m);
    return  .1*log(m) * sqrt(m) / d;
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d; 
}

float RayMarch(in vec3 ro, in vec3 rd, out vec3 col){
    float dO = 0.;

    for(int i=0; i<MAX_STEPS; i++){
        vec3 p = ro+rd*dO;
        p.xz *= Rot(iTime/SPEED);
        p.yz *= Rot(iTime/SPEED-2.);

        float mbd = mandel3(abs(p), col);
        float dS = mbd;

        if(dO > MAX_DIST || dS<SURF_DIST) break;
        dO += dS;
    }

    return dO;
}
vec3 GetNormal(vec3 p){
    vec3 col;
    // my e value made black artifacts
    // so I took a look at iq's normal epsilone. 
    // don't exactly know how this is calculated.
    // looks like it is relevant to some screen pixel size calculation..?
    vec2 e = vec2(1.0,-1.0)*0.5773*0.25*2.0/(iResolution.y*1.5);

    // getting vector with very small vector
    vec3 n = vec3(
        e.xyy*mandel3(p+e.xyy, col)+
        e.yxy*mandel3(p+e.yxy, col)+
        e.yyx*mandel3(p+e.yyx, col)+
        e.xxx*mandel3(p + e.xxx,col ) 
    );

    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/(iResolution.y);
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 col = vec3(0);

    vec3 ro = vec3(0,1.3, 1.3);
    vec3 rd = R(uv, ro, vec3(0,0,0), .7);

    vec3 backgrounduv;

    col = vec3(.05, .12, .1);
    float d = RayMarch(ro, rd, backgrounduv);
    //col += vec3(2.1-d);
    col += pow(clamp(backgrounduv.z, 0., .97), 1.)*vec3(.8, .6, .1);
    col *= 1.7;

    //light
    vec3 lp = vec3(1, 1, 0);
    //lp.xz *= Rot(iTime/3.);
    lp.yz *= Rot(iTime/3.);
    vec3 lr = ro+rd*d;
    lr.xz *= Rot(iTime/SPEED);
    lr.yz *= Rot(iTime/SPEED-2.);
    lr = abs(lr);
    vec3 l = normalize(lp - lr);
    vec3 n = GetNormal(lr);
    float shadow = clamp(dot(n, l), 0.2, .99);
    shadow *= .98;
    col *= vec3(pow(shadow, 2.));
    col += vec3(.09, .04, .09);

    fragColor = vec4(col, 0);
}

vec3 IntersectParabola2D(in vec2 ro, in vec2 rd, in float a, in float b, in float c) {
    float ra = a * rd.x * rd.x;
    float rb = 2.0 * a * ro.x * rd.x + b * rd.x - rd.y;
    float rc = a * ro.x * ro.x + b * ro.x + c - ro.y;

    vec3 intersect = vec3(0.0);

    // If the discriminant is less than zero there are no (real) roots
    float discr = rb * rb - 4.0 * ra * rc;
    if (discr > 0.0) {
        float root = sqrt(discr);
        float denom = 2.0 * ra;
        float t1 =  (root - rb) / denom; // 1st root
        float t2 = -(rb + root) / denom; // 2nd root
        intersect = vec3(t1, t2, 1.0);
    }

    return intersect;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    float time = iTime * 0.25;
    vec3 color = vec3(0.0);

    float a = sin(time);
    float b = cos(time);
    float c = sin(time * 2.0);

    // Draw the parabola
    float g = 2.0 * a * uv.x + b; // Derivative for DE
    float f = a * uv.x * uv.x + b * uv.x + c;
    color += smoothstep(unit, 0.0, abs(uv.y - f) / sqrt(1.0 + g * g));

    // Ray origin (ro) and ray direction (rd)
    vec2 ro = vec2(cos(time * 0.75) * 2.0, sin(time * 1.5));
    vec2 rd = sin(sin(time * 0.25) * 3.14 + vec2(1.57, 0.0));

    // Draw the ray
    color += smoothstep(unit, 0.0, length(uv - ro) - 0.1);
    color += smoothstep(unit, 0.0, abs(dot(uv - ro, rd.yx * vec2(-1.0, 1.0))));

    // Draw the intersections:
    vec3 intersect = IntersectParabola2D(ro, rd, a, b, c);
    if (intersect.z > 0.5) {
        color += smoothstep(unit, 0.0, length(uv - ro - rd * intersect.x) - 0.05);
        color += smoothstep(unit, 0.0, length(uv - ro - rd * intersect.y) - 0.05);
    }

    fragColor = vec4(color, 1.0);
}

vec3 lightDir=vec3(-0.57,0.57,0.57);



float distFuncCube(vec3 p){
	vec3 q=abs(p);
	return length(max(q-vec3(1.0,0.1,0.5),0.0));
}

float distFuncSphere(vec3 p){
	float s=0.5;
	return length(p)-s;
}

float distFunc(vec3 p){
	p=mod(p,0.8)-0.4;
	float d3=distFuncCube(p);
	float d4=distFuncSphere(p);

	return min(d3,d4);//OR

}


vec3 genNormal(vec3 p){
	float d=0.0001;
	return normalize(vec3(
		distFunc(p+vec3(d,0.0,0.0))-distFunc(p+vec3(-d,0.0,0.0)),
		distFunc(p+vec3(0.0,d,0.0))-distFunc(p+vec3(0.0,-d,0.0)),
		distFunc(p+vec3(0.0,0.0,d))-distFunc(p+vec3(0.0,0.0,-d))
		));
}


void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 p=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;


	vec3 cPos=vec3(sin(iTime)*0.6,0.5,iTime*0.3);
	vec3 cDir=vec3(0.0,0.0,-1.0);
	vec3 cUp=vec3(0.0,1.0,0.0);
	vec3 cSide=cross(cDir,cUp);
	float targetDepth=1.0;

	vec3 ray=normalize(cSide*p.x+cUp*p.y+cDir*targetDepth);

	float tmp,dist;
	tmp=0.0;
	vec3 dPos=cPos;

	float emission=0.0;

	for(int i=0;i<48;i++){
		dist=distFunc(dPos);
		tmp+=dist;
		dPos=cPos+tmp*ray;
		emission+=exp(abs(dist)*-0.2);
	}

	vec3 color;

	if(abs(dist)<0.001){
		vec3 normal=genNormal(dPos);
		float diff=clamp(dot(lightDir,normal),0.1,1.0);
		color=0.02*emission*vec3(sin(iTime),1.0,cos(iTime))*diff;
	}else{
		color=vec3(0.0);
	}
	fragColor=vec4(color,1.0);
}

