float CatBody(in vec2 pos)
{
    float sd = 1.0 - smoothstep(0.0,0.1,length(pos) - 0.1);
    sd += 1.0 - smoothstep(0.0,0.1,length(pos + vec2(0.0,0.2)) - 0.16);
    sd += 1.0 - smoothstep(0.0,0.1,length(pos + vec2(-0.06,0.4)) - 0.2);
    sd += 1.0 - smoothstep(0.0,0.1,length(pos + vec2(0.0,0.4)) - 0.18);
    sd += 1.0 - smoothstep(0.0,0.1,length(pos + vec2(0.08,0.6)) - 0.05);
    sd += 1.0 - smoothstep(0.0,0.1,length(pos + vec2(-0.2,0.6)) - 0.05);
    return sd;
}

float CatEars(in vec2 pos)//Simple Egg - exact: https://www.shadertoy.com/view/XtVfRW
{
    float sd = 0.0;
    float ear = 0.0;
    float ra = 0.04;
    float rb = 0.01;
    const float k = sqrt(3.0);
    vec2 p = pos;
    float r = ra - rb;
    p -= vec2(-0.08,-0.02);
    p.x = abs(p.x);
    ear = ((p.y < 0.0) ? length(vec2(p.x,p.y)) - r:
    (k * (p.x + r) < p.y) ? length(vec2(p.x,p.y - k * r)):
    length(vec2(p.x + r,p.y)) - 2.0 * r) - rb;
    sd = 1.0 - smoothstep(0.0,0.001,ear) - 0.1;
    p = pos;
    p += vec2(-0.08,0.02);
    p.x = abs(p.x);
    ear = ((p.y < 0.0) ? length(vec2(p.x,p.y)) - r:
    (k * (p.x + r) < p.y) ? length(vec2(p.x,p.y - k * r)):
    length(vec2(p.x + r,p.y)) - 2.0 * r) - rb;
    sd +=  1.0 - smoothstep(0.0,0.001,ear) - 0.1;
    return sd;
}


float CatTail(in vec2 pos,in vec2 sca,in vec2 scb,in float ra,in float rb)//Arc - exact: https://www.shadertoy.com/view/wl23RK
{
    pos *= mat2(sca.x,sca.y,-sca.y,sca.x);
    pos.x = abs(pos.x);
    float k = (scb.y * pos.x > scb.x * pos.y) ? dot(pos.xy,scb):length(pos);
    float sd = sqrt(dot(pos,pos) + ra * ra - 2.0 * ra * k) - rb;
    return 1.0 - smoothstep(0.0,0.01,sd);
}

vec3 Cat(in vec2 pos)
{
    vec3 cat = vec3(0.0);
    float sd = CatBody(pos - vec2(0.0,0.0 + (0.1 * cos(iTime) / 10.0 + 0.01)));
    sd += CatEars(pos - vec2(0.0,0.18 + (0.1 * cos(iTime) / 10.0 + 0.01))) + (0.1 * sin(iTime * pos.y) / 10.0);
    float ta = 4.00 * (0.1 * cos(iTime) / 4.0 - 0.5);
    float tb = 1.80;
    float rb = 0.04 + (0.01 * cos(iTime * pos.x) / 10.0 + 0.01);
    sd += CatTail(pos - vec2(0.4,-0.4),vec2(sin(ta),cos(ta)),vec2(sin(tb),cos(tb)),0.2 + (0.1 * sin(iTime + pos.x) / 10.0),rb);
    if (sd > 0.1){
        cat = vec3(0.2 - ((pos.y - 0.1) * 2.4 + abs((pos.y + 0.2) - (pos.x + 0.3))),0.4 - ((pos.y * 1.2) - 0.2) - abs((pos.x / 2.0) + 0.2),0.3);
        cat -= vec3(0.5,0.3,0.2);
    }
    return cat;
}


