// Ray intersector for 3D paraboloid in the form: x^2/a^2 + z^2/b^2 - y = 0
vec3 IntersectParaboloid3D(in vec3 ro, in vec3 rd, in float a, in float b) {
    float invSqrA = 1.0 / (a * a);
    float invSqrB = 1.0 / (b * b);

    float ra = invSqrA * rd.x * rd.x + invSqrB * rd.z * rd.z;
    float rb = invSqrA * 2.0 * ro.x * rd.x + invSqrB * 2.0 * ro.z * rd.z - rd.y;
    float rc = invSqrA * ro.x * ro.x + invSqrB * ro.z * ro.z - ro.y;

    vec3 intersect = vec3(0.0);

    // If the discriminant is less than zero there are no (real) solutions
    float discr = rb * rb - 4.0 * ra * rc;
    if (discr > 0.0) {
        float root = sqrt(discr);
        float denom = 2.0 * ra;
        float t1 =  (root - rb) / denom; // 1st root
        float t2 = -(rb + root) / denom; // 2nd root
        intersect = vec3(t1, t2, 1.0);
    }

    return intersect;
}

vec3 getParaboloidNormal3D(in vec3 p, in float a, in float b) {
    float dx = 2.0 / (a * a) * p.x;
    float dz = 2.0 / (b * b) * p.z;
    return vec3(dx, -1.0, dz) / sqrt(dx * dx + dz * dz + 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 screenCenter = 0.5 * iResolution.xy;

    vec2 mouse = vec2(0.0, 0.0);
    if (iMouse.z > 0.0) {
        mouse = (iMouse.xy - screenCenter) / iResolution.y * 3.14;
    }

    vec2 uv = (fragCoord - screenCenter) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 3.0, 10.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float cy = cos(mouse.x), sy = sin(mouse.x);
    float cp = cos(mouse.y), sp = sin(mouse.y);
    ro.yz *= mat2(cp, -sp, sp, cp);
    ro.xz *= mat2(cy, -sy, sy, cy);
    rd.yz *= mat2(cp, -sp, sp, cp);
    rd.xz *= mat2(cy, -sy, sy, cy);

    float a = 2.25 + 2.0 * sin(iTime);
    float b = 2.25 + 2.0 * cos(iTime);
    vec3 intersect = IntersectParaboloid3D(ro, rd, a, b);
    if (intersect.z > 0.5) {
        vec3 n = getParaboloidNormal3D(ro + rd * min(intersect.x, intersect.y), a, b);
        vec3 l = normalize(vec3(-1.0, -1.0, 1.0));

        l.yz *= mat2(cp, -sp, sp, cp);
        l.xz *= mat2(cy, -sy, sy, cy);

        float lambertian = max(0.0, dot(n, l));
        fragColor.r += lambertian;
        if (lambertian > 0.0) {
            fragColor.rgb += max(0.0, pow(max(0.0, dot(reflect(l, n), rd)), 32.0));
        }
    }

    else {
        fragColor.rgb += mix(vec3(0.25, 0.25, 1.0), vec3(1.0), 0.5 + 0.5 * rd.y);
    }
}

#define MAX_STEPS 25
#define MAX_DIST 150.
#define SURF_DIST .01

float GetDistance(vec3 point) {
	vec4 sphere = vec4(2, 2, 2, 1);
    // modulate the point by 4.
    vec3 spoint = mod(point, 4.);

    float sphereDist = length(spoint-sphere.xyz)-sphere.w;    
    float planeDist = point.y;

    float d = min(sphereDist, planeDist);
    return d;
}

float RayMarch(vec3 rayOrgin, vec3 rayDirection) {
	float distance=0.;

    for(int i=0; i<(MAX_STEPS-int(sin(iTime*2.)*10.)); i++) {
    	vec3 point = rayOrgin + rayDirection * distance;
        float surfaceDistance = GetDistance(point);
        distance += surfaceDistance;
        if(distance>MAX_DIST || surfaceDistance<SURF_DIST) break;
    }

    return distance;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // put 0,0 in the center
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // camera   
    vec3 rayOrgin = vec3(0, 3.+sin(iTime)*2., mod(iTime,4.));
    vec3 rayDirection = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(rayOrgin, rayDirection);   

    // color
    vec3 col = vec3(1.-(d/20.));            
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // -1~1
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 c_uv = uv;

    //float radian = 0.785;
    float radian = fract(iTime*.01) * 3.1415926 * 2.;
    float s = sin(radian);
    float c = cos(radian);
    mat2 rotate = mat2(c, -s, s, c);
    uv *= rotate;

    // 5~25
    float zoom = 5. + (sin(iTime*.1)+1.)*10.;

    uv *= zoom;
    uv += .5;

    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    vec3 pixelColor = vec3(0.0);


    float gridLine = step(0.48, gv.x) + step(0.48, gv.y);

    float circle = 0.;

    float aniSpeed = 1.;
    float waveSpeed = .3;

    for(int i=-2;i<3;i++) {
        for(int j=-2;j<3;j++) {
            vec2 offs = vec2(j,i);
            vec2 nowId = id+offs;

            float wave = (sin(-iTime*aniSpeed+length(nowId)*waveSpeed)+1.)/2.; // 0~1;
            float cRadius = .2 + wave*1.5; // 0.2~1.7;

            float isWhite = (1. - step(cRadius, length(gv-offs)));

            // XOR
            circle = circle + isWhite - 2.*circle*isWhite;
        }
    }

    //pixelColor += circle;

    vec3 backColor = vec3(.1);
    vec3 circleColor = vec3(.5-c_uv.x,.5,.5-c_uv.y) + vec3(1.-length(c_uv),0.,0.);
    pixelColor += backColor * (1. - circle) + circleColor * circle;


    //pixelColor += gridLine;

    // Output to screen
    fragColor = vec4(pixelColor, 1.0);
}

