  const mat4 poly = mat4(-0.01554769,  1.52800768,  0.17473044, -0.12072499,
                         0.08148376, -3.49823103,  3.93686816, -1.51359643,
                         -0.11363601,  6.09961648, -9.57827611,  4.39990684,
                         0.04862359, -3.20708214,  5.6870433 , -2.79976505);
  float angle = dot(vec4(1.0, rel.y, rel.y * rel.y * vec2(1.0, rel.y)),
                    poly * 
                    vec4(1.0, rel.x, rel.x * rel.x * vec2(1.0, rel.x)));
  if (pos.x * pos.y < 0.0) {
