vec3 xform(in highp vec3 point) {
  const float h = 9.;
  // point
  point.y += h;
  point.yz = polar(point.yz);
  point.z *= 3.0 / 3.141592654;

  point.y -= h;
  point.y += 1.0;
  point.z = mod(point.z + 0.1 * iTime - 0.05 * point.y, 6.0);
  point.z *= 10.0;
  // point.z -= iTime;
  return point;
}

float waffle_sdf(in highp vec3 point) {
    point = xform(point);
	vec3 local = vec3(abs(abs(mod(point.xz, BLOCKSIZE)) - vec2(0.5 * BLOCKSIZE)), point.y).xzy;
    vec2 lr_dists = max(abs(local.xz) - 0.275 * BLOCKSIZE, 0.0);
    float lr_dist = max(0.25 * BLOCKSIZE - length(lr_dists), 0.0);
    float updist = abs(local.y + 2.0);
    return length(vec3(lr_dist, max(updist - 0.1, 0.0), 
                       max(length(point - camera) - EXTENT, 0.0))) - 0.05;
}

float blocks_sdf(in highp vec3 point) {
    point = xform(point);
	vec3 local = vec3(abs(abs(mod(point.xz, BLOCKSIZE)) - vec2(0.5 * BLOCKSIZE)),
                      abs(point.y + 1.75)).xzy;
        float samp_loc = mod(9.1 * dot(point.xz - mod(point.xz, BLOCKSIZE),
                             vec2(0.1733, 0.1321)), 1.0);

    float samp = texture(iChannel0, vec2(samp_loc, 0.0)).r;
	vec3 to_core = max(vec3(0.0), local - BLOCKSIZE * vec3(0.05, 0.1 + 2.0 * samp, 0.075));
    return max(length(to_core), 
                       max(length(point - camera) - EXTENT, 0.0)) - 0.125 * (1.0 + samp) * BLOCKSIZE;
}

float grid_sdf(in highp vec3 point) {
    return min(waffle_sdf(point), blocks_sdf(point));
}

vec3 waffle_grad(in highp vec3 point) {
    float p = waffle_sdf(point);
    return normalize(vec3(waffle_sdf(point + vec3(0.01, 0.0, 0.0)) - p,
                          waffle_sdf(point + vec3(0.0, 0.01, 0.0)) - p,
                          waffle_sdf(point + vec3(0.0, 0.0, 0.01)) - p));
}


vec3 blocks_grad(in highp vec3 point) {
    float p = blocks_sdf(point);
    return normalize(vec3(blocks_sdf(point + vec3(0.01, 0.0, 0.0)) - p,
                          blocks_sdf(point + vec3(0.0, 0.01, 0.0)) - p,
                          blocks_sdf(point + vec3(0.0, 0.0, 0.01)) - p));
}


float dist_to_grid(in highp vec3 point, in highp vec3 dir) {
	vec3 p = point;
    float result = 0.0;
    if (dir.y < -error_bound) {
	    float to_max = (point.y - waffle_max_height) / dir.y;
    	result = to_max;
    } else if (point.y > waffle_max_height + EXTENT * error_bound) {
        return 2.0 * EXTENT;
    }
    p = p + result * dir;
    for (int i = 0; i < 128; ++i) {
    	float d = 0.9 * waffle_sdf(p);
    	result += d;
        p = p + d * dir;
        if (abs(d) < error_bound || result > 2.0 * EXTENT) {
            return result;
        }
    }
    return result;
}

float dist_to_blocks(in highp vec3 point, in highp vec3 dir) {
	vec3 p = point;
    float result = 0.0;
    if (dir.y < -error_bound) {
        float to_max = (point.y - blocks_max_height) / dir.y;
        result = to_max;
    } else {
        return 2.0 * EXTENT;
    }
    p = p + result * dir;
    for (int i = 0; i < 512; ++i) {
    	float d = 0.35 * blocks_sdf(p);
    	result += d;
        p = p + d * dir;
        if (abs(d) < error_bound || result > 2.0 * EXTENT || p.y > 100.0) {
            return result;
        }
    }
    return result;
}

float dist_out_of_blocks(in highp vec3 point, in highp vec3 dir) {
	vec3 p = point;
    float result = 0.0;
    for (int i = 0; i < 64; ++i) {
    	float d = -0.9 * blocks_sdf(p);
    	result += d;
        p = p + d * dir;
        if (abs(d) < error_bound) {
            return result;
        }
    }
    return result;
}

const vec3 light1 = vec3(0.36, 0.48, -0.8);

const vec3 light2 = vec3(-0.48, 0.64, -0.6);

vec4 getSample(in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / max(iResolution.x, iResolution.y);

    vec4 color_mul = vec4(1.0);

    vec3 ray_orig = camera; // vec3(0.0, 0.0, -1.0); // sin(iTime));
    float distort = 0.85 + 0.65 * dot(uv, uv);
    vec3 ray_dir = normalize(vec3(uv * distort - vec2(0.0, 0.1), -0.2));
    ray_dir = dir_mat *
        mat3(1.0, 0.0, 0.0,
        	 0.0, 0.8, -0.6,
             0.0, 0.6, 0.8) * ray_dir;
    float d1 = dist_to_grid(ray_orig, ray_dir);
    float d2 = dist_to_blocks(ray_orig, ray_dir);
    vec3 n = ray_dir;
    const float eta = 1.85;
    vec3 subsurface = vec3(0.0);
    if (d2 < d1 && d2 < 20.0) {
        ray_orig = ray_orig + ray_dir * (d2 + 0.01);
    	n = blocks_grad(ray_orig);
        vec3 next_ray_dir = refract(ray_dir, n, eta);
        float l = length(next_ray_dir);

        if (l > 0.1) {
            ray_dir = next_ray_dir / l;
        	d2 = dist_out_of_blocks(ray_orig, ray_dir);
        	color_mul *= mix(vec4(1.0, 0.9, 0.5, 1.0), vec4(1.0),
                         exp(-2.0 * max(d2, 0.0)));
        	ray_orig = ray_orig + d2 * ray_dir;
       		n = blocks_grad(ray_orig);
        	next_ray_dir = refract(ray_dir, -n, 1.0/eta);
            l = length(next_ray_dir);
            if (l > 0.1) {
                ray_dir = next_ray_dir / l;
            }
        } else {
            ray_dir = normalize(reflect(ray_dir, n));
        }
        d1 = dist_to_grid(ray_orig, ray_dir);
        const float subsurface_dist_mul = 150.0;
#if DO_SUBSURFACE        
        float d_light_1 = dist_out_of_blocks(ray_orig, light1);
        float d_light_2 = dist_out_of_blocks(ray_orig, light2);
#else
        float d_light_1 = 1000.0 / subsurface_dist_mul;
        float d_light_2 = d_light_1;
#endif        
        subsurface = vec3(1.0 / max(2.0, subsurface_dist_mul * d_light_1),
                          0.0,
                          1.0 / max(2.0, subsurface_dist_mul * d_light_2));
        subsurface.g = 0.5 * (subsurface.b + subsurface.r);

    }
    vec3 bounce = ray_dir;
    if ( d1 < 20.0) {
        vec3 p = ray_orig + ray_dir * d1;
        n = waffle_grad(p);
        bounce = normalize(reflect(ray_dir, n));
        color_mul *= vec4(vec3(0.6, 0.6, 0.7), 1.0);
    }

    float diffuse1 = max(dot(n, light1), 0.0) + 0.1;
    float diffuse2 = 0.4 * max(dot(n, light2), 0.0) + 0.1;
    float spec1 = smoothstep(0.9, 1.0, dot(bounce, light1));
    float spec2 = smoothstep(0.8, 0.9, dot(bounce, light2));
    float mouseness = step(iMouse.y, fragCoord.y);
    return vec4(subsurface * mouseness, 0.0) +
        color_mul * vec4((diffuse1 + spec1) * vec3(1.0, 0.5, 0.0) +
                         (diffuse2 + spec2) * vec3(0.0, 0.5, 1.0), 1.0);
}

/*
vec2 offsets[8] = vec2[8](vec2(-0.4, -0.4), 
                         vec2(-0.45, 0.1),
                         vec2(-0.4, 0.4),
                         vec2(0.1, 0.45),
                         vec2(0.4, 0.4),
                         vec2(0.45, -0.1),
                         vec2(0.4, -0.4),
                         vec2(0.0, 0.0)); */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 result = vec4(0.0);

    float c_theta = cos(0.01);
    float s_theta = sin(0.01);

    camera = mat3(c_theta, 0.0, s_theta,
                  0.0, 1.0, 0.0,
                  -s_theta, 0.0, c_theta) * camera;

    dir_mat = mat3(-c_theta, 0.0, -s_theta,
                   0.0, 1.0, 0.0,
                   s_theta, 0.0, -c_theta);

    float nsamps = 0.0;

    result += getSample(fragCoord + vec2(-0.4));
    nsamps += 1.0;

    if (MULTI_SAMPLE > 1) {
	    result += getSample(fragCoord + vec2(0.1, 0.45));
    	nsamps += 1.0;
    }

    if (MULTI_SAMPLE > 2) {
	    result += getSample(fragCoord + vec2(0.45, -0.1));
    	nsamps += 1.0;
    }        

    /*
    for (int i = 0; i < 8; i += max(8 / MULTI_SAMPLE, 1)) {
        result += getSample(fragCoord + offsets[i]);
    	nsamps += 1.0;
    }
*/

    fragColor = result / nsamps;
}

#define R(a) mat2(cos(a),sin(a),-sin(a),cos(a))
void mainImage(out vec4 O, vec2 C)
{
    O-=O;
    vec3 r=iResolution,p,q,d=vec3((C-.5*r.xy)/r.y,.6);
    for(float i=0.,g,e,l,s;++i<80.;e<.001?O.xyz+=abs(cos(d+log(s)))/i:p)
    {
        s=4.;
        p=g*d;
        p.z-=.9;
        p.xy*=R(iTime*.2);
        p.yz*=R(iTime*.3);
        q=p;
        s=2.;
        for(int j=0;j++<9;)
            p-=clamp(p,-1.,1.)*2.,
            p=p*(l=8.8*clamp(.72/min(dot(p,p),2.),0.,1.))+q,
            s*=l;
        g+=e=length(p)/s;
    }

}

const bool animation_enabled = true;
// rays per pixel
const int NS = 6;
// max reflection
const int ITER = 16;

const bool light_sampling = true;
const bool box_sampling = false;

const float aperture = 0.00;
float focus_dist = 0.5;

const float EPS = .00001;
const float INF = 10000.;
const float PI = 3.14159265;

const float fov = 1.0;

const uint K = 0x456789abU;
const float uint_range = 1. / float(0xFFFFFFFFU);

void xorshift(inout uint seed) {
    seed ^= seed << 24;
    seed ^= seed >> 1;
    seed *= K;
    seed ^= seed << 1;
    seed *= K;
}

float next_float(inout uint seed) {
    xorshift(seed);
    return float(seed - 1U) * uint_range;
}

float next_float(inout uint seed, float min_v, float max_v) {
    return min_v + (max_v - min_v) * next_float(seed);
}

float atan2(float y, float x) {
    return x == 0.0 ? sign(y) * PI*.5 : atan(y, x);
}

struct Hit {
    vec3 rp;
    vec3 nv;
    vec2 uv;
    int index;
};

struct Material {
    int type;

    vec3 albedo;
    vec3 emit;

    // only metal type (0: disable)
