#define hash2(p) fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)

// --- flownoise from https://www.shadertoy.com/view/wdKXzd
// gradient noise derived from https://www.shadertoy.com/view/XdXGW8

float _z = 1.;
vec2 hash( vec2 p ) 
{
 // float s = 0.;                      // standard Perlin noise
    float s = mod(p.x+p.y,2.)*2.-1.;   // flow noise checkered rotation direction
 // float s = mod(floor(p.x)+floor(p.y),2.)*2.-1.;   // should be this but x is integer
 // float s = 1.;                      // flow noise universal rotation direction
 // s *= iTime;                        // same rotation speed at all scales
    s *= iTime/ _z;                    // rotation speed increase with small scale    

    return ( -1. + 2.*hash2(p) ) 
        *  mat2(cos( s + vec4(0,33,11,0))); // rotating gradients. rot: https://www.shadertoy.com/view/XlsyWX
}

float noise( vec2 p )
{
    vec2 i = floor( p ),
         f = fract( p ),
	  // u = f*f*(3.-2.*f);
         u = f*f*f* ( 10. + f * ( -15. + 6.* f ) ); // better for derivative. from http://staffwww.itn.liu.se/~stegu/TNM022-2005/perlinnoiselinks/paper445.pdf

#define P(x,y) dot( hash( i + vec2(x,y) ), f - vec2(x,y) )
    return mix( mix( P(0,0), P(1,0), u.x),
                mix( P(0,1), P(1,1), u.x), u.y);
}

#define noise01(p) 1.5 * ( .5+.5* noise(p)-.18 )    // well fitted to [0,1] without saturation

float perlin( vec2 p )                              // fractal noise
{	
    mat2 m = mat2(2.); // mat2( 1.6,  1.2, -1.2,  1.6 );
    float v  = 0.,s = 1.;
    for( int i=0; i < 5; i++, s /= 2. ) { _z = s;   // for flownoise
        v += s*noise01( p ); p *= m;                // variant using positive noise
    }
    return v;
}

// --- sampling inspired from https://www.shadertoy.com/view/lljczz

#define srnd(p,s) ( 2.* fract( 4e4* sin( 1e3* mat2(1,7.1,-5.1,2.3) * (p) + 1e3*s )) -1. )

// #define f(U)   noise01(U)
// #define f(U)   noise01(ofs(U))
   #define f(U) ( noise01(ofs(U)) + .5*noise01(2.*ofs(U)) ) / 1.2
// #define f(U)   perlin(U) / 1.5     
// #define f(U)   perlin(ofs(U)) / 1.5
#define ofs(U) ( U *vec2(1,2) + vec2(.3*iTime,0) )

void mainImage( out vec4 O,  vec2 u )
{
    vec2 R = iResolution.xy,
         U = (2.*u-R)/R.y, p, P, X,V, x;
    O-=O;       

    float v, n =   11., // R.y/32., // 11.                // number of cells (optim)
          l = .08,                                        // stokes length
       // d = 90.*pow((1.-U.y)/2.,1.7)*R.y/360.;          // strokes density
       //   d = 90.*pow(max(f(U)-.25 -.0*U.y,0.),1.7)*R.y/360.;
          d = 90.*pow(max(2.*(f(U)-.5) -.5*U.y,0.),1.7)*R.y/360.;
    int N = 3; // int(3.*R.y/360.);                       // Neighborhood size = NxN . goes with l

    p = floor(U*n+.5) / n;                                // cell center (NB: we should deffered /n )

    for( int i=0; i<N*N; i++)                             // Worley optim. allows overflow to neighbor cell
      for( float j=0.; j<d; j++) {                        // density loop
        P = p + vec2( i%N -N/2, i/N -N/2 ) / n;           // cell coordinate in neighborhood
        P +=     .5* srnd( round(P*n) , j) / n;           // jittering (round: for precision issues)
        X = U-P,                                          // local coordinate
        V; // =  f(.5*U);    // f(.5*P)                   // local field. f(U): curvy f(P): tangent 
        v = f(U); // v = V.x;
        V = vec2(  dFdx(v), dFdy(v) ) * R.y;              // divergence-free  ( since we trace ortho(V) )  
     // V = vec2( -dFdy(v), dFdx(v) ) * R.y;              // divergence-free  ( if we were tracing V )  

        v = dot ( X , V );                                // distance to segment
     // v /= fwidth(v);
        v /= length(V) * 2.5/R.y;
        O += // (1.-O)*                                    // blend ( comment out for add )
             .25* max( 0., 1. - 1.3*abs(v) )               // draw segment of dir V and length .04
               * clamp( ( l - length(X) ) *R.y ,0.,1.);
}

 // O -=  max(0., length(U)-.8 ) *R.y;                    // white out of circle
    O = sqrt(1.-O*vec4(1,1,.5,0));                        // reverse + to sRGB
}

// https://www.desmos.com/calculator/t1iqt8tj9z?lang=fr

#define MIN_DISTANCE   0.001
#define MAX_DISTANCE   20.
#define MAX_ITERATIONS 200


#define MATERIAL_SPHERE1   1  
#define MATERIAL_SPHERE2  2
#define MATERIAL_GROUND 3


#define PI 3.14159
#define DEG2RAD 0.01745329251

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

/**
* m : index du matriau  appliquer
* d : distance  la surface observe
*/
struct Data {
    float d;
    int m;
};


/**
* Calcul la distance signe en entre le point p et la surface de la sphre
* p : point courant calcul par rapport  la sphre
* r : rayon de la sphre
* m : matriau  appliquer sur la surface de la sphre
*/
Data sdSphere(vec3 p, float r, int m) {
    return Data(length(p) - r, m);
}

/**
* Calcule la distance signe entre le point p et la surface du plan de hauteur h
* p : point courant calcul par rapport  la sphre
* h : hauteur du plan par rapport  l'origine du monde
* m : matriau  appliquer sur la surface du plan
*/
Data sdPlanH(vec3 p, float h, int m) {
    return Data(p.y - h, m);
}

Data sdBox(vec3 p, vec3 b, int m) {
    vec3 q = abs(p) - b;
    float d = min(max(q.x,max(q.y,q.z)),0.0) + length(max(q,0.0));
    return Data(d, m);
}

Data sdTorus(vec3 p, vec2 t, int m) {
    vec2 q = vec2(length(p.xz)-t.x,p.y);
    float d = length(q)-t.y;
    return Data(d, m);
}



Data sdCylinder( vec3 p, float h, float r, int m)
{
  vec2 q = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  float d = min(max(q.x,q.y),0.0) + length(max(q,0.0));
  return Data(d, m);
}


Data minData(Data d1, Data d2) {

    if(d1.d < d2.d) {
        return d1;
    }
    return d2; 
}

Data maxData(Data d1, Data d2) {

    if(d1.d > d2.d) {
        return d1;
    }
    return d2; 
}

Data minusData(Data d) {
    return Data(-d.d, d.m);
}

Data scene(vec3 p) {

    vec3 cp1 = p - vec3(0,0,0);
    cp1.xy *= Rot(90.0*DEG2RAD);

    Data c1 = sdCylinder(cp1, 0.35, 1.5, MATERIAL_SPHERE2);


    vec3 cp2 = p - vec3(0,0,0);
    Data c2 = sdCylinder(cp2, 0.35, 1.5, MATERIAL_SPHERE2);


    vec3 cp3 = p - vec3(0,0,0);
    cp3.yz *= Rot(90.0*DEG2RAD);

    Data c3 = sdCylinder(cp3, 0.35, 1.5, MATERIAL_SPHERE2);


    Data pl = sdPlanH(p, -1., MATERIAL_GROUND);



    Data s1 = sdSphere(p, 1.41421356237, MATERIAL_SPHERE2);  

    vec3 bp1 = p - vec3(0, 0.0, 0);
    Data b1 = sdBox(bp1, vec3(1.0), MATERIAL_SPHERE1);



    Data cr = minData(c1, c2);
    cr = minData(cr, c3);



    //s = minData(b1, pl);
    // Data s = minData(cr, b1);

    Data s = maxData(b1, s1);
    s = maxData(minusData(cr), s);

    s = minData(s, pl);


    return s;
}

Data rayMarch(vec3 rO, vec3 rD) {

    float d = 0.0;
    int m = 0;
    for(int i=0; i < MAX_ITERATIONS; i++) {

        vec3 p =  rO + rD * d;
        Data ds =  scene(p);
        d += ds.d;
        m = ds.m;
        if (ds.d < MIN_DISTANCE || d > MAX_DISTANCE) break;
    }

    return Data(d, m);

}


vec3 material(int index) {

    vec3 col = vec3(0);

    switch(index) {
        case MATERIAL_SPHERE1:
            col = vec3(0.8, 1.1, 5);
            break;
        case MATERIAL_SPHERE2:
            col = vec3(1.0, 0.2, 0.3);
            break;
        case MATERIAL_GROUND:
            col = vec3(1.0, 1.0, 0);
        default:
            break;
    }

    return col * 0.2;

}

vec3 normal(vec3 p) {
    float dp = scene(p).d;

    float eps = 0.01;

    float dx = scene(p + vec3(eps, 0, 0)).d - dp;
    float dy = scene(p + vec3(0, eps, 0)).d - dp;
    float dz = scene(p + vec3(0, 0, eps)).d - dp;

    return normalize(vec3(dx, dy, dz));
}


float lighting(vec3 p, vec3 n, vec3 lp) {

    vec3 ld = lp - p;
    vec3 ln = normalize(ld);

    float d = rayMarch(p + n * 0.01, ln).d;

    if (d < length(ld)) return 0.0;

    return max(0.0, dot(ln, n));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5* iResolution.xy) / iResolution.y;

    vec2 mouse = iMouse.xy / iResolution.xy;


    float k = 4.5;
    float t = 0.1;
    vec3 lp = abs(vec3(cos(iTime*t)*k, 2.0, sin(iTime*t)*k));


    //vec3 camera = vec3(0, mouse.y, 0);
    //vec3 screen = vec3(uv, 1.0);

    float phi = -DEG2RAD * -60.0;
    float fov = 1.0;
    float r = 4.0;

    vec3 eye = vec3(
        cos(2.0 * PI * mouse.x + phi) * r,
        mouse.y * 10., 
        sin(2.0 * PI * mouse.x + phi)* r
    );



    vec3 target = vec3(0,0,0);

    // base vectorielle
    vec3 forward = normalize(target-eye) ;
    vec3 side = cross(vec3(0,1,0), forward);
    vec3 up = cross(forward, side);

    vec3 screenPos = eye + fov*forward + uv.x * side + uv.y * up;

    vec3 rD = normalize(screenPos - eye);

    Data data =  rayMarch(eye, rD);

    // ciel
    vec3 skyColDark = vec3(0.08, 0.3, 1.0);
    vec3 skyColLight = vec3(0.5, 0.8, 1.0);

    vec3 col = mix(skyColLight, skyColDark, pow(uv.y + 0.5, 1.5));

    if(data.d < MAX_DISTANCE) {

       vec3 p = eye + rD * data.d;
       vec3 n = normal(p);
       float l = lighting(p, n, lp);

       vec3 m = material(data.m);

       col = m * l;

       vec3 ambiant = vec3(5, 0, 10) * 0.07;
       vec3 ambiantSky = n.y * skyColLight;

       col = col * ( ambiant + ambiantSky + l);

    }

    // gamma 
    col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}

uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
const uint max32 = 0xffffffffu;
vec2 fragCoord_;
vec2[8] gtable2 = vec2[](   //0.92387953 = cos(pi/8), 0.38268343 = cos(pi/8)
    vec2(0.92387953, 0.38268343),
    vec2(0.38268343, 0.92387953),
    vec2(-0.92387953, 0.38268343),
    vec2(-0.38268343, 0.92387953),
    vec2(0.92387953, -0.38268343),
    vec2(0.38268343, -0.92387953),
    vec2(-0.92387953, -0.38268343),
    vec2(-0.38268343, -0.92387953)
);
uint hash11u(uint n) {
    n ^= (n << 24);
    n ^= (n >> 1);
    n ^= (n << 1);
    return n * k.x;
}
vec2 hash22(vec2 p) {
        uint n = hash11u(hash11u(uint(p.x)) + uint(p.y));
        n = n >> 29;
        n = n % 8u;
        return gtable2[n];
}
float pnoise21(vec2 p) {
    vec2 f = fract(p);
    vec2 g00 = hash22(p);
    vec2 g01 = hash22(p + vec2(0.0, 1.0));
    vec2 g10 = hash22(p + vec2(1.0, 0.0));
    vec2 g11 = hash22(p + vec2(1.0, 1.0));
    float v00 = dot(g00, f);
    float v01 = dot(g01, f - vec2(0.0, 1.0));
    float v10 = dot(g10, f - vec2(1.0, 0.0));
    float v11 = dot(g11, f - vec2(1.0, 1.0));
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f); 
    float v = mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y);
    return 0.5 * v;
}

float hash21(vec2 p) {
    uint n = hash11u(uint(p.x)) + uint(p.y);
    return float(hash11u(n)) / float(max32);
}
float vnoise21(vec2 p) {
    vec2 i = floor(p);
    float v00 = hash21(i);
    float v01 = hash21(i + vec2(0.0, 1.0));
    float v10 = hash21(i + vec2(1.0, 0.0));
    float v11 = hash21(i + vec2(1.0, 1.0));
    vec2 f = fract(p);
    f = f * f * (3.0 -2.0 * f); 
    return mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y) - 0.5;
}

float fbm21(vec2 p, float G){
    float val = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    for (int i = 0; i < 4; i++){
        if (fragCoord_.x < 0.5 * iResolution.x){
            val += amp * pnoise21(freq * p);
        } else{
            val += amp * vnoise21(freq * p);
        }
        amp *= G;
        freq *= 2.01;
    }
    return val;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    fragCoord_ = fragCoord;
    vec2 pos = fragCoord.xy/min(iResolution.x, iResolution.y);
    pos *= 20.0;
    pos += iTime;
    float G = abs(mod(0.2 * iTime, 1.0) - 0.5) + 0.5;
    float v = 0.5 * fbm21(pos, G) + 0.5;
    fragColor = vec4(vec3(v), 1.0);
}

/*
 * Similar to my first two shaders of a similar name, but this one
 * effectively ray marches the perimeter of the voxel using circles
 * to check if the sdf lies inside the voxel to a given degree of accuracy (the size of a pixel).
 *
 * It also does the checks in a slightly larger are than the voxel to catch edge cases where an sdf resolves to
 * less than minstep size between two voxels.
 */

#define gridSize1 2.0
#define gridSize2 10.0
#define gridSize3 30.0
#define gridSize4 60.0

float cornerRadiusMultiplier = 3.-2./sqrt(2.)-sqrt(2.);

float sdBox( vec2 uv, vec2 position, in vec2 bounds )
{
    vec2 d = abs(position - uv)-bounds;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdCircle(vec2 uv, vec2 pos, float radius) {
    return length(uv - pos) - radius;
}

vec2 pixelSize() {
    return 2.0 * ((vec2(iResolution.x, iResolution.y) - (0.5*iResolution.xy))/iResolution.y) / iResolution.xy;
}

float sdf(vec2 uv) {

    vec2 pixelSize = pixelSize();

    float screenEdgeMargin = 0.1;

    float radius1 = 0.7 * (1.0 + sin(iTime * 0.6));
    vec2 position1 = vec2(sin(iTime * 0.2) * (iResolution.x / iResolution.y) * (1. - screenEdgeMargin), sin(iTime * 0.10) * (1. - screenEdgeMargin));
    float circle1 = sdCircle(uv, position1, radius1);   

    float radius2 = 0.4 * (1.0 + sin(iTime * 0.1));
    vec2 position2 = vec2(sin(iTime * 0.4) * (iResolution.x / iResolution.y) * (1. - screenEdgeMargin), sin(iTime * 0.8) * (1. - screenEdgeMargin));
    float circle2 = sdCircle(uv, position2, radius2);      

    // 1/10th the size of a pixel circles
    float radius3 = 0.1 * min(pixelSize.x, pixelSize.y); 
    vec2 position3 = vec2(sin(iTime * 0.6) * (iResolution.x / iResolution.y) * (1. - screenEdgeMargin), sin(iTime * 0.6) * (1. - screenEdgeMargin));
    float circle3 = sdCircle(uv, position3, radius3);   

    float radius4 = 0.1 * min(pixelSize.x, pixelSize.y); 
    vec2 position4 = vec2(sin(iTime * 0.8) * (iResolution.x / iResolution.y) * (1. - screenEdgeMargin), sin(iTime * 0.4) * (1. - screenEdgeMargin));
    float circle4 = sdCircle(uv, position4, radius4);   

    return min(min(min(circle1, circle2), circle3), circle4);
}

bool insideBoundingSquare(vec2 position, vec2 bound) {

    vec2 pixelSize = pixelSize();

    // We'll check just outside of the square to catch cases where the sdf resolves in sub-pixel level between
    // two squares. This means we potentially treat a square as inside/touching when it isn't but better than
    // artefacts due to this scenario.
    vec2 adjustedBound = bound + pixelSize;

    // If all points inside the grid's outer circle are negative, then we're definitely inside.
    float midpointD = sdf(position);
    if (midpointD < -length(adjustedBound)) {
        return true;
    } else if (abs(midpointD) < min(adjustedBound.x, adjustedBound.y)) {
        // There is an sdf edge inside the inner circle, so must not be completely inside.
        return false;
    }


    // For each corner,
    for (float x = 0.; x < 2.; x++) {
        for (float y = 0.; y < 2.; y++) {
            vec2 cornerDirection = normalize(vec2(-1.0 + 2. * x, -1.0 + 2.0 * y)); // -1.0, -1.0 to 1.0, 1.0
            float r = adjustedBound.x; // Set radius of to radius of inner circle of square.
            float distFromMidpoint = r; // Total distance from midpoint to corner circle midpoint.

            // Keep shrinking the corner circle until we reach pixel size (or return a value inside).
            while (r >= min(pixelSize.x, pixelSize.y)) {

                // Calculate size of inscribed circle in corner.
                r = cornerRadiusMultiplier * r;
                distFromMidpoint += r; // Move to midpoint of new circle
                vec2 cornerCircleMidpoint = position + cornerDirection * distFromMidpoint;
                distFromMidpoint += r; // Move to edge of circle for next circle

                // We march in a clock-wise direction around the perimeter.
                float posOrNeg = 1. - x*2.; // 1 or -1
                float applyX = abs(x-y); // 1 or 0
                float applyY = abs(applyX - 1.); // 1 or 0
                vec2 edgeDirection = vec2(applyX * posOrNeg, applyY * posOrNeg);

                // March along the edge.
                for (float i = r; i < adjustedBound.x - r; i = i + r) {
                    vec2 midpoint = cornerCircleMidpoint + i * edgeDirection;
                    if (sdf(midpoint) > r) {
                        return false;
                    }
                }
            }
        }
    }


    return true;
}

bool touchingBoundingSquare(vec2 position, vec2 bound) {

    vec2 pixelSize = pixelSize();

    // We'll check just outside of the square to catch cases where the sdf resolves in sub-pixel level between
    // two squares. This means we potentially treat a square as inside/touching when it isn't but better than
    // artefacts due to this scenario.
    vec2 adjustedBound = bound + pixelSize;

    // If all points inside the grid's outer circle are outside,
    // then we're definitely outside and not touching.
    float midpointD = sdf(position);
    if (midpointD > length(adjustedBound)) {
        // Doesn't touch outer circle, so must be completely outside sdf volume.
        return false;
    } else if (midpointD < min(adjustedBound.x, adjustedBound.y)) {
        // Touches inner circle so must be inside square.
        return true;
    }

    // For each corner,
    for (float x = 0.; x < 2.; x++) {
        for (float y = 0.; y < 2.; y++) {
            vec2 cornerDirection = normalize(vec2(-1.0 + 2. * x, -1.0 + 2.0 * y)); // -1.0, -1.0 to 1.0, 1.0
            float r = adjustedBound.x; // Set radius of to radius of inner circle of square.
            float distFromMidpoint = r; // Total distance from midpoint to corner circle midpoint.

            // Keep going until we reach pixel size or exit condition.
            while (r >= min(pixelSize.x, pixelSize.y)) {

                // Calculate size of inscribed circle in corner.
                r = cornerRadiusMultiplier * r;
                distFromMidpoint += r; // Move to midpoint of new circle
                vec2 cornerCircleMidpoint = position + cornerDirection * distFromMidpoint;
                distFromMidpoint += r; // Move to edge of circle for next circle
                float posOrNeg = 1. - x*2.; // 1 or -1
                float applyX = abs(x-y); // 1 or 0
                float applyY = abs(applyX - 1.); // 1 or 0
                vec2 edgeDirection = vec2(applyX * posOrNeg, applyY * posOrNeg);
                // March along the edge.
                for (float i = r; i < adjustedBound.x - r; i = i + r) {
                    vec2 midpoint = cornerCircleMidpoint + i * edgeDirection;
                    if (sdf(midpoint) <= r) {
                        return true;
                    }
                }
            }
        }
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelSize = pixelSize();

    // Normalized pixel coordinates (from y = -1 to 1 with 0,0 in the center of the screen)
    vec2 uv = 2.0 * (fragCoord - (0.5*iResolution.xy))/iResolution.y;

    vec2 minScreenExtent = 2.0 * (vec2(0,0) - (0.5*iResolution.xy))/iResolution.y;
    vec2 maxScreenExtent = 2.0 * (iResolution.xy - (0.5*iResolution.xy))/iResolution.y;

    // Calculate SDF
    float d = sdf(uv);

    float gridSize;
    if (uv.x > minScreenExtent.x + 3. * (maxScreenExtent.x - minScreenExtent.x) / 4.) {
        gridSize = gridSize4;
    } else if (uv.x > minScreenExtent.x + 2. * (maxScreenExtent.x - minScreenExtent.x) / 4.) {
        gridSize = gridSize3;
    } else if (uv.x > minScreenExtent.x + 1. * (maxScreenExtent.x - minScreenExtent.x) / 4.) {
        gridSize = gridSize2;
    } else {
        gridSize = gridSize1;
    }

    // Bounding Box
    vec2 voxelMidpoint = vec2((floor(uv.x * gridSize) / gridSize) + (0.5 / gridSize), (floor(uv.y * gridSize) / gridSize) + (0.5 / gridSize));
    vec2 voxelSize = vec2(0.5 / gridSize, 0.5 / gridSize);
    vec2 minExtent = voxelMidpoint - voxelSize;
    vec2 maxExtent = voxelMidpoint + voxelSize;
    float box1 = sdBox(uv, voxelMidpoint, voxelSize);

    // Work out if the SDF is inside the box or not.
    bool touching = touchingBoundingSquare(voxelMidpoint, voxelSize);
    bool inside = false;
    if (touching) {
        inside = insideBoundingSquare(voxelMidpoint, voxelSize);
    }

    bool outside = !touching;

    vec3 statusColor = vec3(1.0, 1.0, 0.0);
    if (inside) {
        statusColor = vec3(0.0, 1.0, 0.0);
    } else if (outside) {
        statusColor = vec3(1.0, 0.0, 0.0);
    }

    // Set base colour depending on inside or outside bounding box.
    vec3 baseColor = vec3(0.1,0.4,0.7);
    if (uv.x > minExtent.x && uv.x < maxExtent.x && uv.y > minExtent.y && uv.y < maxExtent.y) {
        baseColor = vec3(0.8,0.2,0.2);
    }

    // Taken from iq to visualize sdf distances (https://www.shadertoy.com/view/4lcBWn)    
    vec3 col = vec3(1.0) - sign(d)*baseColor;
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.008,abs(d)));

    // Add the grid
    col = mix( col, statusColor, 1.0-smoothstep(0.0,0.008,abs(box1)) );

    // Output to screen
    fragColor = vec4(col,1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    vec3 gradient = vec3(1.-length(uv), 0.3, 0.4);
    float ray = mod(length(uv) - iTime * .2, .3);
    vec3 col = vec3(ray) * gradient;
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359


float noise(float x) {
    return fract(sin(x)*345.456);
}

float noise(vec2 x) {
    return fract(sin(dot(x, vec2(2234.456, 1567.789)))*423.234);
}

// smooth gradient noise
float grNoise(float x, float k) {

    x *= k;

    // get integer / fractional parts
    float i = floor(x);
    float f = fract(x);

    // smooth interpolation
    float n1 = noise(i); 
    float n2 = noise(i+1.);
    return mix(n1, n2, smoothstep(0., 1., f));
}

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

float noiseLine(vec2 uv) {
    uv.y -= grNoise(uv.x + iTime, 2. + sin(iTime*0.002));

    return smoothstep(0.02, 0., uv.y) -
            smoothstep(0., -0.02, uv.y);

}

float sineLine(vec2 uv) {
    uv.y += .6;
    uv.x += iTime / .9;
    uv.y -= sin(uv.x * 6.) / 5.;

    return smoothstep(0.02, 0., uv.y) -
            smoothstep(0., -0.02, uv.y);

}

float axis(vec2 uv) {
    float y = smoothstep(0.005, 0., uv.y) -
            smoothstep(0., -0.005, uv.y);
    float x = smoothstep(0.005, 0., uv.x) -
            smoothstep(0., -0.005, uv.x);
    return x + y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.y *= iResolution.y / iResolution.x;

    uv *= 5.;

    vec3 col = vec3(0.);



    col = vec3(axis(uv));
    col.r = noiseLine(uv);
    col.b = sineLine(uv);



    fragColor = vec4(col,1.0);
}

// Cole Peterson

#define R iResolution.xy
#define ss(a, b, t) smoothstep(a, b, t)
#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float sub(float d1, float d2){return max(-d1, d2);}

float sdBox( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z))-.04,0.0);
}

float hash12(vec2 p){
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

void trn(inout vec3 p){
    p.yz*=rot(-0.8);
    p.xy*=rot(0.475);
    p.x += iTime + 50.;
}

#define b vec3(5., 5., 0.)

float map(vec3 rp){
    float d = 999.;
    vec3 p = rp-vec3(0., -5., 9.);
    vec3 pp = p;
    trn(pp);

    for(float i = 0.; i < 2.; i++){
        p = pp;
        vec3 id = floor(p / max(b, 0.0001));
        p.z += i*-5.;

        float rnd = hash12(i*33.+id.xy*vec2(333., 588.));
        p = mod(p, b)-b*.5;

        p.xz *= rot(sign(rnd-0.5)*.8*iTime+id.x+id.y);
        p.xy *= rot(sign(rnd-0.5)*.8*iTime+id.x+id.y);

        d = min(sdBox(p, vec3(1.6 - rnd*0.2, 1.6 - rnd*0.4, 1.0 + rnd*0.9)), d);
        if(rnd > 0.4) d=sub(length(p)-1.68, d);
    }

    return d;
}


vec3 normal( in vec3 pos ){
    vec2 e = vec2(0.002, -0.002);
    return normalize(
        e.xyy * map(pos + e.xyy) + 
        e.yyx * map(pos + e.yyx) + 
        e.yxy * map(pos + e.yxy) + 
        e.xxx * map(pos + e.xxx));
}


void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;
    vec3 rd = normalize(vec3(0., 0.0, 1.));
    vec3 ro = vec3(uv * 12., 0.);

    float d = 0.0, t = 0.0, ns = 0.;
    for(int i = 0; i < 44; i++){
    	d = map(ro + rd*t); 
        if(abs(d) < 0.01 || t > 40.)break;
        t += d * .55;
        ns++;
    }

    vec3 p = ro + rd*t;
    vec3 n = normal(p);

    vec3 lp = vec3(-2., 37.0, -30.);
    vec3 ld = normalize(lp-p);

    float dif = max(dot(n, ld), .3);
    vec3 col = .43 + .3*cos(vec3(0.5, 2., 1.1)*(n.x+n.y+n.z)*1.2 + vec3(2., 1.8, 0.2));

    col *= max(ss(1.85, 0.6, ns/24.), 0.03) * dif;

    col = mix(vec3(0.0), col, exp(-t*t*t*0.0001));
    col = pow(col*1.4, vec3(1.85));

    col*=ss(.42, .419, abs(uv.y));

    f = vec4(sqrt(clamp(col, .0, 1.)), 1.);
}

#define MAX_STEPS 200
#define MAX_DIST 100.
#define SURFACE_DIST 0.01
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r, float offX, float offY, float offZ) {
	a.x += offX;
    b.x += offX;

    a.y += offY;
    b.y += offY;

    a.z += offZ;
    b.z += offZ;

    vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);

    vec3 c = a + t*ab;

    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);

    return e+i;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r){
    //line from a to b
    vec3 ab = b-a;

    // line from p to a
    vec3 ap = p-a;

    // as p approaches a, it will be 1
    // as p approaches b it will b 0
    float t = dot(ab,ap) / dot(ab,ab);

    // clamp values between 0 and 1
    t= clamp(t,0.,1.);

    // center point of the capsule
    // start at a (top of capsule)
    // go towards b, t steps
    vec3 c = a + t*ab;

    float d = length(p-c)-r;

    return d;
}

float sdTorus(vec3 p, vec2 r){
    float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;
}

float dBox(vec3 p, vec3 s){
    return length(max(abs(p)-s,0.));
}
float sdGyroid(vec3 p, float scale, float thickness, float bias) {
	p *= scale;
    return abs(dot(sin(p), cos(p.zxy))-bias)/scale-thickness;
}
float GetDist(vec3 p){

    float g1 = sdGyroid(p, 10., .03, 1.);
    float g2 = sdGyroid(p, 10.76, .03, .3);
    float g3 = sdGyroid(p, 20.76, .03, .3);
    float g4 = sdGyroid(p, 35.76, .03, .3);
    float g5 = sdGyroid(p, 60.76, .03, .3);
    float g6 = sdGyroid(p, 110.76, .03, .3);
    g1 -= g2*.4;
    g1 -= g3*.3;
    g1 += g4*.2;
    g1 += g5*.2;
    g1 += g6*.3;
    vec2 uv = vec2(sin(p.x*4.)*.5+.5,cos(p.z*3.)*.5+.5);
    float disp = texture(iChannel0,uv).r;
    g1-=disp*.05*sin(iTime);
    float box = dBox(p, vec3(5));
    float d = max(g1,box);

    return d;
}
float RayMarch(vec3 ro, vec3 rd){
    // distance from origin
    float dO=0.;
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO*rd;

        // get distance to seam
        float ds = GetDist(p);
        //move origin to new point
        dO+=ds*.1;
        if(ds < SURFACE_DIST || dO > MAX_DIST){
            break;
        }
    }
    return dO;
}
vec3 GetNormal(vec3 p){
    //distance to point being analyzed
    float d = GetDist(p);

    //distance to another point along the objects surface that is closeby
    vec2 e = vec2(0.01,0);

    //slope between the two points
    //note: swizzel is the .xxy or .yyx etc
    vec3 n = d - vec3(
         GetDist(p-e.xyy),
         GetDist(p-e.yxy),
         GetDist(p-e.yyx));

    return normalize(n);

}
float GetLight(vec3 p){
    //position of the light source
    vec3 lightPos = vec3(0,0,0);

    lightPos.xz *= Rot(sin(iTime));
    //light vector
    vec3 l = normalize(lightPos-p+vec3(4,0,0));

    //normal of object
    vec3 n = GetNormal(p);

    // dot product of the light vector and normal of the point
    // will give us the amount of lighting to apply to the point
    // dot() evaluates to values between -1 and 1, so we will clamp it
    float diff = clamp(dot(n, l),0.,1.);

    // calculate if point should be a shadow:
    // raymarch from point being calculated towards light source
    // if hits surface of something else before the light,
    // then it must be obstructed and thus is a shadow
    // the slight offset "p+n*SURFACE_DIST*1.1" is needed to ensure the
    // break condistions in the function are not met too early
    float d = RayMarch(p+n*SURFACE_DIST*1.1,l);
    if(d < length(lightPos-p)){
        diff *= 0.1;
    }
    return diff;
}
vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    // origin is now (0,0)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 col = vec3(0);

    //camera
    //-----------------
    // ray origin
    //controls position
    vec3 ro = vec3(0.,-0.1,sin(iTime*.1));
  //  ro.yz *= Rot(-m.y*3.14+1.);
   // ro.xz *= Rot(sin(iTime)*.6+3.);
   // ro.yz *= -Rot(sin(iTime)*.1+5.);

    //ray dir
    //controls rotation
    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);
    rd.xz *= Rot(sin(iTime)*.6+3.);
    //------------------
    float d = RayMarch(ro,rd);
    // position of point
    // start at ray origin
    // move d units in the ray direction
    vec3 p = ro + rd *d;



    //color objects
    if(d < MAX_DIST){

        //get normals
        vec3 n = abs(GetNormal(p));


        float g1 = sdGyroid(p, 5.23, .03, 1.4);
        float g2 = sdGyroid(p, 10.76, .03, .3);
        float g3 = sdGyroid(p, 20.76, .03, .3);
        float g4 = sdGyroid(p, 35.76, .03, .3);
        float g5 = sdGyroid(p, 60.76, .03, .3);
        float g6 = sdGyroid(p, 110.76, .03, .3);
        g1 -= g2*.4;
        g1 -= g3*.3;
        g1 += g4*.2;
        g1 += g5*.2;
        g1 += g6*.3;
        vec2 uv = vec2(sin(p.x*4.)*.5+.5,cos(p.z*3.)*.5+.5);
        float disp = texture(iChannel0,uv).r;
        g1-=disp*.05;
        float box = dBox(p, vec3(4));
        float d = max(g1,box);
        col = d*vec3(1)*50.;
        col = max(col,disp*vec3(1,0,0)*3.);
        col = max(col,g2*vec3(0,1,0)*50.);

        col *= smoothstep(-.1, .1, g2);	// blackening
        //get diffuse lighting
        float diff = GetLight(p);
        col *= vec3(diff);


    }
    fragColor = vec4(col,1.0);
}

