			vec2 rp = mod((uv.xy+vec2(x,y)*.777*.5/screenY)/(.4+time*.2)+100.5,.6)-.3,
				ap = vec2(atan(rp.y,rp.x)/3.14,length(rp)*2.357022);
			s += max(0.,1.-tree(ap,.9+time,0.01)*(1e-5+ap.y)*1000.);
