vec3 Rect(vec2 cord, float len, float wid)
{

	if(abs(tan(cord.x)) <= len/wid)
        if (cord.y <= wid/abs(cos(cord.x)))
            return vec3(1., 1., 0.);
        else return vec3(.54, .6, 1.);
    else
       if (cord.y <= len/abs(sin(cord.x)))
           return vec3(.5, 1., 0.);
        else return vec3(.6, .75, 1.);

}

