float geo(vec3 p) {
	float d = 1.;
	vec3 fp = floor(p);
	for (float x = -1.; x < 2.; x++) {
		for (float y = -1.; y < 2.; y++) {
			for (float z = -1.; z < 2.; z++) {
				vec3 ap = abs(fp+vec3(x,y,z));
				float h = fract(.5+dot(ap,vec3(1.,128.,2048.))/1.6180339887498948482), yp = clamp(1.1-ap.y*.1,0.,1.), bprob = 20./ap.z*yp;
				yp *= .5;
				if (h > bprob) d = min(d,length(max(abs(p-(fp+vec3(x,y,z)+.5))-yp,0.))-(.5-yp));
			}
		}
	}
	return max(p.y,d);
}

