    return max(distA, distB);
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}

float sceneSDF(vec3 samplePoint) {
    float sphereDist = sphereSDF(samplePoint / 1.2) * 1.2;
    //float cubeDist = cubeSDF(samplePoint) * 1.2;
    return unionSDF(samplePoint.y, sphereDist);
}

float RayMarch2(vec3 eye, vec3 viewRayDirection){

    float depth = 0.0, end = 10.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        float dist = GetDist2(eye + depth * viewRayDirection);
        if (dist < SURF_DIST) {
            // We're inside the scene surface!
            return depth;
        }
        // Move along the view ray
        depth += dist;

        if (depth >= MAX_DIST) {
            // Gone too far; give up
            return MAX_DIST;
        }
    }
    return end;


}

vec3 GetNormal(vec3 p){
    float d = GetDist2(p);
    vec2 e = vec2(0.01, 0);

    vec3 n = d - vec3(
        GetDist2(p-e.xyy),
        GetDist2(p-e.yxy),
        GetDist2(p-e.yyx));

    return normalize(n);
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

float GetLight(vec3 p){
    vec3 lightpos = vec3(2.0, 5, -6);
    lightpos.xz += vec2(sin(iTime), cos(iTime));
    vec3 l = normalize(lightpos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch2(p+n*SURF_DIST, l);
    if(d < length(lightpos-p))dif *= 0.1;
    return dif ;

}






