#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define DF(a,b) length(a) * cos( mod( atan(a.y,a.x)+6.28/(b*8.0), 6.28/((b*8.0)*0.5))+(b-1.)*6.28/(b*8.0) + vec2(0,11) )

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdPie( in vec2 p, in vec2 c, in float r )
{
    p.x = abs(p.x);
    float l = length(p) - r;
    float m = length(p-c*clamp(dot(p,c),0.0,r)); // c = sin/cos of the aperture
    return max(l,m*sign(c.y*p.x-c.x*p.y));
}

float SimpleVesicaDistance(vec2 p, float r, float d) {
    p.x = abs(p.x);
    p.x+=d;
    return length(p)-r;
}

vec3 bamboo(vec2 p, vec3 col, float brightness){
    vec2 prevP = p;

    float d = sdBox(p-vec2(0.0,-0.2),vec2(0.033,0.2));
    col = mix(col,vec3(0.6,0.7,0.5)*brightness,S(d,0.0));    

    p.x*= 1.5;
    d = length(p)-0.05;
    col = mix(col,vec3(0.6,0.7,0.5)*brightness,S(d,0.0));
    p = prevP;
    p.x*= 1.5;
    d = length(p)-0.035;
    col = mix(col,vec3(0.7,0.8,0.1)*1.2,S(d,0.0));
    return col;
}

vec3 hey(vec2 p, vec3 col) {
    vec2 prevP = p;

    float d = sdBox(p,vec2(0.1,0.1));
    col = mix(col,vec3(0.8,0.7,0.1),S(d,0.0));    

    p.x = mod(p.x,0.022)-0.011;
    d = sdBox(p,vec2(0.005,0.1));

    p = prevP;
    float d2 = sdBox(p,vec2(0.1,0.11));
    d = max(d2,d);
    col = mix(col,vec3(0.7,0.6,0.1),S(d,0.0));
    p = prevP;

    d = sdBox(p-vec2(0.0,-0.05),vec2(0.1,0.005));
    col = mix(col,vec3(0.6,0.4,0.1),S(d,0.0)); 

    return col;
}

vec3 grass(vec2 p, vec3 col) {
    vec2 prevP = p;
    float k = 0.01;
    float d = length(p)-0.05;
    p.x = abs(p.x);
    p.x-=0.05;
    float d2 = length(p-vec2(0.0,-0.03))-0.05;
    d = opSmoothUnion(d,d2,k);
    p.x-=0.05;
    d2 = length(p-vec2(0.0,-0.07))-0.05;
    d = opSmoothUnion(d,d2,k);
    p.x+=0.06;
    d2 = length(p-vec2(0.0,-0.06))-0.05;
    d = opSmoothUnion(d,d2,k);
    col = mix(col,vec3(0.1,0.4,0.1),S(d,0.0)); 
    return col;
}

vec3 flower(vec2 p, vec3 col) {
    vec2 prevP = p;
    p = DF(p,1.25);
    p -= vec2(0.019);
    float d = length(p)-0.027;
    col = mix(col,vec3(0.7,0.2,0.2),S(d,0.0));
    p = prevP;

    p = DF(p,1.25);
    p -= vec2(0.015);
    d = length(p)-0.005;
    col = mix(col,vec3(1.0),S(d,0.0));

    return col;
}

vec3 leaf(vec2 p, vec3 col) {
    vec2 prevP = p;
    float d = SimpleVesicaDistance(p,0.08,0.062);
    p.x = abs(p.x);
    p.x-=0.03;
    p.y-=0.015;
    float d2 = SimpleVesicaDistance(p*Rot(radians(-50.0)),0.08,0.061);
    d = min(d,d2);
    col = mix(col,vec3(0.3,0.8,0.3),S(d,0.0));
    return col;
}

vec3 fan(vec2 p, vec3 col)
{
    const vec2 an = vec2(sin(0.98),cos(0.98));

    float d = sdPie(p,an,0.1/0.8);
    col = mix(col,vec3(1.0,0.0,0.0),S(d,0.0));

    d = sdPie(p,an,0.07/0.8);
    col = mix(col,vec3(1.0),S(d,0.0));

    d = length(p-vec2(0.0,0.04/0.8))-0.015/0.8;
    col = mix(col,vec3(0.9,0.9,0.1),S(d,0.0));

    return col;
}

vec3 clouds(vec2 p, vec3 col) {
    vec2 prevP = p;

    float d = length(p)-0.35;
    col = mix(col,mix(vec3(1.0),vec3(0.5,0.7,0.9),p.y+0.3),S(d,0.0));

    p.y*=1.1;
    p.x+=iTime*0.1;
    p.x=mod(p.x,0.8)-0.4;
    p.y-=0.15;

    d = sdBox(p,vec2(0.16,0.005))-0.03;
    float d2 = sdBox(p-vec2(-0.04,0.125),vec2(0.05,0.005))-0.03;
    d = min(d,d2);
    d2 = sdBox(p-vec2(-0.04,0.06),vec2(0.03,0.05));
    d = opSmoothUnion(d,d2,0.02);
    d2 = sdBox(p-vec2(0.12,-0.125),vec2(0.1,0.005))-0.03;
    d = min(d,d2);
    d2 = sdBox(p-vec2(0.1,-0.06),vec2(0.03,0.05));
    d = opSmoothUnion(d,d2,0.02);

    p = prevP;
    d2 = length(p)-0.35;
    d = max(d2,d);

    col = mix(col,vec3(0.5,0.7,0.9)*1.9,S(d,0.0));

    return col;
}

vec3 soil(vec2 p, vec3 col) {
    p.x*=0.4;
    p.y*=2.0;
    float d = length(p)-0.07;
    col = mix(col,vec3(0.7,0.5,0.2),S(d,-0.03));

    return col;
}

vec3 render(vec2 p, vec3 col) {
    vec2 prevP = p;
    float t = iTime;
    col = clouds(p,col);

    col = soil(p-vec2(0.0,-0.4),col);
    col = bamboo(p-vec2(0.0,0.1),col,0.8);
    p.x = abs(p.x);
    p.x-=0.05;
    col = bamboo(p,col,1.0);
    p = prevP;
    col = hey(p-vec2(0.0,-0.3),col);
    col = grass(p-vec2(0.0,-0.125),col);
    col = fan((p-vec2(-0.05,-0.23))*Rot(radians(-25.0)),col);
    col = leaf((p-vec2(-0.06,-0.27))*Rot(radians(30.0)),col);
    col = flower((p-vec2(0.0,-0.22))*Rot(radians(-30.0*t)),col);
    col = flower((p-vec2(0.07,-0.125))*Rot(radians(25.0*t)),col);

    return col;
}

