struct Sphere
{
    vec2 pos;
    float radius;
    vec3 colour;
};
Sphere newSphere(vec2 p, float r, vec3 c)
{
    Sphere s;
    s.pos = p;
    s.radius = r;
    s.colour = c;
    return s;
}
struct Ring
{
    vec2 pos;
    vec3 colour;
    float radius;
    float thickness;
};
Ring newRing(vec2 p, float r, float t, vec3 c)
{
    Ring ri;
    ri.pos = p;
    ri.radius = r;
    ri.thickness = t;
    ri.colour = c;
    return ri;
}

struct Line
{
    vec2 a;
    vec2 b;
    float thickness;
    float l;
    vec3 colour;
};
Line newLine(vec2 a, vec2 b, float t, vec3 c)
{
    Line l;
    l.a = a;
    l.b = b;
    l.thickness = t;
    l.l = length(a-b);
    l.colour = c;
    return l;
}
float dstToLine(Line l, vec2 c)
{
	float s1 = -l.b.y + l.a.y;
	float s2 = l.b.x - l.a.x;
	return abs((c.x - l.a.x) * s1 + (c.y - l.a.y) * s2) / sqrt(s1*s1 + s2*s2);
}

bool inLine(Line l, vec2 p)
{
    return (p.x-l.thickness < max(l.a.x,l.b.x) && p.x+l.thickness > min(l.a.x,l.b.x) && p.y-l.thickness < max(l.a.y,l.b.y) && p.y+l.thickness > min(l.a.y,l.b.y) && dstToLine(l,p) < l.thickness);
}

bool inSphere(vec2 point, Sphere s)
{
    return length(point - s.pos) < s.radius;
}
bool inRing(vec2 point, Ring r)
{
    return !inSphere(point, newSphere(r.pos, r.radius, r.colour)) && inSphere(point, newSphere(r.pos, r.radius+r.thickness, r.colour));
}
void DrawSphere(inout vec3 col, vec2 p, Sphere s)
{
    if (inSphere(p, s))
    {
        col = s.colour;
    }
}
void DrawLine(inout vec3 col, vec2 p, Line l)
{
    if (inLine(l, p))
    {
        col = l.colour;
    }
}
void DrawRing(inout vec3 col, vec2 p, Ring r)
{
    if (inRing(p, r))
    {
        col = r.colour;
    }
}
const float PI = 3.14159265359;

vec2 angle(float a, float l)
{
    return vec2(sin(a), cos(a)) * l * iResolution.x/5.0f;
}
void DrawMarks(inout vec3 col, vec2 p, vec2 middle)
{
    for (int i = 0; i < 12; i++)
    {
        float thisAngle = PI*2.0f * (float(i) / 12.0f);
        vec2 thisRot = angle(thisAngle, 1.0f);
        Line thisL = newLine(middle+thisRot*0.9f, middle+thisRot, 1.0f, vec3(0,0,0));
        DrawLine(col, p, thisL);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    Sphere a = newSphere(iResolution.xy/2.0f,iResolution.x / 5.0f, vec3(1,1,1));

    DrawSphere(col, fragCoord, a);

    vec2 middle = iResolution.xy / 2.0f;

    float secondx = PI*2.0f * (iDate.w/60.0f);
    vec2 secondrot = angle(secondx, 0.9f);
    Line secondl = newLine(middle, vec2(middle +secondrot), 3.0f, vec3(1,0,0));
    DrawLine(col, fragCoord, secondl);

    float minx = PI*2.0f * (iDate.w/60.0f/60.0f);
