float sceneSDF( vec4 pos )
{
    // SDF of sphere with radius 1
    return sdfMandelbulb((rotate(iTime / 3.0) * pos).xyz / 2.0) * 2.0;
}

float raymarch( in float start, in float end, in vec4 eyePos, in vec4 viewRay ) 
{
    float depth = start;

    for (int i = 0; i < MAX_STEPS; i++) 
    {
        float dist = sceneSDF(eyePos + depth * viewRay);

        if (dist < EPSILON) 
        {
            return depth;
        }

        depth += dist;

        if (depth >= end)
        {
            return end;
        }
    }

    return end;
}

vec4 estimateNormal(vec4 p) 
{
    return normalize(vec4(
        sceneSDF(vec4(p.x + EPSILON, p.y, p.z, p.w)) - sceneSDF(vec4(p.x - EPSILON, p.y, p.z, p.w)),
        sceneSDF(vec4(p.x, p.y + EPSILON, p.z, p.w)) - sceneSDF(vec4(p.x, p.y - EPSILON, p.z, p.w)),
        sceneSDF(vec4(p.x, p.y, p.z + EPSILON, p.w)) - sceneSDF(vec4(p.x, p.y, p.z - EPSILON, p.w)),
        sceneSDF(vec4(p.x, p.y, p.z, p.w + EPSILON)) - sceneSDF(vec4(p.x, p.y, p.z, p.w - EPSILON))
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec4 eye = vec4(0, 0, -4, 0);
    float aspect = iResolution.x / iResolution.y;
    vec4 ray = normalize(vec4(((fragCoord.xy / iResolution.xy) - vec2(0.5)) * vec2(aspect, 1), 1, 0));

    float march = raymarch(EPSILON, 100.0, eye, ray);

    if (march > 100.0 - 1.0) 
    {
        fragColor = vec4(0, 0, 0, 1);
        return;
    }

    const vec3 surfaceColor = vec3(0.5, 0.5, 1.0);
    const float ambientLight = 0.2;
    vec4 lightPos = vec4(0.0, 2.0, -4.0, 0.0);

    vec4 collisionPoint = eye + march * ray;
    vec4 normal = estimateNormal(collisionPoint);
    vec4 toLight = normalize(lightPos - collisionPoint);
    float ndotl = dot(normal, toLight);
    vec3 color = surfaceColor * clamp(ndotl, ambientLight, 1.0);
    fragColor = vec4(color, 1.0);
}

#define PI 3.1415926535
#define HALF_PI 1.5707963268
#define TWO_PI 6.2831853072
#define FIVE_PI_SQUARED 49.348022005

float cheapSin(in float x) {
    float nx = mod(x, PI);
    float PI_MINUS_X = PI - nx;
    float y = (16.0 * nx * PI_MINUS_X) / (FIVE_PI_SQUARED - 4.0 * nx * PI_MINUS_X);
    return mod(x, TWO_PI) < PI ? y : -y;
}

float cheapCos(in float x) {
    return cheapSin(x + HALF_PI);
}

float cheapTan(in float x) {
    return cheapSin(x) / cheapCos(x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 16.0 / iResolution.y;

    uv.x += iTime;

    fragColor = vec4(smoothstep(unit, 0.0, abs(uv.y - cheapSin(uv.x))), 0.0, 0.0, 1.0);
    fragColor.b += smoothstep(unit, 0.0, abs(uv.y - cheapCos(uv.x)));
    fragColor.g += smoothstep(unit, 0.0, abs(uv.y - cheapTan(uv.x)));
}

float distFuncCube(vec3 p){
	vec3 q=abs(p);
	return length(max(q-vec3(0.5,0.2,0.9),0.0));
}

float distFuncSphere(vec3 p){
	float s=0.5;
	return length(p)-s;
}


vec2 pmod(vec2 p,float n){
	float np=3.141592*2.0/n;
	float r=atan(p.x,p.y)-0.5*np;
	r=mod(r,np)-0.5*np;
	return length(p)*vec2(cos(r),sin(r));
}


