void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 p=(2.0*fragCoord.xy-iResolution.xy)/min(iResolution.x,iResolution.y);

	float radius=0.1;
	float phi=iTime*0.2;
	vec3 ro=vec3(cos(phi)*radius,0.0,(sin(phi)*radius));
	vec3 ta=vec3(0.0);

	vec3 cDir=normalize(ta-ro);
	vec3 cSide=cross(cDir,vec3(0.0,1.0,0.0));
	vec3 cUp=cross(cSide,cDir);
	float fov=0.6;

	vec3 rd=normalize(p.x*cSide+p.y*cUp+cDir*fov);
	float d,t=0.0;

	float emission=0.0;

	for(int i=0;i<48;i++){
		d=distFunc(ro+rd*t);
		emission+=exp(abs(d)*-0.2);
		if(d<0.001)break;
		t+=d;
		}

	vec3 col=0.03*emission*vec3(0.3,sin(iTime*0.5),cos(iTime*0.5));

	fragColor=vec4(col,1.0);
}

#define MAX_STEPS_MARCH 64
#define SPHERE_RADIUS 1.0
#define COLOR vec3(0.8, 0.5, 1.0)

float raymarch(vec3 ro, vec3 rd, vec3 lv, float g)
{
    float t = 0.0;
    for(int x = 0; x < MAX_STEPS_MARCH; x++)
    {
        vec3 pos = ro + rd * t;
        float d = length(pos) - SPHERE_RADIUS;
        if(d < 0.001)
        {
            float l = dot(normalize(lv), normalize(pos));
            return max(l, 0.1);
        }

        t += d;
    }

    return g;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // aspect ratio for correction rays..
    float a = iResolution.x / iResolution.y;

    // pseudo perspective pyramid (directions rays)
    vec2 dTr = vec2(1.0, 1.0);
    vec2 dTl = vec2(-1.0, 1.0);
    vec2 dBr = vec2(1.0, -1.0);
    vec2 dBl = vec2(-1.0, -1.0);

    // common uv ray direction
    vec3 rd = vec3(mix(mix(dBl, dBr, uv.x),
                       mix(dTl, dTr, uv.x), uv.y),
                   1.0);
    rd.x *= a;

    // origin direction (or pseudo camera position)
    vec3 ro = vec3(0.0, 0.0, -2.0);
    // light vector
    vec3 lv = vec3(sin(iTime), 0.5, cos(iTime));
    // background gradient
    float g = pow(1.0 - distance(uv, vec2(0.5)), 2.0) * 0.7;

    vec3 rgb = vec3(COLOR * raymarch(ro, rd, lv, g));

    // Output to screen
    fragColor = vec4(rgb, 1.0);
}

