vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}

float modMirror1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize,size) - halfsize;
  p *= mod(c, 2.0)*2.0 - 1.0;
  return c;
}

float smoothKaleidoscope(inout vec2 p, float sm, float rep) {
  vec2 hp = p;

  vec2 hpp = toPolar(hp);
  float rn = modMirror1(hpp.y, TAU/rep);

  float sa = PI/rep - SABS(PI/rep - abs(hpp.y), sm);
  hpp.y = sign(hpp.y)*(sa);

  hp = toRect(hpp);

  p = hp;

  return rn;
}

float height(vec2 p, vec2 c, float hh) {
  p*=mix(1.5, 0.5, TIMEINPERIOD/PERIOD);
  p+=mix(0.2, 0.0, TIMEINPERIOD/PERIOD);
  p*=ROT(TIMEINPERIOD*0.05);
  vec2 op = p;
  float l2 = L2(p);
  float rep = 2.0*round(mix(3.0, 30.0, hh*hh));
  float s = 0.05*6.0/rep;
  float n = smoothKaleidoscope(p, s, rep);
  p *= ROT(TAU*hh);
  vec2 u = p;

  float a = 0.0;


  for (int i = 0; i < 13; ++i) {
    float m = dot(u, u);
    u = SABS(u, 0.025)/m + c;
  }

  float h = L2(u-p);
  h = log(h-hh);
  h = abs(h);
  h = tanh_approx(.45*h*h+0.125/(l2+0.125));

  return h;
}

vec3 normal(vec2 p, vec2 c, float hh) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(4.0/RESOLUTION.y, 0);

  vec3 n;
  n.x = height(p + e.xy, c, hh) - height(p - e.xy, c, hh);
  n.y = 2.0*e.x;
  n.z = height(p + e.yx, c, hh) - height(p - e.yx, c, hh);

  return normalize(n);
}

vec3 hsv2rgb(vec3 c) {
  const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 postProcess(vec3 col, vec2 q)  {
  col=pow(clamp(col,0.0,1.0),vec3(1.0/2.2)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // saturation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  const float s = 1.25;
  const vec3 lp1 = s*vec3(1.0, 2.25, 1.0)*vec3(s, 1.0, s);
  const vec3 lp2 = s*vec3(-1.0, 2.25, 1.0)*vec3(s, 1.0, s);

  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  float hh = HPERIOD;

  vec3 col = vec3(0.0);
  vec2  c = vec2(-0.4-0.2*fract(7.0*hh), -0.4-0.2*fract(13.0*hh));

  float h = height(p, c, hh);
  vec3  n = normal(p, c, hh);

  vec3 ro = vec3(0.0, -10.0, 0.0);
  vec3 pp = vec3(p.x, 0.0, p.y);

  vec3 po = vec3(p.x, h, p.y);
  vec3 rd = normalize(po - ro);

  vec3 ld1 = normalize(lp1 - po);
  vec3 ld2 = normalize(lp2 - po);

  float l = length(p);

  vec3 hsv1 = vec3(hh+mix(0.0, 0.2, PSIN(0.3*TIME-2.0*l+(p.x+p.y))), tanh_approx(h*h), tanh_approx(3.0*h*h));
  hsv1.yz = clamp(hsv1.yz, 0.0, 1.0);
  vec3 col1 = hsv2rgb(hsv1);
  vec3 col2 = pow(col1, vec3(0.25, 0.5, 2.0));

  float diff1 = max(dot(n, ld1), 0.0);
  float diff2 = max(dot(n, ld2), 0.0);

  vec3  ref   = reflect(rd, n);
  float ref1  = max(dot(ref, ld1), 0.0);
  float ref2  = max(dot(ref, ld2), 0.0);

  col += col1*mix(0.1, 1.0, pow(diff1, 8.0))*0.5;
  col += col2*mix(0.1, 1.0, pow(diff2, 8.0))*0.5;
  col = pow(col, vec3(1.5));
  col += 8.0*col1*pow(ref1, 40.0);
  col += 4.0*col2*pow(ref2, 10.0);

  col = tanh(2.5*col);
  col = postProcess(col, q);

  float fadeIn  = smoothstep(0.0, FADE, TIMEINPERIOD);
  float fadeOut = 1.0-smoothstep(PERIOD-FADE, PERIOD, TIMEINPERIOD);
  col = mix(vec3(0.0), col, fadeIn*fadeIn*fadeOut*fadeOut);

  fragColor = vec4(col, 1.0);
}

#define PI 3.14159
#define hue(v) ( .6 + .6 * cos( 6.3*(v) + vec4(0,23,21,0) ) )
#define hash21(p) fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)
#define hash33(p) fract(sin( (p) * mat3( 127.1,311.7,74.7 , 269.5,183.3,246.1 , 113.5,271.9,124.6) ) *43758.5453123)

bool insideTriangle(vec2 a, vec2 b, vec2 c, vec2 p) {
    vec2 q = inverse(mat2(b-a, c-a))*(p-a);
    return q.x >= 0. && q.y >= 0. && q.x+q.y <= 1.;
}

vec3 colorize (vec3 r) {
    int c = int(r.z*5.);
    if (c == 0) return vec3(38, 70, 83)/255.;
    if (c == 1) return vec3(42, 157, 143)/255.;
    if (c == 2) return vec3(233, 196, 106)/255.;
    if (c == 3) return vec3(244, 162, 97)/255.;
    /* c == 4*/ return vec3(231, 111, 81)/255.;
}

vec4 render(vec2 U) {

    vec2 R = iResolution.xy, pw=1./R, uv=U*pw, z=1.04*(2.0*U-R)/R.y;

    vec2 a = vec2(2./sqrt(3.), -1.), b = vec2(0,1), c = vec2(-2./sqrt(3.), -1.);
    vec2 tmp;
#define SWAP(_v, _w) {tmp=_v; _v=_w; _w=tmp;}

    if (!insideTriangle(a,b,c,z)) {
        return vec4(0);
    }

    float t = fract(iTime/40.);
    float iterations = .3+14.*(.5-abs(t-.5));
    float fill = 1.;
    vec3 rand = hash33(vec3(float(fill), 0, 0));
    vec3 prevRand;
    vec4 oldColor, newColor;
    for (int i = 0; i < 10; i++) {
        if (float(i) >= floor(iterations)+1.) continue;
        if (i == 0) {
            oldColor = vec4(0);
            newColor = vec4(colorize(rand), 1.);
        } else {
            oldColor = newColor;
            if (rand.x > .66) {
                SWAP(a, c);
            } else if(rand.x > .33){ 
                SWAP(a,b);
            }
            float div = .25+.5*rand.y+.1*sin(iTime);
            vec2 n = mix(b, c, div);
            fill *= 2.;
            if (insideTriangle(a,b,n,z)) {
                c = n; fill+=1.;
            } else {
                b = n;
            }
            prevRand = rand;
            rand = hash33(vec3(float(fill), 0, 0));
            newColor = vec4(colorize(rand), 1.0);
        }
    }

    vec2 center = (a+b+c)/3.;
    float maxDist = max(
        distance(a,center),
        max(distance(b,center), distance(c,center)));
    float f = distance(z, center) / maxDist;
    float transition = clamp(0.,1.,.5+3.*(fract(iterations)-.5));
    if (f < transition) {
        return newColor;
    } else {
        return oldColor;
    }
}
void mainImage( out vec4 O, in vec2 U ) {
    vec2 d = vec2(.5,0);
    O = (
        render(U+d.xy)+
        render(U-d.xy)+
        render(U+d.yx)+
        render(U-d.yx)
    )*.25;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cc = 127.5;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float x = (fragCoord.x)+iResolution.x/2.0;
    float y = (fragCoord.y)+iResolution.y/2.0;

    // Time varying pixel color

    float t = sin(iTime)+0.5;
    float layer1 = cc + (cc * sin(x / t));
    float layer2 = cc + (cc * cos(y + x / t));
    float layer3 = cc + (cc * sin(sqrt(x * x + y * y) / t));

    float c = (layer3 + layer2 + layer1) / 3.0;

    vec3 col =vec3(c/256.0, 0,0); 

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float PI = 3.141592658;
const float TWOPI = 2.0 * PI;

// pseudo-random function, returns value between [0.,1.]
float rand (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(31.7667,14.9876)))
                 * 833443.123456);
}

//bilinear value noise function
float bilinearNoise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners of a 2D square
    float f00 = rand(i);
    float f10 = rand(i + vec2(1.0, 0.0));
    float f01 = rand(i + vec2(0.0, 1.0));
    float f11 = rand(i + vec2(1.0, 1.0));

    vec2 u = smoothstep(0.,1.,(1.-f));
	return u.x*u.y*f00+(1.-u.x)*u.y*f10+
    u.x*(1.-u.y)*f01+(1.-u.x)*(1.-u.y)*f11;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 uv =fragCoord.xy/ iResolution.xy;
  //from Cartesian to polar coordinates
  float scale=3.;
  uv=fract(scale*uv)-.5;
  float radius = length(uv);
  float angle = atan(uv.y, uv.x);
  //change Nsections using horizontal movement of mouse
    float Nsections=20.;//-15.*iMouse.x/iResolution.x;
  //create Nsections which are identical
  float angleM = mod(angle, TWOPI/Nsections);
  //make each section symmetric along its bisector
  angleM = abs(angleM -PI/Nsections);
  //back to Cartesian coordinates
  uv = radius*vec2(cos(angleM),sin(angleM));
  uv=sin(uv+.04*iTime);
  vec3 color=vec3(bilinearNoise(100.*uv),bilinearNoise(75.*uv),bilinearNoise(50.*uv));
  fragColor = vec4(color,1.);
}

#define MAX_STEPS 1000
#define MAX_DIST 1000.0
#define SMALL_DIST 0.01

//color setting
vec3 sphereCol = vec3(0.98,0.89,0.32);
vec3 planeCol = vec3(0.86, 0.3, 0.43);
vec3 fogCol = vec3(1.0, 1.0, 1.0);
float fogdepth = 11.0;

//----------------------------------
float DistSphere(vec3 p, vec4 sphere) {
    return length(p - sphere.xyz) - sphere.w;
}

vec4 GetDist(vec3 p) {
    vec3  m = vec3(0.0,0.5,7.0);


    vec4 sphere0 = vec4(m.x, m.y , m.z, 0.5); 
    sphere0.xz += 2.0*vec2(sin(2.0 * iTime), 2.0 * cos(iTime)); 

    float DistPlane = p.y;
    float Sphere = DistSphere(p, sphere0);
    float minimum = min(Sphere, DistPlane);

    if (minimum == Sphere) {
        return vec4(sphereCol, Sphere);
    }
    return vec4(planeCol, DistPlane);
}

float RayMarch (vec3 RayOri, vec3 RayDi) {
    float DistOrig = 0.0;
    for (int i = 0; i < MAX_STEPS; ++i) {
        vec3 p = RayOri + DistOrig * RayDi;
        float dist = GetDist(p).w;
        DistOrig += dist;
        if (dist < SMALL_DIST || DistOrig > MAX_DIST) break;
    }
    return DistOrig;
}

vec3 GetColour(vec3 p) {
    vec3 col = vec3(0.0);
    for (int i = 0; i < 1; ++i) {
        vec3 amb = GetDist(p).xyz * 0.9;
        col += (amb);
    }

    float fog = 1.0-clamp(length(p) / 13.0, 0.0, 1.0); //method 1
    fog = exp(-0.22*length(p));//method 2 beer-lambert
	col = fogCol * (1.0 - fog) + fog * col;
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.0, 0.0, 0.0);
    vec3 RayOri = vec3(0, 1, 0);
    vec3 RayDi = normalize(vec3(uv.x, uv.y, 1));
    float d = RayMarch(RayOri, RayDi);
    vec3 p = RayOri + RayDi * d;
    col = GetColour(p);
    fragColor = vec4(col, 1.0);
}

