float sdPyramid( in vec3 p, in float h )
{
    float m2 = h*h + 0.25;

    // symmetry
    p.xz = abs(p.xz);
    p.xz = (p.z>p.x) ? p.zx : p.xz;
    p.xz -= 0.5;

    // project into face plane (2D)
    vec3 q = vec3( p.z, h*p.y - 0.5*p.x, h*p.x + 0.5*p.y);

    float s = max(-q.x,0.0);
    float t = clamp( (q.y-0.5*p.z)/(m2+0.25), 0.0, 1.0 );

    float a = m2*(q.x+s)*(q.x+s) + q.y*q.y;
	float b = m2*(q.x+0.5*t)*(q.x+0.5*t) + (q.y-m2*t)*(q.y-m2*t);

    float d2 = min(q.y,-q.x*m2-q.y*0.5) > 0.0 ? 0.0 : min(a,b);

    // recover 3D and scale, and add sign
    return sqrt( (d2+q.z*q.z)/m2 ) * sign(max(q.z,-p.y));;
}

// la,lb=semi axis, h=height, ra=corner
float sdRhombus(vec3 p, float la, float lb, float h, float ra)
{
    p = abs(p);
    vec2 b = vec2(la,lb);
    float f = clamp( (ndot(b,b-2.0*p.xz))/dot(b,b), -1.0, 1.0 );
	vec2 q = vec2(length(p.xz-0.5*b*vec2(1.0-f,1.0+f))*sign(p.x*b.y+p.z*b.x-b.x*b.y)-ra, p.y-h);
    return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}

//------------------------------------------------------------------

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

//------------------------------------------------------------------

#define ZERO (min(iFrame,0))

//------------------------------------------------------------------
float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}
vec2 sdGyroid(vec3 p, float scale, float thickness, float bias) {
	p *= scale;
    return vec2(abs(dot(sin(p), cos(p.zxy))-bias)/scale-thickness,15.);
}
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}
// http://iquilezles.org/www/articles/smin/smin.htm
vec2 smin( vec2 a, vec2 b, float k )
{
    float h = clamp( 0.5+0.5*(b.x-a.x)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// http://iquilezles.org/www/articles/smin/smin.htm
float smax( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*0.25/k;
}
vec2 cactus (vec3 pos){
    //add cactus
    vec2 uv4 = vec2(atan(pos.x,pos.z)/(3.14159*1.),pos.y/1.);
    float disp4 = texture(iChannel1,uv4).r;
    vec2 cactus = sdCapsule(pos, vec3(10,0,0), vec3(10,1,0), .2 );
    vec2 thorn = sdCapsule(pos, vec3(10,0,0), vec3(10,1,0), .14 );
    thorn.x-=disp4*.1;
    thorn.y = 17.;
    cactus = opU(cactus,thorn);

    vec3 nP = pos;
    nP.y += (sin(nP.x*4.) * .1 * smoothstep(1.1,1.2,nP.x));
    vec2 cactusR = sdCapsule(nP, vec3(10,.5,0), vec3(10.5,.5,0), .13 );
    vec2 thornR = sdCapsule(nP, vec3(10,.5,0), vec3(10.5,.5,0), .08 );

    thornR.x-=disp4*.1;
    thornR.y = 17.;
    cactusR = opU(cactusR,thornR);
    cactus.x = smin(cactusR,cactus, .1).x;
    return cactus;
}
vec2 map( in vec3 pos )
{
    vec2 res = vec2( 1e10, 0.0 );

    //get rock displacement
    vec2 uv = vec2(pos.z*.4, pos.y*.4);
    float disp = texture(iChannel0,uv).r;

    vec2 uv2 = vec2(pos.x*.1, pos.z*.1);
    float disp2 = texture(iChannel0,uv2).r;

    vec2 uv3 = vec2(pos.x*.3, pos.z*.3);
    float disp3 = texture(iChannel1,uv3).r;




    //add floor to scene
    vec2 floorP = vec2(pos.y,20.);
    //apply rocky displacement to floor
    floorP.x-=disp2*.1;
    res = opU(floorP,res);

    //wall
    vec2 wall= sdBox( pos-vec3(0,1,0), vec3(.5,1,10) );
    wall.x-=disp*.3;
    //cut hole in wall for door
    vec2 wallMask = sdCapsule(pos, vec3(0,-.5,0), vec3(0,.7,0), 1. );
    wall.x = differenceSDF(wall.x, wallMask.x);
    res = opU(wall,res);

    //path
    vec3 nP = pos;
    nP.z += sin(nP.x *.4);
    vec2 path = sdBox( nP-vec3(0,0,-.1), vec3(10.,.1,1.3) );
    path.x-=disp2*.2;
    path.y = 26.;
    res = opU(path,res);

    //gate
    vec2 bars = sdCylinder(pos, vec3(.5,0,0), vec3(.5,2,0), .01);
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,-.1), vec3(.5,2,-.1), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,-.2), vec3(.5,2,-.2), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,-.3), vec3(.5,2,-.3), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,-.4), vec3(.5,2,-.4), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,-.5), vec3(.5,2,-.5), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,-.6), vec3(.5,2,-.6), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,-.7), vec3(.5,2,-.7), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,-.8), vec3(.5,2,-.8), .01)));

    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,.1), vec3(.5,2,.1), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,.2), vec3(.5,2,.2), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,.3), vec3(.5,2,.3), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,.4), vec3(.5,2,.4), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,.5), vec3(.5,2,.5), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,.6), vec3(.5,2,.6), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,.7), vec3(.5,2,.7), .01)));
    bars =abs(opU(bars,sdCylinder(pos, vec3(.5,0,.8), vec3(.5,2,.8), .01)));

    res = opU(bars,res);

    //plants
   /* vec3 pPos = pos-vec3(1.3,0,1.3);
    pPos.xz *= Rot(iTime);
    pPos.xz -= sin(pPos.y*4.+.4)*.4+.4;

    vec2 bush = sdCylinder( pPos, vec2(.01-.01*pPos.y,.3) );
    bush.y = 90.;
    res = opU(bush,res);*/

    vec2 grass = vec2(pos.y,90.);
    grass.x-=disp3*.1;
    res = opU(grass,res);

    //flag
     //add flag
    vec3 flagP = pos - vec3(1.,0,1);
    vec2 pole = sdCylinder(flagP, vec2(.01,1.4) );
    res = opU(pole,res);
    //add cloth
    flagP.x += (sin(flagP.z + iTime*5.)*.1) * smoothstep(.1,1.1,flagP.z);
    vec2 cloth = sdBox( flagP-vec3(0,1.1,0.5), vec3(.01,.3,.5) );
    cloth.y = 91.;
    //cloth.x -=disp3*.03;
    res = opU(cloth,res);

    //second flag
    flagP.xz *= Rot(3.145);
    flagP = flagP - vec3(0,0,3);
    pole = sdCylinder(flagP, vec2(.01,1.4) );
    res = opU(pole,res);
    //add cloth
    flagP.x += (sin(flagP.z + iTime*5.)*.1) * smoothstep(.1,1.1,flagP.z);
    cloth = sdBox( flagP-vec3(0,1.1,0.5), vec3(.01,.3,.5) );
    cloth.y = 91.;
    res = opU(cloth,res);

    //guardian
    vec2 guard = sdSphere( pos-vec3(2,1,0), 0.2);
    guard = smin(guard,sdSphere( pos-vec3(2,.6,0), 0.3),.3);
    vec2 guardMask = sdCapsule(pos, vec3(2.1,1.1,.05), vec3(2.1,1.1,-.1), .2 );
    guard = max(-guardMask, guard);

    //add arms
    //shoulder
    vec2 arms = sdSphere( pos-vec3(2.18,1.,0.25 + sin(iTime)*.01), 0.13);
    guard = smin(guard,arms,.1);
    guard.y = 100.;
    //arm
    vec2 arm = sdCapsule(nP, vec3(2.18,1,1.), vec3(2.2,.8+ sin(iTime)*.01,1.2), .08 );


    vec2 hand = sdSphere( pos-vec3(2.5,.8+ sin(iTime)*.01,0.4),.04);
    arm = smin(arm,hand,.4);
    arm.y = 26.;
    res = opU(arm,res);

    //left
    //shoulder
    vec2 armsL = sdSphere( pos-vec3(2.18,1.+ sin(iTime)*.01,-0.25), 0.13);
    guard = smin(guard,armsL,.1);
    guard.y = 100.;
    //arm
    vec2 armL = sdCapsule(nP, vec3(2.18,1,.48), vec3(2.2,.8+ sin(iTime)*.01,.38), .08 );
    vec2 handL = sdSphere( pos-vec3(2.5,.8+ sin(iTime)*.01,-0.4+cos(iTime)*.07),.04);
    armL = smin(armL,handL,.4);
    armL.y = 26.;
    res = opU(armL,res);
    //spear
    vec2 spear = sdCylinder(pos-vec3(2.5,.8+ sin(iTime)*.01,-0.4+cos(iTime)*.07), vec2(.01,.5) );
    vec3 bladeP = pos;
    bladeP.x -= sin(bladeP.y*4.-.2);
    vec2 blade = sdBox( bladeP-vec3(3.5,1.3+ sin(iTime)*.01,-0.4+cos(iTime)*.07), vec3(.1,.1,.014*(1./(pos.y*4.))) );
    res = opU(spear,res);
    blade.y = 26.;
     res = opU(blade,res);
    //head
    vec3 headPos = pos;
    headPos.y -= sin(iTime)*.05+.05;
    vec2 head = sdSphere( headPos-vec3(2.13,1.13,0), 0.2);
    head = smin(guard,head,.04);
    head.y = 100.;
    res = opU(head,res);

    //helmet
    vec2 helmet = sdSphere( headPos-vec3(2.13,1.13,0), 0.22);
    head = sdSphere( headPos-vec3(2.13,1.13,0), 0.15);
    helmet.x = max(helmet.x,-head.x);
    vec2 helmetM = sdBox( headPos-vec3(02.2,0.85,0), vec3(.3,.2,.3) );

    helmet.x = max(helmet.x,-helmetM.x);

    vec2 helmetM2 = sdBox( headPos-vec3(02.2,0.85,0), vec3(.4,.4,.01 *headPos.y*2.) );
    helmet.x = max(helmet.x,-helmetM2.x);

    vec2 helmetM3 = sdBox( headPos-vec3(02.17,1.2,0), vec3(.2,.01 * abs(headPos.z)*40.+.01,.1));
    helmet.x = max(helmet.x,-helmetM3.x);
    helmet.y = 26.;
    res = opU(helmet,res);
    return res;
}

// http://iquilezles.org/www/articles/boxfunctions/boxfunctions.htm
vec2 iBox( in vec3 ro, in vec3 rd, in vec3 rad ) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
	return vec2( max( max( t1.x, t1.y ), t1.z ),
	             min( min( t2.x, t2.y ), t2.z ) );
}

vec2 raycast( in vec3 ro, in vec3 rd )
{

    // raymarch primitives   
    // distance from origin
    float dO=0.;
    float ID = -1.;
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO*rd;

        // get distance to seam
        vec2 getObj = map(p);
        ID = getObj.y;
        float ds = getObj.x;
        //move origin to new point
        if(ID == 15.){
            dO+=ds*.8;
        }
        else if(ID == 17.){
            dO+=ds*.8;
        }
        else if(ID == 1.){
            dO+=ds*.8;
        }
        else if(ID == 90.){
            dO+=ds*.8;
        }else{
            dO+=ds;
        }
        if(ds < SURFACE_DIST || dO > MAX_DIST){
            break;
        }
    }
    return vec2(min(dO, MAX_DIST),ID);
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (0.8-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=ZERO; i<24; i++ )
    {
		float h = map( ro + rd*t ).x;
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.2 );
        if( res<0.004 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{

#if 0
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
#else
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.0005*e).x;
      //if( n.x+n.y+n.z>100.0 ) break;
    }
    return normalize(n);
#endif    
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

// http://iquilezles.org/www/articles/checkerfiltering/checkerfiltering.htm
float checkersGradBox( in vec2 p, in vec2 dpdx, in vec2 dpdy )
{
    // filter kernel
    vec2 w = abs(dpdx)+abs(dpdy) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    // xor pattern
    return 0.5 - 0.5*i.x*i.y;                  
}

vec3 render( in vec3 ro, in vec3 rd, in vec3 rdx, in vec3 rdy )
{ 
    // background
    vec3 col = vec3(0.7, 0.7, 0.9) - max(rd.y,0.0)*0.3;

    // raycast scene
    vec2 res = raycast(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );
        float ks = .3;

        vec3 colXZ = texture(iChannel2, pos.xz*.5+.5).rgb;
        vec3 colXY = texture(iChannel2, pos.xy*.5+.5).rgb;
        vec3 colYZ = texture(iChannel2, pos.yz*.5+.5).rgb;

        vec3 colXZ2 = texture(iChannel3, pos.xz*.5+.5).rgb;
        vec3 colXY2 = texture(iChannel3, pos.xy*.5+.5).rgb;
        vec3 colYZ2 = texture(iChannel3, pos.yz*.5+.5).rgb;

        vec3 colXZ3 = texture(iChannel1, pos.xz*.5+.5).rgb;
        vec3 colXY3 = texture(iChannel1, pos.xy*.5+.5).rgb;
        vec3 colYZ3 = texture(iChannel1, pos.yz*.5+.5).rgb;
        // material        
        if(m == 1.){
            nor = abs(nor);
            col = (colXZ2*nor.y + colXY2*nor.z + colYZ2*nor.x) * .5;
            //col = nor;
            ks = .01;
        }
        else if( m==20.)
        {
            // project pixel footprint into the plane
            /*vec3 dpdx = ro.y*(rd/rd.y-rdx/rdx.y);
            vec3 dpdy = ro.y*(rd/rd.y-rdy/rdy.y);

            float f = checkersGradBox( 3.0*pos.xz, 3.0*dpdx.xz, 3.0*dpdy.xz );
            col = 0.15 + f*vec3(0.05);*/
            col = colXZ*nor.y + colXY*nor.z + colYZ*nor.x;
            ks = .01;
        }
        else if( m==15.){
            col = vec3(0.4,.1,0);
            ks = .1; 
        }
         else if( m==16.){
            col = vec3(0,.1,0);
            ks = .1; 
        }
        else if( m==21.){
            col = vec3(.1);
            ks = 5.; 
        }
        else if( m==26.){
            col = vec3(.5) * .01;
            ks = .1; 
        }
        else if( m==90.){
            col = vec3(0,1,0) * .1;
            ks = .1; 
        }
        else if( m==91.){
            col = vec3(1,0,0) * .1;
            ks = .1; 
        }
        else if( m==100.){
            col = vec3(1,pos.y,pos.y) * .1;
            ks = .1; 
        }
        // lighting
        float occ = calcAO( pos, nor );

		vec3 lin = vec3(0.0);

        // sun
        {
            vec3  lig = normalize( vec3(0.6, 0.7, -0.9) );
            vec3  hal = normalize( lig-rd );
            float dif = clamp( dot( nor, lig ), 1.0, 1.0 );
          //if( dif>0.0001 )
        	      dif *= calcSoftshadow( pos, lig, 0.02, 2.5 );
			float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),16.0);
                  spe *= dif;
                  spe *= 0.04+0.96*pow(clamp(1.0-dot(hal,lig),0.0,1.0),5.0);
            lin += col*2.20*dif*vec3(1.30,1.00,0.70);
            lin +=     5.00*spe*vec3(1.30,1.00,0.70)*ks;
        }
        // sky
        {
            float dif = sqrt(clamp( 0.5+0.5*nor.y, 0.0, 1.0 ));
                  dif *= occ;
            float spe = smoothstep( -0.2, 0.2, ref.y );
                  spe *= dif;
                  spe *= 0.04+0.96*pow(clamp(1.0+dot(nor,rd),0.0,1.0), 5.0 );
          //if( spe>0.001 )
                  spe *= calcSoftshadow( pos, ref, 0.02, 2.5 );
            lin += col*0.60*dif*vec3(0.40,0.60,1.15);
            lin +=     2.00*spe*vec3(0.40,0.60,1.30)*ks;
        }
        // back
        {
        	float dif = clamp( dot( nor, normalize(vec3(0.5,0.0,0.6))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
                  dif *= occ;
        	lin += col*0.55*dif*vec3(0.25,0.25,0.25);
        }
        // sss
        {
            float dif = pow(clamp(1.0+dot(nor,rd),0.0,1.0),2.0);
                  dif *= occ;
        	lin += col*0.25*dif*vec3(1.00,1.00,1.00);
        }

		col = lin;

        col = mix( col, vec3(0.7,0.7,0.9), 1.0-exp( -0.0001*t*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 32.0 + iTime*1.5;

    // camera	
    vec3 ta = vec3( 0, 0, 0 );
    vec3 ro = ta + vec3( 4.5, 1.3 + 2.0*mo.y, 4.5*sin(0.1 + 7.0*mo.x) );
    // camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );

    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif

        // ray direction
        vec3 rd = ca * normalize( vec3(p,2.5) );

         // ray differentials
        vec2 px = (2.0*(fragCoord+vec2(1.0,0.0))-iResolution.xy)/iResolution.y;
        vec2 py = (2.0*(fragCoord+vec2(0.0,1.0))-iResolution.xy)/iResolution.y;
        vec3 rdx = ca * normalize( vec3(px,2.5) );
        vec3 rdy = ca * normalize( vec3(py,2.5) );

        // render	
        vec3 col = render( ro, rd, rdx, rdy );

        // gain
        // col = col*3.0/(2.5+col);

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    fragColor = vec4( tot, 1.0 );
}

vec3 hsl2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
   vec2 uv = fragCoord/iResolution.xy;

	uv.x = uv.x/122.;
	uv.y = uv.y/32. - 0.015;

    float pixel_y = uv.y * 100.;
    float pixel_x = uv.x * 550. - 3.;

    float X = pixel_x;
    float Y = pixel_y;
    float n = 120.;

	for (float k=17.;k>0.;k-=1.) {

		float X_new = X*X - Y*Y + pixel_x;
		float Y_new = 2.*X*Y    + pixel_y;
        X = X_new;
        Y = Y_new;

        n=n-1.;

		if (tan(sin(X_new+iTime)+sin(Y_new))<-4.) {
          fragColor = vec4(hsl2rgb( 
                              vec3(.2+k/20. -iTime*0.1, 0.8, 0.6+ sin(n/5.)*cos(n/15.+iTime/2.)*n/280.)
                      ),1.0);
        }
	}
}

