        u = cross(f, r),
        c = ro + f * zoom,
        i = c + uv.x * r + uv.y * u,
        rd = normalize(i-ro);



     vec3 t;
     float dO, dif, dif2, dif3, difT;
     vec3 p;



     t = RayMarch2(ro, rd, uv);
     p = ro + rd * t.x;

    float alpha = 1.0;
