   vec3 diff2 = GetLightM(p, uv, lightpos, n1, ro, rd);
   //vec3 diff3 = GetLightM(p, uv, lightpos2, n1, ro, rd);


     if(t.x > 0.0){







         if(t.z == 0.0){
            // col = ph * vec3(0.0, 1.0, 0.0);
         }
         else if(t.z == 1.0){
             col = diff2* vec3(1.0, 0.0, 0.0);
         }
         else if(t.z == 2.0){


            // col = diff2 * vec3(1.0, 0.5, 0.5);
