            q *= RotX(iTime);

            vec3 m = Line(q.xy, vec2(5.0, 0.0), vec2(-5.0, 0.0), 0.1, 0.05);
             m += Line(q.xy, vec2(0.0, -5.0), vec2(0.0, 5.0), 0.1, 0.05);

