#define PI 3.14159265359

// 2D Random
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}


float random (in float x) {
    return fract(sin(x)*1e4);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {

    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    // vec2 u = f*f*(3.0-2.0*f);
    vec2 u = f*f*(3.0-15.*abs(tan(iTime*2.))*f);
    u += smoothstep(0.,0.5,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}
float randomSerie(float x, float freq, float t) {
    return step(.8,random( floor(x*freq)-floor(t) ));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 st = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.y, iResolution.x);    vec3 color = vec3(1.0);
        // move space from the center to the vec2(0.0)
    st -= vec2(random(0.5));
    // rotate the space
    st = rotate2d( sin(cos(iTime))*PI*(abs(cos(iTime))/1e2+1.)) * st/0.8;
        st = scale( vec2(sin(iTime)+1.0) ) * st/5.;
    // move it back to the original place
    st += vec2(random(0.5));

    // Scale the coordinate system to see
    // some noise in action
    vec2 pos = vec2(st*10.0*smoothstep(0.,0.5,abs(sin(iTime))));
    float freq = random(1.);
    float t = random(10.);
   float offset = 0.0005+random(0.5);
        // Use the noise function

float n = noise(pos);
    color = vec3(randomSerie(st.x*n*1e1, freq*200., n*t+offset),
                 randomSerie(atan(st.x,st.y), freq*100., n/t),
                 randomSerie(tan(st.y), freq*1000.*cos(iTime), t-offset));
    color/= vec3(n);
    color*=vec3(n)*1e2;


    fragColor = vec4(color, 1.0);
}

mat2 rotate(float r) {
    float c = cos(r);
    float s = sin(r);
    return mat2(c, -s, s, c);
}

vec3 circle (vec2 uv, float posX, float posY, float r, float blur, vec3 color){
    uv.x-=posX;
    uv.y-=posY;

    float d = length(uv);
    return color * vec3(smoothstep(r,r-blur,d));
}
vec3 circle (vec2 uv, float r, float blur, vec3 color){
    float d = length(uv);
    return color * vec3(smoothstep(r,r-blur,d));
}
vec3 square(vec2 uv, float w, float h, float posX, float posY, float rot, float blur, vec3 color){
    uv*=rotate(rot);
    float wPos = length(uv.x-posX);
    float hPos = length(uv.y-posY);

    float col = smoothstep(w,w-blur, wPos);
    col *= smoothstep(h,h-blur, hPos);
    return color*vec3(col);
}
vec3 triangle(vec2 uv, float w, float h, float posX, float posY, float rot, float blur, vec3 color){
    uv.x-=posX;
    uv.y-=posY;
    uv*=rotate(rot);
    vec3 sq = square(uv,w,h,0.,0.,0.,blur,color);
    uv*=rotate(3.1415/2./2.);
    uv-= vec2(0.,h);
    float hyp = sqrt(w*w + h*h);
    vec3 sqMask = square(uv,hyp,h,0.,0., 0.,blur,color);
    sq -= sqMask;
    return sq;
}
vec3 ellipse(vec2 uv, float posX, float posY, float rot, float a,float b, float blur, vec3 color){
    uv -= vec2(posX,posY);
    uv*=rotate(rot);
    float x = uv.x;
    float y = sqrt((1.-a)*(1.-((x*x)/(1.-b))));
    float d = length(uv);
    return color * vec3(smoothstep(y,y-blur,d));
}
vec3 wave (vec2 uv, float xPos, float yPos, float blur, float vertSpeed, float horSpeed){
    float movX = sin(iTime*vertSpeed)*.04;
    float movY = cos(iTime*horSpeed)*.04;
    uv -= vec2(xPos,yPos);
    vec3 wave = circle (uv - vec2(movX,movY),.2,blur, vec3(1.));
    wave = min(wave, wave-circle (uv - vec2(0.12 + movX,.05 + movY), 0.2, blur, vec3(1.)));
    wave = min(wave, wave-circle (uv - vec2(-0.12+ movX,.05+ movY), 0.2, blur, vec3(1.)));
    wave = min(wave, wave-circle (uv - vec2(0.+ movX,-.34+ movY), 0.2, blur, vec3(1.)));
    return wave;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv-= 0.5;

    uv.x *= iResolution.x/iResolution.y;


    //background
    float blur = 0.01;
    //sky
    vec3 col = vec3(uv.y+.3) * vec3(1,.2,.4);

    //ocean
    col = max(col, vec3(-uv.y) * vec3(.5,.4,.8));
    //waves

    float movX = sin(iTime)*.04;
    float movY = cos(iTime*2.)*.04;

    vec3 w = wave(uv,0.4,-0.2,blur, 1., 2.);
    w = max(w, wave(uv,-.4,-0.2,blur, 1., 4.));
    w = max(w, wave(uv,-.2,-0.3,blur, 1.3, 2.));
    w = max(w, wave(uv,.3,-0.3,blur, 1.6, 2.3));
    col = max(col, w);

    //water lines
    vec3 waterLine = square(uv, .7, .005, -.9+movX, -0.4, 0., blur, vec3(1.));
    waterLine = max(waterLine, square(uv, .9, .005,.9+movX*.5, -0.39, 0., blur, vec3(1.)));
    waterLine = max(waterLine, square(uv, .8, .005,-.9+movX*.7, -0.38, 0., blur, vec3(1.)));
    waterLine = max(waterLine, square(uv, .777, .005,.9+movX*.9, -0.37, 0., blur, vec3(1.)));
    waterLine = max(waterLine, square(uv, .8, .005,-.9+movX*.2, -0.36, 0., blur, vec3(1.)));
    waterLine = max(waterLine, square(uv, .82, .005,.9+movX*1.2, -0.35, 0., blur, vec3(1.)));
    waterLine = max(waterLine, square(uv, .76, .005,-.9+movX*.7, -0.34, 0., blur, vec3(1.)));
    waterLine = max(waterLine, square(uv, .9, .005,.9+movX*.9, -0.33, 0., blur, vec3(1.)));
    waterLine = max(waterLine, square(uv, .9, .005,-.9+movX*.2, -0.32, 0., blur, vec3(1.)));
    waterLine = max(waterLine, square(uv, .8, .005,.9+movX*1.2, -0.31, 0., blur, vec3(1.)));
    col = max(col, waterLine);
    //ground
    vec3 groundM = ellipse(uv, 0., -0.26, 3.1415/2., .9,.996, blur, vec3(1.));
    groundM = max(groundM, square(uv, .1, .2, -0.2, -0.33, -1., blur, vec3(1.)) - square(uv, .2, .3, 0., 0., 0., blur, vec3(1.)) );
    groundM = max(groundM, square(uv, .45, .136, 0.7,-.5, 0., blur, vec3(1.)));
    col-= groundM;
    vec3 ground = ellipse(uv, 0., -0.26, 3.1415/2., .9,.996, blur, vec3(.5));
    ground = max(ground, square(uv, .1, .2, -0.2, -0.33, -1., blur, vec3(.5))- square(uv, .2, .3, 0., 0., 0., blur, vec3(1.)));
    ground = max(ground, square(uv, .45, .136, 0.7,-.5, 0., blur, vec3(.5)));
    col = max(col,ground);
    //tori gate
    ///mask----------------------
    //legs
    vec3 toriM = square(uv, .03, .25, -0.2, 0., 0.02, blur, vec3(1.));
    toriM = max(toriM,square(uv, .03, .25, 0.2, 0., -0.02, blur, vec3(1.)));

    //horizontal beams
    toriM = max(toriM,square(uv, .37, .03, 0., 0.15, 0., blur, vec3(1.)));
    toriM = max(toriM,square(uv, .37, .022, 0., 0.25, 0., blur, vec3(1.)));

