    tori = max(tori,square(uv, .034, .022, 0.198, .22, 0., blur, vec3(1,.6,.4)));
    tori = max(square(uv, .034, .04, -0.205, -.21, 0., blur, vec3(.5)),tori-square(uv, .034, .04, -0.205, -.21, 0., blur, vec3(.5)));
    tori = max(square(uv, .034, .04, 0.205, -.21, 0., blur, vec3(.5)),tori-square(uv, .034, .04, 0.205, -.21, 0., blur, vec3(.5)));
    col = max(col,tori);

    //sun
    vec3 sun = circle (uv-vec2(.0,.405), .04, blur, vec3(1));
    sun = max(sun, circle (uv-vec2(.0,.405), .06, blur, vec3(1,.5,.5)));
    col = max(col, sun);
    // Output to screen

    fragColor = vec4(col,1.0);
}

vec3 hsl2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}


