#define HALF_PI 1.57079632679
#define COS_120 -0.5
#define SIN_120 0.86602540378

// Container for 3 complex numbers
struct Complex3 {
    vec2 a;
    vec2 b;
    vec2 c;
};

Complex3 complexCuberoot(in vec2 z) {
    // De Moivre's formula
    vec2 r = sin(atan(z.y, z.x) / 3.0 + vec2(HALF_PI, 0.0)) * pow(length(z), 1.0 / 3.0);
    return Complex3(r,
                    r * mat2(COS_120,  SIN_120, -SIN_120, COS_120),  // Rotate +120 degrees
                    r * mat2(COS_120, -SIN_120,  SIN_120, COS_120)); // Rotate -120 degrees
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 screenCenter = 0.5 * iResolution.xy;

    vec2 uv = (fragCoord - screenCenter) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    // Draw grid
    color = mix(color, vec3(0.0, 0.0, 1.0), smoothstep(unit, 0.0, abs(mod(uv.y + 0.125, 0.25) - 0.125)));
    color = mix(color, vec3(0.0, 0.0, 1.0), smoothstep(unit, 0.0, abs(mod(uv.x + 0.125, 0.25) - 0.125)));

    // Draw axes
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(uv.y)));
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(uv.x)));

    // Complex number (demo if no mouse)
    vec2 z = vec2(sin(iTime * 1.25) * 2.0, cos(iTime * 0.5));
    if (iMouse.z > 0.0) {
        z = (iMouse.xy - screenCenter) / iResolution.y * 4.0;
    }

    // Find cube roots of z and plot
    Complex3 cbrt = complexCuberoot(z);

    color = mix(color, vec3(1.0), smoothstep(unit, 0.0, length(uv - z) - 0.05));

    color = mix(color, vec3(1.0, 0.0, 1.0), smoothstep(unit, 0.0, length(uv - cbrt.a) - 0.05));
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - cbrt.b) - 0.05));
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - cbrt.c) - 0.05));

    // Draw "z" character
    vec4 zTexBbox = vec4(0.625, 0.5, 0.6875, 0.5625);
    vec4 zScrBbox = vec4(-0.15, 0.1, 0.15, 0.4);

    zScrBbox.xy += z;
    zScrBbox.zw += z;

    if (all(lessThan(uv, zScrBbox.zw)) && all(greaterThan(uv, zScrBbox.xy))) {
        vec2 mappedUv = (uv - zScrBbox.xy) / (zScrBbox.zw - zScrBbox.xy) * (zTexBbox.zw - zTexBbox.xy) + zTexBbox.xy;
        color = mix(color, vec3(1.0), smoothstep(unit, 0.0, texture(iChannel0, mappedUv).w - 0.5));
    }

    fragColor = vec4(color, 1.0);
}

