/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */



float fract1(float a){
    return (abs(fract(a/2.0)-.5))*2.0;
}

vec2 fract1(vec2 a){
    return (abs(fract(a/2.0)-.5))*2.0;
}

vec3 fract1(vec3 a){
    return (abs(fract(a/2.0)-.5))*2.0;
}

vec3 fractal(vec3 uv1){
    vec3 col,uv;
    for(int c=0;c<3;c++){
        uv = uv1;
        //uv += vec2(iTime/3.0,iTime/8.0)/2.0;
        for(int i=0;i<ITERATIONS;i++)
        {
            uv = (fract1(uv/scale2))*scale1;
            uv= fract1(uv/(2.0-fract1((uv.x-uv.y)/(8.0)))-(uv/(1.7+(fract1(uv.x+uv.y))))/scale)*scale/scale1+scale1*scale;
            uv /= scale1+col.yzx/scale;
        	uv=uv.yzx;
        }
     col[c] = fract(fract(uv.y)+fract(uv.x));
	}
    return col;
}


float sceneSDF(vec3 p) {
    return length(fractal(p/10.0));
}

vec3 surface_color(vec3 p)
{
    return vec3(fractal(p/5.0)+fractal(p.yzx/6.0));
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    float eps = EPSILON;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < eps*(1.0+depth*.1)) {
			return depth;
        }
        depth += dist;
        eps *= 1.001;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 illumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    //vec3 light1Pos = eye;
    //vec3 light1Intensity = vec3(0.8);

    //color += phongContribForLight(k_d, k_s, alpha, p, eye,
    //                              light1Pos,
    //                              light1Intensity);   
    return color;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
        viewDir.x+=-.5;
        /*if (length(iMouse.xy) > 40.0) {
            viewDir.yz *= rot(-iMouse.y/iResolution.y*3.14-3.14*0.5);
            viewDir.xz *= rot(iMouse.x/iResolution.x*3.14*2.0-3.14);
        }*/

    vec3 eye = vec3(-35.0+sin(iTime/2.0)/2.0,1.0+cos(iTime/2.0)/2.0,iTime*2.0+50.0);

    mat3 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = -viewToWorld * viewDir;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    vec3 K_a = surface_color(p);
    vec3 K_d = K_a;
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 7.0;

    vec3 color = illumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = fragCoord/iResolution.xy;
  vec2 q = uv - vec2(0.5, 0.5);
  q.xy = q.yx;
  float p1 = 0.5;
  float te1 = iTime/10.;
  float te3 = te1/20.;
  float te5 = te3/40.;
  q.x = sin(te1) * q.x;
  float R = sin(te1);
  float G = cos(te1);
  float B = sin(q.x)/cos(q.y);
  vec3 col = p1 * vec3(R, G, B);
  float fC = sin(te1);
  float f1 = fC * sin(q.x/q.y);
  float pulse = sin(
    (cos(te1) * cos(te5))
    /(f1 * sin(te3) * cos(te3)));
  vec2 tig = vec2(sin(te1), cos(te3));
  //opt1:
  tig *= tig;
  vec2 squig = vec2(sin(pulse), cos(pulse));
  //opt2:
  squig *= squig;
  col *= 0.2 / length(q * tig * squig);
  // Mix in sound
  vec2 sound = texture( iChannel1, vec2(0.1,0.3) ).xy;
  col.x *= sound.x;
  col.y *= sound.y;
  // Output to screen
  fragColor = vec4(col,1.0);
}

#define width 800.f
#define height 450.f
#define numColors 10
#define numCircles 100

vec3 GetColor(vec2 coord, vec4 circles[numCircles], vec3 colors[numColors], float timeDelay)
{
    int value = 0;
    float r;
    float xc;
    float yc;
    float d = sqrt((coord[0] - xc)*(coord[0]-xc)+(coord[1]-yc)*(coord[1]-yc));
    for(int i = 0; i < numCircles; i++)
    {
        xc = circles[i][0];
        yc = circles[i][1];
        r = circles[i][2];
        d = sqrt((coord[0] - xc)*(coord[0]-xc)+(coord[1]-yc)*(coord[1]-yc));
        if(d <= r && iTime >= timeDelay * float(i))
        {
            value+= int(circles[i][3]);
        }
    }
    return colors[value % numColors];
}
void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    float timeDelay = 0.05f;
    float timeFactor = 50.f;
    vec3 colors[numColors] = vec3[numColors](vec3(0.976, 0.254, 0.266),vec3(0.952, 0.447, 0.172),vec3(0.972, 0.588, 0.117),\
    vec3(0.976, 0.517, 0.290),vec3(0.976, 0.780, 0.309),vec3(0.564, 0.745, 0.427),vec3(0.262, 0.666, 0.545),\
    vec3(0.301,0.564,0.556),vec3(0.341,0.458,0.564),vec3(0.152,0.490,0.631));
    float timeInt = 0.f;
    int i = 0;
    float xGap = 1.f/11.f;
    float yGap = 1.f/11.f;
    vec4 circles[numCircles];
    for(float y = height * yGap; y < height; y+= height * yGap)
    {
        for (float x = width * xGap; x < width; x += width * xGap)
        {
            circles[i] = vec4(x, y, iTime * timeFactor - timeFactor * timeDelay * float(i),i+1);
            i++;
        }
    }

    vec3 col = GetColor(fragCoord, circles, colors, timeDelay);
    fragColor = vec4(col,1.0);
}

//calm pleasant nights

float Smooth(in float val,in float factor)
{
    return smoothstep(factor,-factor,val);//need to lookup true pixels
}

float Circle(in vec2 pos,in float radius)
{
    return length(pos) - radius;
}

float Box(in vec2 pos,in vec2 size)//Box - exact: https://www.youtube.com/watch?v=62-pRVZuS5c
{
    vec2 d = abs(pos) - size;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float CatTail(in vec2 pos,in vec2 sca,in vec2 scb,in float ra,in float rb)//Arc - exact: https://www.shadertoy.com/view/wl23RK
{
    pos *= mat2(sca.x,sca.y,-sca.y,sca.x);
    pos.x = abs(pos.x);
    float k = (scb.y * pos.x > scb.x * pos.y) ? dot(pos.xy,scb):length(pos);
    return sqrt(dot(pos,pos) + ra * ra - 2.0 * ra * k) - rb;
}

vec4 Cat(in vec2 pos,in vec3 color,in float factor)
{
    float sd = 0.0;
    float d = 0.0;
    d = Circle(pos,0.20);
    d += Circle(pos - vec2( 0.15,-0.15),0.2);
    d += Circle(pos - vec2( -0.15,-0.15 - (0.3 * sin(iTime + pos.y) / 18.0)),0.2);
    d = Smooth(d,factor);
    sd = max(sd,d);
    d = Circle(pos - vec2(-0.14,0.10),0.12);
    d += Circle(pos - vec2(-0.14,0.0),0.1);
    d += Circle(pos - vec2(0.04,0.0),0.1);
    d = Smooth(d,factor);
    sd = max(sd,d);
    d = Circle(pos - vec2(0.14,0.10),0.12);
    d += Circle(pos - vec2(0.14,0.0),0.1);
    d += Circle(pos - vec2(-0.04,0.0),0.1);
    d = Smooth(d,factor);
    sd = max(sd,d);
    d = Circle(pos - vec2(-0.1,-0.2),0.28);
    d += Circle(pos - vec2(0.1,-0.4),0.28);
    d += Circle(pos - vec2(0.0,-0.6 - (0.1 * sin(iTime + pos.x) / 16.0)),0.26);
    d -= Circle(pos - vec2(0.1,-0.62),0.32);
    d = Smooth(d,factor);
    sd = max(sd,d);
    float ta = 1.2;
    float tb = 4.1;
    float rb = 0.03;
    d = CatTail(pos - vec2(-0.265,-0.45),vec2(sin(tb * 1.34),cos(tb * 1.1)),vec2(sin(ta),cos(ta)),0.2 + (0.1 * sin(iTime + pos.x) / 40.0),rb);
    d = Smooth(d,factor / 3.0);
    sd = max(sd,d);
    return vec4(color,sd);
}

vec3 CatColor(in vec2 pos)
{
    vec3 color = vec3(0.9,0.3,0.3);
    color.r += (pos.y + 1.2) + abs((pos.x - 0.3) / 10.0) + (pos.x * 4.0) / 2.0;
    color.g += ((pos.y - pos.x * 0.2) / 2.0) - abs((pos.y * 2.2) / 4.0);
    color += 0.2 + pos.y / 20.0;
    return color;
}

vec4 CatReflection(in vec2 pos,in float size,in float factor)
{
    vec3 color = CatColor(pos) * abs(pos.x - 0.5);
    float sd = Circle(pos,size);
    sd = Smooth(sd,factor);
    return vec4(color,sd);
}

vec4 Wall(in vec2 pos,in vec2 size,in vec3 color,in float factor)
{
    float sd = Box(pos,size);
    sd = Smooth(sd,factor);
    return vec4(color,sd);
}

vec4 Moon(in vec2 pos,in float factor)
{   vec3 color = vec3(0.8,0.2,0.2);
    float sd = Smooth(Circle(pos,0.4),factor);
    color = vec3(1.6,0.45 - abs(((pos.y + pos.x) / 3.0) + 0.2),0.4);
    return vec4(color,sd);
}

vec3 Sky(in vec2 uv)
{
    return vec3(mix(vec3(0.6,0.2,0.4),vec3(0.2,0.2,0.4),uv.y * 1.2));
}

vec3 Draw(in vec3 color,in vec4 obj)
{
    return mix(color,obj.rgb,obj.a);
}

vec2 Screen(in vec2 pix,in vec3 res)
{
    return vec2((2.0 * pix - res.xy) / res.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = Screen(fragCoord,iResolution);
    vec3 color = Sky(uv);
    vec4 moon = Moon(uv - vec2(0.6,0.4),0.002);
    vec4 wall_a = Wall(uv - vec2(0.0,-0.6),vec2(2.0,0.16),vec3(0.8,0.4,0.2),0.4);
    vec4 wall_b = Wall(uv - vec2(0.0,-0.6),vec2(2.0,0.16),CatColor(uv) * (abs(uv.x) / 2.0),0.002);
    vec4 wall_c = Wall(uv - vec2(0.0,-0.8),vec2(2.0,0.06),CatColor(uv) * (abs(uv.y) / 6.0),0.002);
    vec4 cat_fx = CatReflection(uv - vec2(-0.6,-0.548),0.06,0.14);
    vec4 cat = Cat(uv - vec2(-0.7,-0.2),CatColor(uv),0.002);
    color = Draw(color,moon);
    color = Draw(color,wall_a);
    color = Draw(color,wall_b);
    color = Draw(color,wall_c);
    //color = Draw(color,cat_fx);
    color = Draw(color,cat);
    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
   vec2 uv = fragCoord/iResolution.xy;

	uv.x = uv.x/122.;
	uv.y = uv.y/32. - 0.015;

    float pixel_y = uv.y * 100.;
    float pixel_x = uv.x * 550. - 3.;

    float X = pixel_x;
    float Y = pixel_y;

	for (float k=17.;k>0.;k-=1.) {

		float X_new = X*X - Y*Y + pixel_x;
		float Y_new = 2.*X*Y    + pixel_y;
        X = X_new;
        Y = Y_new;
		if (tan(X_new+sin(Y_new+iTime))<(-3.)) {
			fragColor.r=pow(k,3.)/255.;
            fragColor.g=pow(k,2.)/455.;
        }
	}
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define fold(p,v)p-2.*min(0.,dot(p,v))*v;
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 r=iResolution,p;
    float i,g,e=1.,l,s;
    for(i=0.;
        ++i<99.&&e>.001;
        g+=e=length(p.xy)/s
        )
    {
        p=g*vec3((C-.5*r.xy)/r.y,1);
        p=R(p,normalize(vec3(1)),iTime*.2);
        p.z-=-1.;
        p.x-=sin(.4*iTime+.5*sin(iTime*.5))*.2;
        p.y-=cos(.7*iTime+.5*cos(iTime*.5))*.2;
        s=3.;
        for(int i = 0;++i<15;)
        {
            p.xy=fold(p.xy,normalize(vec2(1,-1.3)));
            p.y=-abs(p.y);
            p.y+=.5;
            p.xz=abs(p.xz);
            p.yz=fold(p.yz,normalize(vec2(8,-1)));
            p.x-=.5;
            p.yz=fold(p.yz,normalize(vec2(1,-2)));
            p-=vec3(1.8,.4,.1);
            l = 2.6/dot(p,p);
            p*=l;
            p+=vec3(1.8,.7,.2);
            s*=l;
        }
    }  
    O.xyz+=mix(vec3(1),cos(vec3(5,18,3)+p*10.),.5)*500./i/i;
}

