#define PI acos(-1.)
// I know it's TAU but I found that typo funny :3
#define TAY 6.283185
#define ITER 64.

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define mo(uv,d)uv=abs(uv)-d;if(uv.y>uv.x)uv=uv.yx;
#define crep(p,c,l) p=p-c*clamp(round(p/c),-l,l)

#define cyl(p,r,h) max(length(p.xy)-(r),abs(p.z)-h)

#define time iTime
#define dt(speed) fract(time*speed)
#define hash21(x) fract(sin(dot(x,vec2(12.4,33.8)))*1247.4)



struct obj
{
  float d;
  vec3 color;
};

obj minobj (obj a, obj b)
{
  if (a.d<b.d) return a;
  else return b;
}

void moda (inout vec2 p, float rep)
{
  float per = TAY/rep;
  float a = mod(atan(p.y,p.x),per)-per*0.5;
  p = vec2(cos(a),sin(a))*length(p);
}

float box (vec3 p, vec3 c)
{
  vec3 q = abs(p)-c;
  return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float torus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float heightmap (vec2 uv)
{
  uv = fract(uv)-.5;
  uv = abs(uv);
  return smoothstep(0.2,0.27,max(uv.x,uv.y*0.7));
}

obj tunnel (vec3 p)
{
  vec3 pp = p;
  vec2 tuv = vec2(atan(p.x,p.z)*5.,p.y*0.4);
  float r;
  if (tuv.x > -5. && tuv.x < 20.) r = 10.5-heightmap(tuv)*0.15;
  else r = 10.5;
  float td = -cyl(p.xzy,r,1e10);

  float per = 10.;
  float id = floor(p.y/per);
  p.xz *= rot((TAY/6.)*(id+.5));
  p.y = mod(p.y,per)-per*0.5;
  float holes = -cyl(p,1.5,25.);   
  td = max(holes ,td);
  p.z = abs(p.z)-10.25;
  td = min(td,max(holes,cyl(p,1.8,0.3)));

  return obj(td,vec3(1.));
}

float pipe (vec3 p)
{
  float pd = cyl(p.xzy,0.2,1e10);
  float per = 2.;
  p.y = mod(p.y,per)-per*0.5;
  pd = min(pd,cyl(p.xzy,0.25,0.1));

  return pd;
}

obj pipes (vec3 p)
{
  moda(p.xz, 6.);
  p.x -= 9.;
  crep(p.z,0.7,2.);
  float psd = pipe(p);
  return obj(psd,vec3(.7,0.,0.));
}

float g1=0.;
float platform (vec3 p)
{
  p.xz *= rot(TAY/6.);
  float s = length(p)-.5;
  g1 += 0.1/(0.1+s*s);

  mo(p.xz,vec2(.5));
  float d = box(p,vec3(10.,0.2,1.5));
  crep(p.xz,0.4,vec2(30.,4.));
  d = max(-box(p,vec3(0.2,0.5,0.1)),d);
  d = min(d,s);
  return d;
}

obj platforms (vec3 p)
{
  float per = 40.;
  p.y = mod(p.y-per*0.5,per)-per*0.5;
  float pld = platform(p);
  return obj(pld,vec3(0.9,0.1,0.1));
}

float roundpipe (vec3 p)
{
  float rpd = torus(p,vec2(10.3,0.2));
  moda(p.xz,18.);
  p.x -= 10.3;
  rpd = min(rpd, cyl(p,0.25,0.1));
  return rpd;
}

obj roundpipes (vec3 p)
{
  float per = 20.;
  p.y = mod(p.y,per)-per*0.5;
  return obj(roundpipe(p),vec3(0.9,0.4,0.));
}

obj SDF (vec3 p)
{
  p.y -= dt(1./5.)*120.;
  obj so = minobj(tunnel(p),pipes(p)); 
  so = minobj(so,platforms(p));
  so = minobj(so,roundpipes(p));
  return so;
}

vec3 getcam (vec3 ro, vec3 ta, vec2 uv)
{
  vec3 f = normalize(ta-ro);
  vec3 l = normalize(cross(vec3(0.,1.,0.),f));
  vec3 u = normalize(cross(f,l));
  return f + l*uv.x + u*uv.y;
}

vec3 getnorm (vec3 p)
{
  vec2 eps = vec2(0.01,0.);
  return normalize(SDF(p).d-vec3(SDF(p-eps.xyy).d,SDF(p-eps.yxy).d,SDF(p-eps.yyx).d));
}

float AO (float eps,vec3 n, vec3 p)
{return SDF(p+eps*n).d/eps;}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

  float dither = hash21(uv);
  vec3 ro = vec3(0.3,0.01,-6.), rd=getcam(ro,vec3(.5,-7.,0.),uv),p=ro, col=vec3(0.);
  float shad; bool hit=false; obj O;

  for(float i =0.; i<ITER; i++)
  {
    O = SDF(p);
    if (O.d<0.0001)
    {
      hit=true; shad=i/ITER; break;
    }
    O.d *= 0.5+dither*0.05;
    p += rd*O.d;
  }

  float t = length(ro-p);
  if(hit)
  {
    vec3 n = getnorm(p);
    float ao = AO(0.1,n,p) + AO(0.4,n,p) + AO(0.5,n,p);
    col = O.color;
    col *= (1.-shad);
    col *= ao/3.;
  }
  col = mix(col,vec3(0.,0.05,0.1),1.-exp(-0.001*t*t));
  col += g1*vec3(0.1,0.8,0.2)*0.8;

  fragColor = vec4(sqrt(col),1.);
}

float fract1(float a){
    return (abs(fract(a/2.0)-.5))*2.0;
}

vec2 fract1(vec2 a){
    return (abs(fract(a/2.0)-.5))*2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    float t;

    for(int c=0;c<3;c++){

        vec2 uv = (fragCoord*40.0-iResolution.xy)/iResolution.y/10.0;
        uv += vec2(iTime/2.0,iTime/8.0)/2.0;
        t = iTime+float(c)/10.;
        float scale = 5.5;
        float scale1 = 2.0;
        for(int i=0;i<3;i++)
        {
            uv = (fract1(uv/scale1)+fract1(uv/scale/2.0))*scale1;
            uv= fract1(uv/(2.0-fract1((uv.x-uv.y)/(8.0)))-(uv/(1.7+(fract1(uv.x+uv.y))))/scale)*scale/scale1+scale1*scale;
            uv /= scale1+col.yx;
        	uv=uv.yx+col.xy;
        }
     col[c] = abs(fract(uv.y)-fract(uv.x));
	}

    fragColor = vec4(vec3(col),1.0);

}

#define rand(x) fract(sin(x)*43758.5453)

const float pi = acos(-1.);
const float pi2 = pi*2.;

float rand2(vec2 p) {
  return rand(dot(p, vec2(24.1214, 15.2321)));
}

float rand3(vec3 p) {
  return rand(dot(p, vec3(24.1214, 15.2321, 21.2362)));
}

mat2 rotate(float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a));
}

vec2 pmod(vec2 p, float n) {
  float a = pi2 / n;
  float theta = atan(p.y, p.x) + .5*a;
  theta = floor(theta/a)*a;
  return p*rotate(-theta);
}

vec3 hsv2rgb(float h, float s, float v) {
  vec3 res = fract(h+vec3(0,2,1)/3.);
  res = abs(res*6.-3.)-1.;
  res = clamp(res, 0., 1.);
  res = (res-1.)*s+1.;
  res *= v;
  return res;
}

float interval_xy = 10.;
float interval_z = 5.;
float dist1(vec3 p) {
  vec3 z = p;
  vec2 id = floor(z.xy/interval_xy);

  z.z -= fract(iTime)*10.;
  z.xy = mod(z.xy, interval_xy) - .5*interval_xy;
  z.xy *= rotate(floor(z.z/interval_z)*.2 + iTime);
  z.z = mod(z.z, interval_z) - .5*interval_z;

  float r1 = rand2(id+floor(iTime)*.1);
  z.xy = pmod(z.xy, floor(r1*8.+3.));
  z.x -= 1.5;

  return length(max(abs(z)-vec3(.5), 0.));
}

float dist2(vec3 p) {
  vec3 z = p;
  z = abs(z)-.25;
  z *= 2.;
  z = abs(z)-.25;
  z *= 2.;

  float size = .5+pow(sin(iTime*10.)*.5+.5, 5.);
  size *= .5;
  return length(max(abs(z)-vec3(size), 0.))/4.;
}

vec3 calcNormal1(vec3 p, float eps) {
  vec2 e = vec2(0, eps);
  return normalize(vec3(dist1(p+e.yxx)-dist1(p-e.yxx),
  dist1(p+e.xyx)-dist1(p-e.xyx),
  dist1(p+e.xxy)-dist1(p-e.xxy)));
}

vec3 calcNormal2(vec3 p, float eps) {
  vec2 e = vec2(0, eps);
  return normalize(vec3(dist2(p+e.yxx)-dist2(p-e.yxx),
  dist2(p+e.xyx)-dist2(p-e.xyx),
  dist2(p+e.xxy)-dist2(p-e.xxy)));
}

float exp2Fog(float dist, float density) {
  float s = dist*density;
  return exp(-s*s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 p = (fragCoord*2.-iResolution.xy) / min(iResolution.x, iResolution.y);
  vec3 col = vec3(0);

  float c = iTime;
  float L = 1.-fract(c);
  float id = ceil(c);
  for(int i=0; i<10; i++) {
    float a = atan(.7, L)*3.;
    vec2 z = p/a;
    float r1 = rand(id);
    float r2 = rand(id+20.);
    z = pmod(z, floor(r1*10.+3.));
    z.x += -1.;
    z *= rotate(iTime*.5+pi2/20.*id);
    z = pmod(z, floor(r1*8.+3.));
    z.x += -1.;
    z = pmod(z, floor(r1*8.+3.));
    col += .005/abs(z.x-.5)*hsv2rgb(r2, 1., 1.);
    L++;
    id++;
  }

  vec3 cPos = vec3(0, 0, 5.);
  vec3 cDir = vec3(0, 0, -1);
  vec3 cUp = vec3(0, 1, 0);
  cUp.xy *= rotate(iTime*.1);
  vec3 cSide = cross(cDir, cUp);
  vec3 ray = normalize(p.x*cSide + p.y*cUp + cDir*2.5);

  float d = 0.;
  vec3 rPos = cPos;
  for(int i=0; i<100; i++) {
    d = dist1(rPos);
    if(d<0.001) {
      break;
    }
    rPos += ray*d;
  }

  if(d<0.1) {
    vec3 normal = calcNormal1(rPos, 0.0001);
    vec3 normal2 = calcNormal1(rPos, 0.1);
    float edge = clamp(length(normal-normal2), 0., 1.);
    vec2 id = floor(rPos.xy/interval_xy);
    float r1 = rand2(id+floor(iTime)*.1);
    float fog = exp2Fog(length(rPos-cPos), 0.01);
    col = mix(col, hsv2rgb(r1, 1., 1.)*edge, fog);
  }

  cPos.zx *= rotate(.5);
  cPos.yz *= rotate(iTime);
  cDir = normalize(-cPos);
  vec3 up = vec3(0, 1, 0);
  cSide = normalize(cross(cDir, up));
  cUp = normalize(cross(cSide, cDir));
  ray = normalize(p.x*cSide + p.y*cUp + cDir*2.5);

  d = 0.;
  rPos = cPos;
  for(int i=0; i<30; i++) {
    d = dist2(rPos);
    if(d<0.0001 || length(rPos-cPos) > 10.) {
      break;
    }
    rPos += ray*d;
  }

  vec3 lightDir = normalize(vec3(1));
  if(d<0.1) {
    vec3 normal = calcNormal2(rPos, 0.0001);
    vec3 normal2 = calcNormal2(rPos, 0.01);
    float edge = clamp(length(normal-normal2)*1.5, 0., 1.);
    vec3 id = floor(rPos*4.);
    float diff = max(dot(lightDir, normal), .0);
    float r1 = rand3(id+floor(iTime*10.)*.1);
    col = hsv2rgb(r1, diff*.5+.5, 1.)*(1.-edge);
  }

  fragColor = vec4(col, 1.);
}

#define sabs(p) sqrt((p)*(p)+.8)
void sfold90(inout vec2 p)
{
    p=(p.x+p.y+vec2(1,-1)*sabs(p.x-p.y))*.5;
}

float Scale;

float map(vec3 p)
{
	p=mod(p-1.5,3.)-1.5;
	p=abs(p)-1.3;
	sfold90(p.xz);
	sfold90(p.xz);
	sfold90(p.xz);

	float s=1.;
	p-=vec3(.5,-.3,1.5);
	for(float i=0.;i++<7.;)
    {
		float r2=2.1/clamp(dot(p,p),.0,1.);
		p=abs(p)*r2;
		p-=vec3(.1,.5,7.);
		s*=r2;
	}
	Scale=log2(s);
    float a=3.;
    p-=clamp(p,-a,a);
    return length(p)/s-.005;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv=(2.*fragCoord-iResolution.xy)/iResolution.y;
	vec3 p,
  		ro=vec3(0,0,iTime),
  		w=normalize(vec3(.3*sin(iTime*.5),.5,1)),
  		u=normalize(cross(w,vec3(cos(iTime*.1),sin(iTime*.1),0))),
  		rd=mat3(u,cross(u,w),w)*normalize(vec3(uv,2));
	float h=0.,d,i;
	for(i=1.;i<80.;i++)
    {
    	p=ro+rd*h;
		d=map(p);
    	if(d<.001)break;
    	h+=d;
	}
	fragColor.xyz=15.*vec3(sin(Scale*.7+p.xyx*.28)*.5+.5)/i;
}

