float Circle(vec2 uv, vec2 p, float r, float blur){
    float d = length(uv - p);
    float c = smoothstep(r, r - blur,d);
    return c;
}

float Band(float t, float start, float end, float blur)
{
    float step1 = smoothstep(start - blur,start + blur,t);   
    float step2 = smoothstep(end + blur,end - blur,t);   
    return step1 * step2;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur) 
{
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);
    return band1 * band2;
}

float Smiley(vec2 uv, vec2 p, float size)
{
    uv -= p; //Translate coordinate system
    uv /= size; //Scale coordinate system
    //face
    float c = Circle(uv, vec2(0.0,0.0), 0.4, 0.02);
    //mouth
    c -= Circle(uv, vec2(0.0, -0.1), 0.2, 0.01);
    c += Circle(uv, vec2(0.0, 0.0), 0.2, 0.01);
    c = clamp(c,0.0,1.0);
    //eyes
    c -= Circle(uv, vec2(0.15, 0.1), 0.1, 0.01);
    c -= Circle(uv, vec2(-0.15,0.1), 0.1, 0.01);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= 0.5; //-0.5 <> 0.5
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0);

    float c = Smiley(uv, vec2(0.5,0.3), abs(0.5*sin(iTime)));
    for(int i = 0; i<9; i++)
    {
        float x = float(i % 3);
        float y = float(i / 3);
        c += Smiley(uv, vec2(0.3*x-0.4,0.2*y-0.3), abs(0.2*cos(iTime+float(i)/9.0)));
    }
    c += Rect(uv, 0.4, 0.8, -0.4, -0.1, abs(0.1*cos(0.5*iTime)));   
    col = vec3(1.0, 1.0, .0)*c;


    // Output to screen
    fragColor = vec4(col,1.0);
}

// adapted from an old Pixel Bender pbk i had lying around...
vec3 xAxisColor = vec3(1,.3,.4);
vec3 yAxisColor = vec3(.3, .8,.4);
vec3 zAxisColor = vec3(.1,.3,1);
vec3 edgeColor = vec3(.1,.3,1);

 float edgeThickness = 0.05;     
float invert = 1.0;    
vec2 dstsize = vec2(300.0, 300.0);
vec3 spin = vec3(.1,.02,.3);


float plunge = -0.0;

float cellDensity = 3.0;
float radius = 0.5;

// evaluatePixel(): The function of the filter that actually does the 
//                  processing of the image.  This function is called once 
//                  for each pixel of the output image.
vec4
evaluatePixel(vec2 fragCoord)
{
    spin.x = iTime / 5.123;
    spin.y = iTime / 117.2;
    cellDensity = 8.0 + sin(iTime / 14.4) * 7.0;
    dstsize = iResolution.xy;
    vec3 axis1 = vec3(1.0,0.0,0.0);
    vec3 axis2 = vec3(0.0,1.0,0.0);

    mat3 elevR = mat3(1,0,0,0,cos(spin.x),sin(spin.x),0,-sin(spin.x),cos(spin.x));
    mat3 bearR = mat3(cos(spin.y), sin(spin.y), 0,-sin(spin.y), cos(spin.y), 0, 0, 0, 1 );
    mat3 yamR = mat3(cos(spin.z),0,sin(spin.z),0,1,0,-sin(spin.z),0,cos(spin.z));

    axis1 *= elevR * bearR * yamR;
    axis2 *= elevR * bearR * yamR;

    float cellDensity2 = cellDensity / 2000.0;
    vec2 oc = (fragCoord - dstsize / 2.0) * cellDensity2;

    //oc -= outSize/2.0;
    vec3 p = oc.x * axis1 + oc.y * axis2;


    vec3 perp = cross(axis1,axis2);

    float radiusInPixels = radius *  dstsize.x;
    float plungeMore = radiusInPixels * radiusInPixels * cellDensity2 * cellDensity2 - oc.x * oc.x - oc.y * oc.y;
    if(plungeMore < 0.0)
        plungeMore = 0.0;
    plungeMore = sqrt(plungeMore);

    // Strangely, PBT and Flash have opposite senses here.
    // Some sort of arithmetic bug, probably, making them
    // behave differently.
    if(invert > 0.0)
        plungeMore = -plungeMore;

    p += (plunge - plungeMore) * perp;


    vec3 pCell = floor(p);


    p = mod(p,1.0);

    /*
    Our cell size, here, is 1x1x1. Perp is a unit vector representing
    the direction we're now looking, the ray cast if you will. We like
    to cast to the planes x=0, y=0, z=0, because it's easy. So first
    we'll see if each element of perp is negative, and, if so, flip
    it and reposition our starting point, like p.x := 1-p.x.
    */

    /* this is the cleanest way, but Flash doesn't allow bools,
       and ?: doesn't seem to work in this mixed-dimension way
       either

    bool3 perpNeg = lessThan(perp,vec3(0,0,0));
    p = perpNeg ? 1.0 - p : p;
    perp = abs(perp);
    */

    /* We can be clever with step and abs, though. */
    vec3 perpStep = 1.0 - step(0.0,perp);
    p = perpStep - p;
    p = abs(p);
    perp = abs(perp);


    vec3 t = p / perp; // casts from p, in direction of perp, to zero. T is how far to each plane (x,y, or z)
    vec3 co = vec3(0,0,0);
    float z;

    if(t.x >= 0.0)
    {
        co = xAxisColor;
        z = t.x;
    }
    if(t.y >= 0.0 && t.y < t.x)
    {
        co = yAxisColor;
        z = t.y;
    }
    if(t.z >= 0.0 && t.z < t.x && t.z < t.y)
    {
        co = zAxisColor;
        z = t.z;
    }

    vec4 dst;
    dst.rgb = co * (1.0 - z/1.2);
    dst.a = 1.0;

    if(t.x < edgeThickness || t.y < edgeThickness || t.z < edgeThickness)
        dst.rgb = edgeColor;

    if(plungeMore == 0.0)
        dst.xyz *= 0.0;
    return dst;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);

    fragColor = evaluatePixel(fragCoord);
}

mat2 rotate(float a) {
    float si = sin(a), co = cos(a);
    return mat2(co, si, -si, co);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float sdBox(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec3 id = vec3(0);
float map(in vec3 p) {
    float scale = 40.;
    p *= scale;
    vec3 pmod = mod(p, 2.0) - 1.; 
    vec3 pint = p - pmod;
    id = pint;
    float period = 6.;
    float t = (iTime + 9.24) * 2.;
    float size = (sin(period*pint.x + t) 
               +  sin(period*pint.y + t) 
               +  sin(period*pint.z)) 
               * 0.5;
    float roundness = 0.05;
    pmod.xz *= rotate(size);
    float sizeBox = size * 2. * (0.5 - roundness);
    float box = sdBox(pmod, vec3(sizeBox)*.5) - roundness;
    return box/scale;

}

#define MIN_MARCH_DIST 0.0001
#define MAX_MARCH_DIST 40.
#define MAX_MARCH_STEPS 60.
float march(in vec3 ro, in vec3 rd) {
    float t = 0.4, // don't render too close to camera
          i = 0.;
    for(i=0.; i < MAX_MARCH_STEPS; i++) {
        vec3 p = ro + t*rd;
        float d = map(p);
        if(abs(d) < MIN_MARCH_DIST)
            break;
        t += d;
        if(t > MAX_MARCH_DIST)
            break;
    }
    if(i >= MAX_MARCH_STEPS) {
        t = MAX_MARCH_DIST;
    }
    return t;
}

vec3 normal(in vec3 p) {
    float eps = MIN_MARCH_DIST;
    vec2 h = vec2(eps, 0);
    return normalize(vec3(map(p+h.xyy) - map(p-h.xyy),
                          map(p+h.yxy) - map(p-h.yxy),
                          map(p+h.yyx) - map(p-h.yyx)));
}

float G1V(float dotNV, float k) {
    return 1.0 / (dotNV * (1.0 - k) + k);
}

// http://filmicworlds.com/blog/optimizing-ggx-shaders-with-dotlh/
float brdf_ggx(vec3 N, vec3 V, vec3 L, float roughness, float F0) {
    float alpha = roughness * roughness;
    vec3 H = normalize(V+L);
    float dotNL = clamp(dot(N,L), 0., 1.);
    float dotNV = clamp(dot(N,V), 0., 1.);
    float dotNH = clamp(dot(N,H), 0., 1.);
    float dotLH = clamp(dot(L,H), 0., 1.);
    float alphaSqr = alpha*alpha;
    float pi = 3.14159;
    float denom = dotNH * dotNH * (alphaSqr - 1.0) + 1.0;
    float D = alphaSqr / (pi * denom * denom);
    float dotLH5 = pow(1.0 - dotLH, 5.0);
    float F = F0 + (1.0 - F0) * dotLH5;
    float k = alpha / 2.0;
    float vis = G1V(dotNL, k) * G1V(dotNV, k);
    return dotNL * D * F * vis;
}

vec3 shade(vec3 N, vec3 L, vec3 V, vec3 diffuse, vec3 specular) {
    return diffuse * clamp(dot(L, N), 0., 1.)       // Lambertian Diffuse
         + specular * brdf_ggx(N, V, L, 0.55, 0.1); // GGX Specular
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    // Camera
    vec3 ro = vec3(0,0,-1.);
    vec3 ta = vec3(0,0,0);
    vec3 ww = normalize(ta-ro);
    vec3 uu = normalize(cross(ww, vec3(0,1,0)));
    vec3 vv = normalize(cross(uu,ww));
    vec3 rd = normalize(uv.x*uu + uv.y*vv + 1.0*ww);
    float time = (iTime + 9.24) * 0.1;
    rd.zx *= rotate(time);
    ro.z += time;
    ro.y += time;

    // March
    float t = march(ro, rd);

    // Shade
    vec3 col = vec3(0);
    if(t < MAX_MARCH_DIST) {
        vec3 P = ro + t*rd;             
        vec3 N = normal(P);                
        vec3 V = normalize(ro - P); 
        vec3 ambient = vec3(.06);
        vec3 diffuse = hsv2rgb(vec3(fract(length(id) / 80. + time * 0.2), 1., 1.));
        vec3 specular = vec3(.9);      
        vec3 tangent = cross(V, vec3(0,1,0));
        col =       shade(N, normalize(ro-P+tangent*0.03), V, diffuse, specular)
            + 0.3 * shade(N, normalize(P-ro+tangent*0.2), V, diffuse, specular)  
            + ambient;                                               
        col *= clamp(exp(-0.20*(t-0.2)), 0., 1.);                       
    }

    fragColor = vec4(pow(col, vec3(2.2)),1.0); // Gamma correction
}

#define R(a)mat2(cos(a),sin(a),-sin(a),cos(a))
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    float i,g,e,s,B=2.;
    for(vec3 r=iResolution,p;
        ++i<99.;
        e<.001?O+=.5/i:O
        )
    {
        p=vec3(g*(C.xy-.5*r.xy)/r.y,g-22.);
        p.xy*=R(iTime*.5);
        p.xz*=R(iTime*.5);
        s=2.;
        for(int j=0;j++<10;)
        {
            p=abs(p),
            p.y<p.z?p.yz=p.zy:p.yz;
            p.x<p.z?p.xz=p.zx:p.xz;
            p.x=abs(p.x)-5.;
            p.y=abs(p.y)-5.;
            p.z=abs(p.z)+5.;
            p*=B;
            s*=B;
        }
        p-=s;
        g+=e=length(p)/s-.01;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    uv *= 20.;

    float x = 0.;
    for (int i = 0; i < 10; ++i) {
        float t = float(i) * iTime / 20.;
        x += cos(cos(t) * radians(360.) * uv.y + sin(t) * radians(360.) * uv.x);
    }

    vec3 col = vec3(1.) * (.5 + .5 * x);
    fragColor = vec4(col, 1.);
}

// inspiration code: https://thebookofshaders.com/edit.php#10/ikeda-03.frag
