float random (in float x) {
    return fract(sin(x)*1e4);
}

float random (in vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

float pattern(vec2 st, vec2 v, float t) {
    vec2 p = floor(st+v);
    return step(t, random(100.+p*.000001)+random(p.x)*0.8 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
 {
 //   vec2 st = fragCoord.xy/iResolution.xy;
   vec2 st = (1.0 * fragCoord.xy - iResolution.xy) / min(iResolution.y, iResolution.x);
   st.x *= iResolution.x/iResolution.y;

    vec2 grid = vec2(100.0,50.);
    st *= grid;

    vec2 ipos = floor(st);  // integer
    vec2 fpos = fract(st);  // fraction

    vec2 vel = vec2(iTime*2.*min(grid.x,grid.y)); // time
    vel *= vec2(1.)*sin(iTime)/1e4 * random(0.+max(ipos.x,ipos.y)); // direction

    // Assign a random value base on the integer coord
    vec2 offset = vec2(random(st),random(st*1e3)) ;
    offset+=iTime/1e3;

    vec3 color = vec3(0.880,0.354,0.201);
    color.r = pattern(st+offset,vel,0.5+iMouse.x/iResolution.x);
    color.g = pattern(st,vel,0.5+iMouse.x/iResolution.x);
    color.b = pattern(st-offset,vel,0.5+iMouse.x/iResolution.x);

    // Margins
    color /= step(sin(iTime*1.5),max(fpos.x,fpos.y));

    fragColor = vec4(1.0-color*random(st)*1e3,clamp(abs(sin(iTime)), 0.8,1.0));
}

const float pi = 3.1415926535897932384626433;
const vec2 c = vec2(0.5,0.5);
const mat2 mat = mat2(.5,.5,
						-.5,.5);
const mat2 negmat = mat2(1.,-1.,
						1.,1.);
const vec4 white = vec4(1,1,1,1);
const vec4 gray = vec4(.5,.5,.5,1);
const vec4 black = vec4(0,0,0,1);

vec4 coordtester(vec2 xy) {
	float r = .001;
	vec2 p1 = vec2(0,0);
	vec2 p2 = vec2(1,1);
	vec2 diff1 = xy - p1;
	vec2 diff2 = xy - p2;
	float len1 = dot(diff1,diff1);
	float len2 = dot(diff2,diff2);
	if (len1 < r)
	{
		return black;
	}
	if (len2 < r) {
		return gray;
	}
	return white;
}

vec2 getUV()
{
	bool HeightIsGreater = iResolution.y > iResolution.x;
	float mindim = HeightIsGreater ? iResolution.x : iResolution.y;
	float resdiff = HeightIsGreater ? iResolution.y - iResolution.x : iResolution.x - iResolution.y;
	vec2 uv = gl_FragCoord.xy/mindim;
	if (HeightIsGreater)
	{
		uv.y-=.5*resdiff/( iResolution.x);
	}
	else
	{
		uv.x-=.5*resdiff/(iResolution.y);
	}
	return uv - c;
}

float ptx(float x) {
	return (x / 3733.) - .5;
}

float pty(int y) {
	return ((float(3704 - y)) / 3704.) - .5;
}

vec2 pt(int x, int y) {
	return vec2(ptx(float(x)),pty(y));
	//return vec2( float(x) / 3733., (float(3704 - y)) / 3704.) - c;
}

vec2 pt(float x, int y) {
	return vec2(ptx(x),pty(y));
	//return vec2( x / 3733., (float(3704 - y)) / 3704.) - c;
}

bool enclose_tlbr(vec2 tl, vec2 br, vec2 uv) {
	return (tl.x < uv.x && uv.x < br.x && tl.y > uv.y && uv.y > br.y);
}

bool enclose_trbl(vec2 tr, vec2 bl, vec2 uv) {
	return (bl.x < uv.x && uv.x < tr.x && tr.y > uv.y && uv.y > bl.y);
}

bool above(vec2 p1, vec2 p2, vec2 uv) {
	float r = (uv.x - p1.x) / (p2.x - p1.x);
	float h = r * (p2.y - p1.y) + p1.y;
	return uv.y > h;
}

bool below(vec2 p1, vec2 p2, vec2 uv) {
	float r = (uv.x - p1.x) / (p2.x - p1.x);
	float h = r * (p2.y - p1.y) + p1.y;
	return uv.y < h;
}

bool T(vec2 uv) {
	vec2 tlt = pt(400,92);
	vec2 brt = pt(602,135);
	vec2 tlb = pt(475,135);
	vec2 brb = pt(527,340);
	return enclose_tlbr(tlt,brt,uv) || enclose_tlbr(tlb,brb,uv);
}

bool A(int b, int l, vec2 uv) {
	vec2 itl = pt(b, l - 340 + 92);
	vec2 ibr = pt(b - 587 + 822,l);
	if (!enclose_tlbr(itl,ibr,uv)) return false;
	vec2 bll = pt(b,l);
	vec2 blr = pt(b-587+642,l);
	vec2 brl = pt(b-587+765,l);
	vec2 brr = pt(b-587+822,l);
	vec2 tl = pt(b-587+675,l-340+92);
	vec2 tr = pt(b-587+735,l-340+92);
	vec2 it = pt(float(b-587)+704.5,l-340+149);
	vec2 mtl = pt(b-587+658,l-340+246);
	vec2 mbr = pt(b-587+753,l-340+289);
	bool r = true;
	r = r && (above(blr,it,uv) && below(bll,tl,uv));
	r = r || (above(it,brl,uv) && below(tr,brr,uv));
	r = r || enclose_tlbr(mtl,mbr,uv);
	return r;
}

bool L(int b, int l, vec2 uv) {
	vec2 itr = pt(b-853+1029,l-340+92);
	vec2 mc = pt(b-853+905,l-340+296);;
	vec2 bl = pt(b,l);
	return enclose_trbl(itr,bl,uv) && !enclose_trbl(itr,mc,uv);
}

bool Y(vec2 uv) {
	vec2 tll = pt(1223,92);
	if (uv.y > tll.y) return false;
	vec2 tlr = pt(1284,92);
	vec2 trl = pt(1385,92);
	vec2 trr = pt(1444,92);
	vec2 itm = pt(1336,200);
	vec2 nl = pt(1309,245);
	vec2 nr = pt(1361,246);
	vec2 bl = pt(1309,340);
	//vec2 br = pt(1361,340);
	vec2 ibtr = pt(1361,220);
	bool r = true;
	r = r && (above(tll,nl,uv) && below(tlr,itm,uv));
	r = r || (above(nr,trr,uv) && below(itm,trl,uv));
	r = r && uv.y > nr.y;
	r = r || enclose_trbl(ibtr,bl,uv);
	return r;
}

bool H(vec2 uv) {
	vec2 ltl = pt(2345,93);
	vec2 lbr = pt(2397,341);
	vec2 mtl = pt(2397,188);
	vec2 mbr = pt(2492,231);
	vec2 rtl = pt(2492,93);
	vec2 rbr = pt(2544,341);
	return enclose_tlbr(ltl,lbr,uv) || enclose_tlbr(mtl,mbr,uv) || enclose_tlbr(rtl,rbr,uv);
}

bool tally(vec2 uv) {
	bool r = false;
	r = r || T(uv);
	r = r || A(578,340,uv);
	r = r || L(853,340,uv);
	r = r || L(1063,340,uv);
	r = r || Y(uv);
	return r;
}

bool hall(vec2 uv) {
	bool r = false;
	r = r || H(uv);
	r = r || A(2576,341,uv);
	r = r || L(2842,341,uv);
	r = r || L(3052,341,uv);
	return r;
}

bool text(vec2 uv) {
	vec2 lowmid = pt(2000,341);
	if (uv.y < lowmid.y) return false;
	if (uv.x < lowmid.x) {
		return tally(uv);
	} else {
		return hall(uv);
	}
}

bool udtext(vec2 uv) {
	//flip y 92 - 341
	float y92 = pty(92);
	float y341 = pty(341);
	if (y92 > uv.y && uv.y > y341) {
		uv.y -= y92 + (y341 - y92)/2.;
		uv.y = -uv.y;
		uv.y += y92 + (y341 - y92)/2.;
	}
	return text(uv);
}

bool alltext(vec2 uv) {
	bool r = false;
	r = r || text(uv); //top
	r = r || udtext(vec2(abs(uv.y),-uv.x)); //right
	r = r || text(vec2(abs(uv.y)-.5,uv.x)); //left
	r = r || text(vec2(uv.x,-uv.y)); //bottom
	return r;
}

bool base(vec2 uv) {
	bool filled = uv.x > uv.y;
	filled = filled != -uv.x < uv.y;
	filled = filled != uv.x < 0.;
	filled = filled != uv.y > 0.;
	filled = filled != alltext(uv);
	return filled;
}

bool inbase(vec2 uv) {
	//if (uv.y > .5 || uv.x > .5) return false;
	//if (uv.y < -.5 || uv.x < -.5) return false;
	//base square, remove later
	if (uv.x  + .5 < uv.y) return true;
	if (.5 - uv.x < uv.y) return true;
	if (uv.x - .5 > uv.y) return true;
	if (uv.y + .5 < -uv.x) return true;
	return false;
}

bool outerbase(vec2 uv) {
	if (uv.y > .5 || uv.x > .5) return true;
	if (uv.y < -.5 || uv.x < -.5) return true;
	return false;
}

bool innerbase(vec2 uv) {
	return !outerbase(uv) && !inbase(uv);
}

bool picture(vec2 uv) {
	if (outerbase(uv)) {
		for (int r = 0; r < 100; r++) {
			uv *= mat;
			if (!outerbase(uv)) {
				return base(uv);
			}
		}
	}
	for (int r = 0; r < 100; r++) {
		if (inbase(uv)) {
			return base(uv);
		}
		uv *= negmat;
	}
	return false;
}

vec2 cmul(vec2 ab, vec2 cd) {
	return vec2(ab.x*cd.x - ab.y*cd.y, ab.x*cd.y + ab.y*cd.x);
}

vec2 cexp(vec2 z) {
	return exp(z.x) * vec2(cos(z.y),sin(z.y));
}

vec2 clog(vec2 z) {
	return vec2(log(dot(z,z)),atan(z.y,z.x));
}

vec2 getrot(float time) {
	vec2 increment = vec2(-0.34657359028,-0.785398163397);
		//clog(.5,-.5)
	return cexp(fract(time)*increment);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = getUV();
	uv = cmul(uv,getrot(iTime/2.));
	//bool base = base(uv) ;
	//bool inbase = inbase(uv);
	//bool outerbase = outerbase(uv);
	//bool innerbase = innerbase(uv);
	bool pic = picture(uv);

	bool filled = pic;
	fragColor = filled ? black : white;	
