#define sat(a) clamp(a, 0., 1.)

vec3 cols[5];
void setup()
{
    cols[0] = vec3(74, 0, 57)/255.;
    cols[1] = vec3(145, 10, 62)/255.;
    cols[2] = vec3(207, 4, 55)/255.;
    cols[3] = vec3(255, 116, 36)/255.;
    cols[4] = vec3(245, 205, 27)/255.;
}

vec3 getCol(float v)
{
    float mx = 4.;
    float curV = v*mx;
    float cur = min(floor(curV), mx);
    float next = min(cur+1., mx);
    float f = curV - cur;
    vec3 a = cols[int(cur)];
    vec3 b = cols[int(next)];
    return mix(a, b, sat(f));
}
float lenny(vec2 p)
{
    return abs(p.x)+abs(p.y);
}
float _cir(vec2 p, float r)
{
    return length(p)-r;
}

float _leave(vec2 p, vec2 sz, float t)
{
    p.x /= abs(p.y*5.);
    return max(max(max(p.x-sin(p.y*25.+t+.2)*sz.x*2., -(p.x+sz.x-sin(p.y*25.+t)*sz.x*2.)), p.y), -p.y-sz.y);
}

vec2 _min(vec2 a, vec2 b)
{
    if (a.x<b.x)
        return a;
    return b;
}

vec2 _bush(vec2 p, int cnt, float sz)
{
    float mid = sz *.5;
    vec2 acc = vec2(1., 0.);

    for (int i = 0; i < cnt; ++i)
    {
        float f = (float(i)-float(cnt)*.5)/(float(cnt)*.5);
        acc = _min(acc, vec2(_leave(p+vec2(f*sz, 0.+abs(f)*.1+.01*sin(f*15.)), vec2(.005, .2-abs(f)*.1), float(i)+iTime*.25), float(i)));
    }
    return acc;
}

vec3 rdr(vec2 uv)
{
    vec3 col;// = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float shp = 400.;

    col = vec3(.2)*(1.-sat(abs(uv.y*2.-.5)));
    col += pow(texture(iChannel0, uv*5.).x, iResolution.x*.01);

    col += pow(1.-sat(lenny((uv-vec2(0.,.25))*vec2(3.,8.))), 5.);

    float planetY = .45;
    col = mix(col, mix(vec3(0.), vec3(.25), pow(abs((uv.y+planetY)*2.), .5)), 1.-sat(_cir(uv-vec2(0.,-planetY), .45)*shp));

    vec2 mainBush = _bush(uv-vec2(0., .15), 10, .05);
    col = mix(col, vec3(sin(mainBush.y*20.+iTime*.25)*.5+.5)*.25, 1.-sat(mainBush.x*shp));

    for (int i = 0; i < 16; ++i)
    {
        vec2 miniBush = _bush(uv*3.-vec2(float(i)*.25-1.15, sin(float(i)))*.25+.25, 10, 0.1);
        col = mix(col, vec3(sin(miniBush.y*20.+iTime*.25)*.5+.5)*.25, (1.-sat(miniBush.x*shp))*.25);
    }

   col += vec3(.05)*texture(iChannel0, vec2(iTime*5.+uv*3.)).x;

    col = getCol(col.x*3.);

    col = pow(col, vec3(1.45));

    col *= .01+sat(1.-sat((abs(uv.x)-.25)*shp));

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;
    uv -= vec2(0.,-.1);
    setup();
    vec3 col = rdr(uv);

    col = pow(col, vec3(.45));
    fragColor = vec4(col,1.0);
}

