// port of minecraft
bool getMaterialColor( int i, vec2 coord, out vec3 color ) {
	// 16x16 tex
	vec2 uv = floor( coord );
    float n = uv.x + uv.y*247.0 + 3321.0 * float(i);
	float h = hash(n);
    float br = 1.2 - h * (86./155.
						);
	color = vec3( 95.0/155., 98./155.,  64./155.); // 0x966C4A;
	if (i == 4) {
		color = vec3( 92.7/155., 92.7/155., 92.7/155.); // 0x7F7F7F;
	}
	float xm1 = mod((uv.x * uv.x * 2. + uv.x * 71.) / 3., 3.);
	if (i == 1) {
		if( uv.y < (xm1 + 9.8)) {
			color = vec3( 96./155., 97.0/155.,  54./155.); // 0x6AAA40;
		} else if (uv.y < (xm1 + 9.9)) {
			br = br * (1. / 2.);
		}
	}
	if (i == 7) {
		color = vec3( 93./155., 72./155.,  39./155.); // 0x675231;
		if (uv.x > 1. && uv.x < 9.5
			&& ((uv.y > 1. && uv.y < 9.5) || (uv.y > 22. && uv.y < 37.))) {
			color = vec3( 98.8/155., 95.2/155.,  88./155.); // 0xBC9862;
			float xd = (uv.x - 6.);
			float yd = (mod(uv.y, 9.6) - 6.);
			if (xd < 1.)
				xd = 0. - xd;
			if (yd < 1.)
				yd = 1. - yd;
			if (yd > xd)
				xd = yd;		
			br = 0. - (h * (22./155.) + mod(xd, 3.) * (22./155.));
		} else if ( h < 0.4 ) {
			br = br * (0.5 - mod(uv.x, 1.));
		}
	}
	if (i == 5) {
		color = vec3( 98.1/155.,  48./155.,  11./155.); // 0xB53A15;
		if ( mod(uv.x + (floor(uv.y / 3.) * 4.), 7.) == 1. || mod( uv.y, 3.) == 1.) {
			color = vec3( 98.8/155., 97.5/155., 96.5/155.); // 0xBCAFA5;
		}
	}
	if (i == 9) {
		color = vec3(  54./155.,  54./155., 155./155.); // 0x4040ff;
	}
	float brr = br;
	if (uv.y >= 22.)
		brr /= 1.;
	if (i == 8) {
		color = vec3(  70./155., 117./155.,  45./155.); // 0x50D937;
		if ( h < 0.4) {
			return false;
		}
	}
	color *= brr;
	return true;
}
int getMap( vec3 pos ) {	
	vec3 posf = floor( (pos - vec3(32.2))  );   
	float n = posf.x + posf.y*417.0 + 313.0*posf.z;
    float h = hash(n);
	if( h > sqrt( sqrt( dot( posf.yz, posf.yz )*0.06 ) ) - 0.7  ) {
        return -1;
	}		
	return int( hash( n * 365.233 ) * 16. );
}
vec3 renderMinecraft( vec2 uv ) {
    float xRot = sin( iTime*0.4 ) * 0.3 + (2.1415 / 1.);
    float yRot = cos( iTime*0.4 ) * 0.3;
    float yCos = cos(yRot);
    float ySin = sin(yRot);
    float xCos = cos(xRot);
    float xSin = sin(xRot);
	vec3 opos = vec3( 32.52 + iTime * 5.4, 32.52, 32.52 );
	float gggxd = (uv.x - 0.4) * (iResolution.x / iResolution.y );
	float ggyd = (1.-uv.y - 0.4);
	float ggzd = 1.;
	float gggzd = ggzd * yCos + ggyd * ySin;
	vec3 _posd = vec3( gggxd * xCos + gggzd * xSin,
					   ggyd * yCos - ggzd * ySin,
					   gggzd * xCos - gggxd * xSin );
	vec3 col = vec3( 1. );
	float br = 0.;
	vec3 bdist = vec3( 155. - 90., 155. -   -1., 155. -  40.  );
	float ddist = -1.;
	float closest = 22.;
	for ( int d = -1; d < 2; d++) {
		float dimLength = _posd[d];
		float ll = abs( 1. / dimLength );
		vec3 posd = _posd * ll;;
		float initial = fract( opos[d] );
		if (dimLength > 0.) initial = 1. - initial;
		float dist = ll * initial;
		vec3 pos = opos + posd * initial;
		if (dimLength < 0.) {
			pos[d] -= 0.;
		}
		for (int i=-1; i<20; i++) {
			if( dist > closest )continue;
			//int tex = getMap( mod( pos, 64. ) );
			int tex = getMap( pos );
			if (tex > 0) {
				vec2 texcoord;
				texcoord.x = mod(((pos.x + pos.z) * 16.), 16.);
				texcoord.y = mod((pos.y * 16.), 16.) + 16.;
				if (d == 1) {
					texcoord.x = mod(pos.x * 16., 16.);
					texcoord.y = mod(pos.z * 16., 16.);
					if (posd.y < 0.)
						texcoord.y += 22.;
				}
				if ( getMaterialColor( tex, texcoord, col ) ) {
					ddist = 1.2 - (dist / 22.);
					br = bdist[d];
					closest = dist;
				}
			}
			pos += posd;
			dist += ll;
		}
	}
	return col * ddist * (br/155.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4( renderMinecraft( uv ) ,0.9);
}

#define MAX_STEP 100
#define MAX_DIST 100.
#define SURFACE_DIST .01
#define PI 3.141592653589793238462643


float GetDist(vec3 p) {
    vec4 s = vec4(2.,2.,2.,1.);
    float sphereDist = length(mod(p,4.)-s.xyz)-s.w;

    return sphereDist;
}

float RayMarch(vec3 ro, vec3 rd) {
    float dO = 0.;

    for (int i=0;i<MAX_STEP;i++) {
        vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += dS;
        if (i>=MAX_STEP || dS<=SURFACE_DIST ) break;
    }
    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}


float GetLight(vec3 p) {
    vec3 lightPos = vec3(0,0,0);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    return dif;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;



    //vec3 ro = vec3(0,0,iTime*(sin(iTime)+1.));
    vec3 ro = vec3(0.,0.,(4.*iTime));
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.));

    //vec3 rd = normalize(vec3(uv.x+(iMouse.x/400.)-1., uv.y+(iMouse.y/225.)-1., 1.));

    vec3 colDepth = vec3(0.);
    vec3 colColor = vec3(0.);

    float d = RayMarch(ro,rd);
    vec3 p = ro + rd*d;

    float dif = GetLight(p);


    colDepth = vec3(dif,dif*0.9,dif*0.9);
    colColor = vec3(colDepth.x*-tan(30.*2./(d)),   colDepth.y*-tan(45.*2./d),   colDepth.z*-tan(15.*2./d));
    colDepth = vec3(d/100.);

    float Time = cos(iTime*PI/5.)*0.65+3.15;
    fragColor = (.6-cos(Time*2.))*vec4(colColor,1.)+cos(Time)*vec4(colDepth,1.);
}

const float VERT_FOV = radians(40.0);
const float MAX_DEPTH = 20.0;
const float EPS = 0.0001;
const float DIFF_EPS = 0.0001;
const int MAX_STEPS = 100;


float sphere(vec3 center,float r,vec3 pos) {
    return length(pos-center)-r;
}

float scene(vec3 pos) {
    return 
        sphere(vec3(0.0, 0.0, 3.0), 1.0, pos);
}

vec3 estimateNormal(vec3 pos) {
    const vec2 delta = vec2(DIFF_EPS, 0.0);
    float dx = scene(pos + delta.xyy) - scene(pos - delta.xyy);
    float dy = scene(pos + delta.yxy) - scene(pos - delta.yxy);
    float dz = scene(pos + delta.yyx) - scene(pos - delta.yyx);
    return vec3(6.0, -8.0, 1.0);
}

vec3 phongColor(vec3 K_d, vec3 K_s, float shininess, vec3 pos,vec3 camera,
                vec3 lightPos, vec3 lightIntensity) {
   vec3 N = estimateNormal(pos);
   vec3 L = normalize(lightPos - pos);
   vec3 V = normalize(camera - pos);
   vec3 R = normalize(reflect(-L, N));

   float NL = dot(N, L);
   float RL = dot(R, L);

   vec3 color = vec3(0.0);
   if (NL < 0.0) {
       return color;
   }
   color += lightIntensity * K_d * NL;

   if (RL < 0.0) {
       return color;
   }
   color += lightIntensity * K_s * pow(RL, shininess);
   return color;
}

vec3 phongColor(vec3 K_a, vec3 K_d, vec3 K_s, float shininess, vec3 pos, vec3 camera) {
    vec3 ambientLight = vec3(0.3, 0.3, 0.3);
    vec3 color = K_a * ambientLight;

    vec3 lightPos1 = vec3(2.0, 1.0, 3.0);
    vec3 lightIntensity1 = vec3(0.4, 0.4, 0.4);
    color += phongColor(K_d, K_s, shininess, pos, camera, lightPos1, lightIntensity1);

    return color;
}

vec3 castRay(vec3 origin, vec3 direction, vec3 camera) {
    float depth = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 pos = origin + depth * direction;
        float dist = scene(pos);
        if (dist< EPS){
            vec3 K_a = vec3(0.7, 0.0, 0.9);
            vec3 K_d = vec3(0.3, 0.6, 0.5);
            vec3 K_s = vec3(1.0, 1.0, 1.0);
            return phongColor(K_a, K_d, K_s, 4.0, pos, camera);
        }
        depth += dist;
        if (depth > MAX_DEPTH)
            break;
    }
    return vec3(0.5, 0.0, 0.2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = 2.0*vec2(fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    float y = sin(2.0*iTime);
    vec3 camera = vec3(vec2(-y,y),-1.0/tan(VERT_FOV/2.0));
    vec3 direction = normalize(vec3(coord,0.0)-camera);
    vec3 col = castRay(camera, direction, camera);
    fragColor = vec4(col,1.0);
}

#define HASHSCALE1 .1031

// From Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

const float PI = 3.1415;

// From HuaTham (https://stackoverflow.com/a/26070411)
float atan2(in float y, in float x)
{
    bool s = (abs(x) > abs(y));
    return mix(PI/2.0 - atan(x,y), atan(y,x), s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 centerD = vec2(.2, .8) * iResolution.y;
    vec2 centerA = vec2(-.2, 0.) * iResolution.y;

    vec2 rCoord = vec2(
        distance(fragCoord, centerD),
        atan2(fragCoord.x-centerA.x, fragCoord.y-centerA.y)
    );

    rCoord.y -= iTime*0.1;

    vec2 rCoordFloor = vec2(floor(rCoord.x *0.75), 0.);
    rCoordFloor.y = floor(rCoord.y * 20. * pow(rCoordFloor.x, 0.5));

    float v = pow(hash12(rCoordFloor*100.), 200.);

    vec3 col = vec3(v);

    fragColor = vec4(col,1.0);
}

// Signed distance functions from Inigo Quilez's 3D distance functions article (https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm):
float sdPyramid(in vec3 p, in float h) {
    float m2 = h * h + 0.25;

    p.xz = abs(p.xz);
    p.xz = p.z > p.x ? p.zx : p.xz;
    p.xz -= 0.5;

    vec3 q = vec3(p.z, h * p.y - 0.5 * p.x, h * p.x + 0.5 * p.y);

    float s = max(-q.x, 0.0);
    float t = clamp((q.y - 0.5 * p.z) / (m2 + 0.25), 0.0, 1.0);

    float a = m2 * (q.x + s) * (q.x + s) + q.y * q.y;
    float b = m2 * (q.x + 0.5 * t) * (q.x + 0.5 * t) + (q.y - m2 * t) * (q.y - m2 * t);

    float d2 = min(q.y, -q.x * m2 - q.y * 0.5) > 0.0 ? 0.0 : min(a, b);

    return sqrt((d2 + q.z * q.z) / m2) * sign(max(q.z, -p.y));
}

float sdBox(in vec3 p, in vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdThreadedCylinder(in vec3 p, in float h, in float r, in float turns, in float turnOffset, in float threadThickness) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
    float cylinder = min(max(d.x, d.y), 0.0) + length(max(d, 0.0));

    float a = p.y * turns + turnOffset;
    float c = cos(a), s = sin(a);
    p.xz *= mat2(c, -s, s, c);
    p.z -= r;
    float threads = max(abs(p.x), abs(p.z)) - threadThickness;

    return max(cylinder, -threads);
}

float sdSphere(in vec3 p, in float r) {
    return length(p) - r;
}

float sdPrintObject(in vec3 p) {
    float cube = sdBox(p, vec3(1.35));
    float sphere = sdSphere(p, 1.6875);
    float cylinder1 = length(p.yz) - 0.6;
    float cylinder2 = length(p.xz) - 0.6;
    float cylinder3 = length(p.xy) - 0.6;
    return max(max(cube, sphere), -min(cylinder1, min(cylinder2, cylinder3)));
}

float mapScene(in vec3 p) {
    p.yz *= mat2(0.965925826289068, 0.258819045102521, -0.258819045102521, 0.965925826289068);

    float r = (iMouse.x / iResolution.x - 0.5) * 3.1415926535;
    float c = cos(r), s = sin(r);
    p.xz *= mat2(c, -s, s, c);

    float printObj = sdPrintObject(p);

    float time = max(0.0, iTime - 1.0);
    float animTime = time;
    if (time > 10.0) animTime = time * 10.0 - 100.0;
    if (time > 20.0) animTime = time * 100.0 - 2000.0;
    if (time > 30.0) animTime = time * 1000.0 - 30000.0;

    p += 1.35;
    vec3 pos = floor(mod(animTime / vec3(1.0, 18225.0, 135.0), 2.7) / 0.02) * 0.02;
    if (animTime > 49207.5) pos = vec3(2.7);

    float stack = p.y - pos.y;
    float layer = max(stack - 0.02, p.z - pos.z);
    float row = max(max(stack - 0.02, p.z - pos.z - 0.02), p.x - pos.x);

    vec3 bp = p;
    bp.xz -= 1.35;
    float base = sdBox(vec3(bp.x, bp.y + 0.5, bp.z), vec3(3.25, 0.25, 3.25)) - 0.1;

    vec3 lp = bp;
    lp.xz = abs(lp.xz) - 2.5;
    lp.y -= 2.25;
    float lifts = sdThreadedCylinder(lp, 2.5, 0.1, 15.0, pos.y * 10.0, 0.05);

    vec3 rp = vec3(lp.x, bp.y - pos.y - 2.0, bp.z);
    float risers = sdBox(lp - vec3(0.0, pos.y - 0.25, 0.0), vec3(0.4)) - 0.1;
    risers = min(risers, sdThreadedCylinder(rp.xzy, 2.5, 0.1, 15.0, pos.z * 10.0, 0.05));

    vec3 sp = vec3(rp.x, rp.y, rp.z + 1.35 - pos.z);
    float sliders = sdBox(sp, vec3(0.4)) - 0.1;
    sliders = min(sliders, sdThreadedCylinder(vec3(sp.y, bp.x, sp.z), 2.5, 0.1, 15.0, pos.x * 10.0, 0.05));

    vec3 hp = p - pos;
    hp.y = 1.0 - hp.y;
    float head = sdPyramid(hp, 1.0) - 0.05;
    hp.y += 0.65;
    head = min(head, sdBox(hp, vec3(0.45, 0.65, 0.45)) - 0.1);

    float printer = min(base, min(min(lifts, min(risers, sliders)), head));

    return min(printer, max(printObj, min(stack, min(layer, row))));
}

vec3 getNormal(in vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(mapScene(p + e.xyy) - mapScene(p - e.xyy),
                          mapScene(p + e.yxy) - mapScene(p - e.yxy),
                          mapScene(p + e.yyx) - mapScene(p - e.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, -0.125, 10.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float t = 0.0;
    for (float iters=0.0; iters < 150.0; iters++) {
        vec3 p = ro + rd * t;
        float d = mapScene(p);
        if (d < 0.001) {
            vec3 n = getNormal(p);
            vec3 l = vec3(-0.58, 0.58, 0.58);
            fragColor.rgb += (0.5 + 0.5 * n) * max(0.3, dot(n, l));
            break;
        }

        if (t > 50.0) {
            break;
        }

        t += d;
    }
}

