//............................................. piston 
/////////////////////////////////////////////////////
/////////////////////////////////////////////////////
float piston(vec3 p) {
   float sc, sc2, sc3, sc4, sc5;
   vec3 p2 = p, p3 = p;
   pModPolar(p2.yz, 3.0);
   sc = fCylinder(p2, 0.25, 0.25);
   sc2 = fCylinder(p2+v3(sin(time)*0.25, 0.0, 0.0), 0.125, 0.5);
   //sc3 = fOpUnionChamfer(sc, sc2, 0.125);
   sc3 = fOpUnionStairs (sc, sc2, 0.25, 8.0);
   pR(p3.xy, 1.5);
   sc4 = fCylinder(p3, 0.25, 0.5);
   sc5 = fOpDifferenceColumns( sc3, sc4, 0.05, 4.0);
   sc5 = fOpGroove(sc5, sc2, 0.1, 0.1);
   return sc5;
}


//.............................................nut 
/////////////////////////////////////////////////////
/////////////////////////////////////////////////////
float nut(vec3 p) {

   float sc, sc2, sc3, sc4, sc5;
   vec3 p2 = p, p3 = p;
   pModPolar(p2.yz, 6.0);
   sc = fBox(p2, v3(0.25));
   sc2 = fBox(p2-v3(0.125, 0.0, 0.0), v3(0.125, 0.5, 0.250));
   sc3 = fOpUnionStairs (sc, sc2, 0.25, 8.0);
   pR(p3.xy, 1.5);
   sc4 = fCylinder(p3, 0.25, 0.5);
   sc5 = fOpDifferenceColumns( sc3, sc4, 0.05, 4.0);
   sc5 = fOpGroove(sc5, sc2, 0.1, 0.1);
   return sc5;
}

//............................................. scene 
/////////////////////////////////////////////////////
/////////////////////////////////////////////////////
float scene(vec3 p) {
   //pR45(p.xz);
   pR(p.xz, touch.x*0.01); //time);
   pR(p.yz, touch.y*0.01);
